// Marching

#define EPSILON 1e-4
#define MAX_STEPS 512
#define tmin 0.
#define tmax 1e2

vec3 getNormal( in vec3 pos ) {
    vec2 e = vec2(0.001, -0.001);
    return normalize(
        e.xyy * sceneSDF(pos + e.xyy) + 
        e.yyx * sceneSDF(pos + e.yyx) + 
        e.yxy * sceneSDF(pos + e.yxy) + 
        e.xxx * sceneSDF(pos + e.xxx));
}

float shadow(vec3 ro, vec3 rd, float lightDist) {
    float t = EPSILON;
    float penumbra = 1e8;
    for (int i=0; i<MAX_STEPS; i++) {
        float dist = sceneSDF(ro+rd*t);
        if (dist < EPSILON) {
            return 0.;
        }
        penumbra = min(penumbra, 64.*dist/t);
        t += dist;
        if (t > lightDist) {
            return smoothstep(penumbra, 0., .1);
        }
    }
    return 0.;
}

float march(vec3 ro, vec3 rd) {
    float t = tmin;
    for (int i=0; i<MAX_STEPS; i++) {
        float dist = sceneSDF(ro+rd*t);
        if (dist < EPSILON) {
            return t;
        }
        if (dist > tmax) {
            return -1.;
        }
        t += dist;
    }
    return -1.;
}

float marchSphere(vec3 ro, vec3 rd, vec3 pos, float rad) {
    float t = tmin;
    for (int i=0; i<MAX_STEPS; i++) {
        float dist = length(ro+rd*t + pos)-rad;
        if (dist < EPSILON) {
            return t;
        }
        if (dist > tmax) {
            return -1.;
        }
        t += dist;
    }
    return -1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime*.3;

    vec3 lightDir = normalize(vec3(1,1,1));
	vec3 darkDir  = normalize(vec3(cos(time),1./sqrt(3.),sin(time)));

    vec2 pos = (fragCoord - iResolution.xy/2.) / (iResolution.y/2.);
    vec2 uv = fragCoord/iResolution.xy;


    vec3 ro = vec3(0.,1,6.);
    vec3 rd = normalize(vec3(pos, -1));


    vec3 col;

    float tl = marchSphere(ro, rd, vec3(1,1,1)*-10., .2);
    float td = marchSphere(ro, rd, vec3(cos(time),1,sin(time))*-10., .2);

    if (tl != -1.) {fragColor = vec4(1,1,1,1); return;}
    if (td != -1.) {fragColor = vec4(0,0,0,1); return;}

    float t = march(ro, rd);
    if (t != -1.) {
        vec3 p = ro+rd*t;
        vec3 n = getNormal(p);

        vec3 sro = p+lightDir*10.;
        vec3 srd = -normalize(lightDir);
        float penumbra = shadow(sro, srd, 8.);

        float diff = max(dot(n, lightDir), 0.);
        col += vec3(diff*penumbra);

        sro = p+darkDir*10.;
        srd = -normalize(darkDir);
        penumbra = shadow(sro, srd, 8.);

        diff = max(dot(n, darkDir), 0.);
        col -= vec3(diff*penumbra);
    }

    col = tanh(col)*.5+.5;

    fragColor = vec4(col,1);
}

