#define dmin(a,b) a.x < b.x ? a :b

#define T iTime

#define rot(x) mat2(cos(x),-sin(x),sin(x),cos(x))


vec2 map(vec3 p){
  vec2 d = vec2(10e6);

  vec4 q = vec4(p,1);


  float dpp = dot(q.xyz,q.xyz);
  q /= dpp;  

  q.xy += sin(T)/5.;


  for(int i = 0; i <13; i++){
    float dpp = dot(q.xyz,q.xyz);
    q = abs(q)/dpp - vec4(0.22,0.2,0.1,0);

    q.xy *= rot(0.4+sin(T)/2000.);    

  }
  float b = sdBox(q.xyz,vec3(1.4,3.4,1.1)*q.w/4.);
  float bb = sdBox(q.xyz-vec3(-.2,0.,0.5),vec3(1.9,3.4,1.1)*q.w/2.);

  float bc = sdBox(q.xyz-vec3(0.04,0.2,0.5),vec3(1.9,1.4,3.1));


