// Defines

#define COLOR_SKY vec3(.1,.2,.6)
#define COLOR_CLOUDS vec3(.98,.96,.92)
#define COLOR_DARK_SOMETHING vec3(.1,.01,.1)
#define SCREEN_GAMMA 2.2

// Function prototypes

float hash(float value); // Produces random values
float hash(vec2 value); // Produces random values
float noise(vec2 position); // A single layer of noise
float value_noise(vec2 position, int octaves); // Multiple layers of noise
mat2 rotate2D(float a); // Returns 2D rotation matrix

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates
    vec2 uv = fragCoord/iResolution.yy;
    vec2 position = uv;

	// Rotate and translate the camera using some random sine waves
	position*=rotate2D(iTime*.1)*pow(2.0,sin(iTime*.1));
	position = position*7.0+sin(vec2(iTime, iTime+2.0));

    // Add some distorsion, often used for chaging the texture
	float distort_amount = sin(iTime*.3)*.5+.5; // set to 0 to disable distorsion
    position = position + distort_amount*vec2(
        2.0*value_noise(position*.23+iTime*.1, 8),
        4.0*value_noise(position*.14-iTime*.1, 8));

    // This is where the texture is sampled.
	float noise_value = value_noise(position, 16);
	noise_value=mix(noise_value, value_noise(position*.2+4.0, 16),.5);

    // Apply gradient 1 which makes it look like clouds
    vec3 col = mix(COLOR_CLOUDS, COLOR_SKY, smoothstep(.2,.7,noise_value));

    // Apply another gradient
    vec3 col2 = mix(COLOR_CLOUDS, COLOR_DARK_SOMETHING, pow(abs(noise_value-(sin(iTime*2.0)*.1+.5))*2.0,.2));

    // Mix between the two gradients
	col=mix(col,col2, smoothstep(-.2,.2, sin(iTime+uv.x*.2))); // cooment out for clouds only

    // Gamma correction
	col=pow(col, vec3(1.0/SCREEN_GAMMA));

    // Output to screen
    fragColor = vec4(col,1.0);
}

mat2 rotate2D(float a){
	float c=cos(a), s=sin(a);
	return mat2(c,s,-s,c);
}

// https://en.wikipedia.org/wiki/Value_noise
float value_noise(vec2 position, int octaves){
	float value = 0.0;
    // Sum together various layers of noise
	for (int i=1; i<octaves; i++)
	{
		float scale = pow(2.0,float(i)); // At different scales
		float contrib = 1.0/scale; // Weighted accordint to scale
		value += noise(position*scale)*contrib;
	}
	return value;
}

float noise(vec2 position){
    // Fractional part is used for interpolation
	vec2 fractional_part = fract(position);
    // Integral part is used for sampling the hash function
	vec2 integral_part = position-fractional_part;

    // Generate 4 sample points
	float sample_00=hash(integral_part);
	float sample_10=hash(integral_part+vec2(1,0));
	float sample_11=hash(integral_part+vec2(1,1));
	float sample_01=hash(integral_part+vec2(0,1));

    // Interpolate them so we have nice and stable continuous texture
	return mix(
		mix(sample_00, sample_10, fractional_part.x),
		mix(sample_01, sample_11, fractional_part.x),
		fractional_part.y
	);
}

float hash(vec2 v) 
{ 
    // Random numbers thrown together to produce other random numbers
    return fract(hash(v.x*.97+v.y*.98)*143.94213); 
}

float hash(float v) 
{ 
    // Even more pseudo randomness
    return fract(fract(v*11.3334)*fract(v*91.73362341)*43.123*429.32234643);  
}

// Set to zero for a better frame rate.
#define REFLECTIONS 1

float hash(vec2 p) {
    return fract(sin(dot(p, vec2(123.45, 875.43))) * 5432.3);
}

float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);

    float a = hash(i);
    float b = hash(i + vec2(1.0, 0.0));
    float c = hash(i + vec2(0.0, 1.0));
    float d = hash(i + vec2(1.0, 1.0));
    f = f * f * (3.0 - 2.0 * f); // smoothstep with no clamp.

    return mix(a, b, f.x) +
            (c - a) * f.y * (1.0 - f.x) +
            (d - b) * f.x * f.y;
}

float fbm(vec2 p) {
	float f = 0.0;
    f += 0.5 * noise(p * 1.1);
    f += 0.22 * noise(p * 2.3);
    f += 0.155 * noise(p * 3.9);
    f += 0.0625 * noise(p * 8.4);
    f += 0.03125 * noise(p * 15.0);

    return f;
}

vec3 vignette(vec3 col, vec2 fragCoord) {
    vec2 q = fragCoord.xy / iResolution.xy;
    col *= 0.5 + 0.5 * pow(16.0 * q.x * q.y * (1.0 - q.x) * (1.0 - q.y), 0.4);
    return col;
}

float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

mat2 rot(float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c, s, -s, c);
}

float sdTorus(vec3 p, vec2 t) {
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float sdCylinder(vec3 p, vec2 rh) {
    return max(length(p.xz) - rh.x, abs(p.y) - rh.y);
}

vec3 opElongate(vec3 p, vec3 h) {
    return p - clamp( p, -h, h );
}

float sdCup(vec3 p) {
    float d = sdTorus(opElongate(p, vec3(0.0, 1.0, 0.0)), vec2(1.0, 0.05));

    vec3 pp = p;
    pp.y += 0.4;
    pp.y -= pp.x * 0.5;
    pp.xy -= vec2(1.0, 0.0);
    pp.zy *= rot(3.14159 * 0.5);
    return smin(d, sdTorus(pp, vec2(0.6, mix(0.08, 0.06, p.y))), 0.15);
}

vec3 getRayDir(vec3 ro, vec3 lookAt, vec2 uv) {
    vec3 forward = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0.0, 1.0, 0.0), forward));
    vec3 up = cross(forward, right);
    return normalize(forward + right * uv.x + up * uv.y);
}

vec2 dmin(vec2 d1, vec2 d2) {
    return d1.x < d2.x ? d1 : d2;
}

vec2 map(vec3 p) {
    vec2 d1 = vec2(sdCup(p), 1.5);

    vec3 pp = p;
    pp.xy *= rot(sin(iTime) * 0.05);
    pp.y -= sin(length(pp.xz) * 10.0 + iTime) * 0.02;
    vec2 d2 = vec2(sdCylinder(pp, vec2(1.0, 0.85)), 2.5); // Coffee
    vec2 d3 = vec2(abs(p.y + 1.1), 3.5); // Table

    return dmin(d1, dmin(d2, d3));
}

vec3 calcNormal(in vec3 p) {
    // Thanks iq!
    vec2 e = vec2(1.0, -1.0) * 0.5773 * 0.0005;
    return normalize(e.xyy * map(p + e.xyy).x + 
					 e.yyx * map(p + e.yyx).x + 
					 e.yxy * map(p + e.yxy).x + 
					 e.xxx * map(p + e.xxx).x);
}

float calcShadow(vec3 p, vec3 lightPos, float sharpness) {
    vec3 rd = normalize(lightPos - p);

    float h;
    float minH = 1.0 / sharpness;
    for (float d = 0.1; d < 32.0; d += h) {
        h = max(0.0, map(p + rd * d).x);
        minH = min(minH, h / d);
        if (minH < 0.01)
            break;
    }

    return minH * sharpness;
}

float calcOcc(vec3 p, vec3 n, float strength) {
    const float dist = 0.3;
    return 1.0 - (dist - map(p + n * dist).x) * strength;
}

int march(vec3 ro, vec3 rd, out vec3 p) {
    float d = 0.01;
    for (float steps = 0.0; steps < 128.0; steps++) {
        p = ro + rd * d;
        vec2 h = map(p);
        if (h.x < 0.001 * d) {
            return int(h.y);
        }

        d += h.x;
    }

    return 0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Raymarch.
    float camTime = clamp(iTime - 4.0, 0.0, 4.0) / 4.0;
    vec2 cyz = mix(vec2(2.0, -2.0), vec2(0.6, -1.2), smoothstep(0.0, 1.0, camTime));
    vec3 ro = 2.5 * vec3(0.2, cyz);
    ro.xy += clamp(iTime - 8.0, 0.0, 1.0) * vec2(sin(iTime - 8.0) * 0.05, 0.0);
    vec3 rd = getRayDir(ro, vec3(0.0), uv);

    vec3 rgb;
    vec3 p;
    float ref = 1.0;
    for (int i = 0; i <= REFLECTIONS; i++) {
        int hit = march(ro, rd, p);

        if (hit > 0) {
            vec3 n = calcNormal(p);
            vec3 lightPos = vec3(20.0, 15.0, -15.0);
            vec3 lightCol = vec3(1.0, 0.9, 0.8);
            vec3 lightToPoint = normalize(lightPos - p);
            vec3 skyCol = vec3(0.15, 0.2, 0.25);
            float sha = calcShadow(p, lightPos, 5.0);
            float occ = calcOcc(p, n, 4.0);
            float spe = pow(max(0.0, dot(rd, reflect(lightToPoint, n))), 15.0);
            vec3 mainLight = clamp(dot(n, lightToPoint), 0.01, 1.0) * lightCol;
            vec3 backLight = clamp(dot(n, -rd), 0.01, 1.0) * 0.1 * lightCol;
            vec3 skyLight = clamp(dot(n, vec3(0.0, 1.0, 0.0)), 0.01, 1.0) * 0.4 * skyCol;
            float d = length(p - ro);
            float fog = 1.0 - exp(-max(0.0, d - 8.0) * 0.06);
            float shiny;

            vec3 mat;
            if (hit == 1) {
                // Cup.
                vec2 wuv = p.xy * 0.7;
                wuv.x *= iResolution.y / iResolution.x;
                wuv.xy += vec2(0.45, 0.4);
                if (abs(wuv.x - 0.5) > 0.35 || abs(wuv.y - 0.5) > 0.5 || p.z > 0.0)
                    mat = vec3(0.5, 0.51, 0.49);
                else
                    mat = texture(iChannel0, wuv).rgb;
                shiny = 0.3;
            } else if (hit == 2) {
                // Coffee.
                mat = vec3(0.43, 0.3, 0.22);
                shiny = 0.2;
            } else if (hit == 3) {
                // Table.
                mat = vec3(0.6, 0.3, 0.18);
                vec3 pp = p;
                pp.x -= 5.6;
                pp.xz *= rot(2.2);
                mat *= 0.4 + 0.6 * abs(sin((3.0 + pp.x) * 0.8 * (5.0 + fbm(pp.xz * 0.8))));
                shiny = 0.1;
            }

            vec3 col = mainLight * lightCol * sha;
            col += backLight * occ;
            col += skyLight * occ;
            col += spe * lightCol * occ;
            col *= mat;
			col = mix(col, skyCol, fog);

            rgb += col * ref;


            rd = reflect(rd, n);
            ref *= shiny;
            ro = p;
        } else {
            // Sky.
            rgb += vec3(0.15, 0.2, 0.25) * ref;
            break;
        }
    }

    // Output to screen
    rgb = pow(rgb, vec3(0.4545));
    fragColor = vec4(vignette(rgb, fragCoord), 1.0);
}

