float Noise(vec2 st) 
{
    vec2 p = floor(st);
    vec2 f = fract(st);
    vec2 u = f*f*(3.0-2.0*f);

    float v00 = random2(p+vec2(0.,0.));
    float v10 = random2(p+vec2(1.,0.));
    float v01 = random2(p+vec2(0.,1.));
    float v11 = random2(p+vec2(1.,1.));

    return mix( mix( dot( vec2(v00), f - vec2(0.,0.) ), dot( vec2(v10), f - vec2(1.,0.) ), u.x ),
                 mix( dot( vec2(v01), f - vec2(0.,1.) ), dot( vec2(v11), f - vec2(1.,1.) ), u.x ), 
                 u.y)+0.5;
}

float fbm (vec2 st) 
{
    float f = 0.;
    vec2 q = st;
    for(int i = 1 ;i < 4;i++){
        f += Noise(q)/pow(2.,float(i) );
        q = q * (2.00+float(i)/100.);
    }

    return f;
}

float meltIce(vec3 p ,float tdist, float size)
{
    vec3 sp = p;
    float s = sdEllipsoid(sp,vec3(0.5,0.2,0.5) * size);

    vec3 b=p + vec3(0.,tdist,0.) ; 
    b.xz *= rot(p.y * 20. + iTime * 2.4 );
    b.yx *= rot(p.x * 15. );
    b.zy *= rot(p.z * 10.);

    float box = sdBox(b ,vec3(0.1)*size);
    s = smin(box,s,1.5);
    return s;
}

float map(vec3 p)
{
    p.y -= 1.;
    vec4 n = vec4(0.0, 1.0, 0.0, 0.9);
    vec3 pp = p;
    pp.y += fbm(pp.xz*2. +vec2(2.,-1.1)*iTime);
    float plane = sdPlane(pp,n);

    vec3 mIp = p - vec3(0.,0.5,0.);

    vec2 areaID = floor(mIp.xz/5.);
    mIp.xz = mod(mIp.xz,5.) - 0.5 * 5.;
    float mI = meltIce(mIp + vec3(0.,0.5,0.) , 0.8,1.0);
    vec3 ppp = p;
    vec2 pillerSize = vec2(.8,0.2);
    pillerSize.x -=saturate( sin(iTime) )/2.;
    ppp.xz = mod(ppp.xz,5.)-5./2.;
    ppp.yz *= rot(acos(-1.)/2.);
    float piller = sdCone(ppp,pillerSize);
    float hashid = random2(areaID);
    for(int i = 1 ; i<7 ; i+=2)
    {
        mIp += vec3(2.,0.,2.) * (random2(vec2(float(i)) ) - 0.5) ;

        float h =  saturate(sin(iTime/float(i) + float(i)*8. + 6.*hashid ) + 0.5);
        h = h * h * h + sin(iTime*2.+hashid*11.)/10.;
        mIp.y += h;
        mIp.xz *= rot(0.1 + float(i)* 3.);
        mI = smin(mI,meltIce(mIp,0.8,0.4 + float(i)/15.),0.3);
    }
   // mI = smin(mI,piller,0.9);
    return smin(mI,plane,0.9) + 0.09;
}

vec2 castRay(vec3 ro,vec3 rd)
{
    float c = 0.;
    float cyc = 0.;
    for(int i = 0; i < 100; i ++)
    {
        vec3 rp = ro + c * rd;
        float d = map(rp);
        float dist = length(ro - rp);
        if(d < 0.00001){
            break;
        }
        c += d;
        cyc += 1.;
    }
    if(c > 20.){c = -1.;}
	return vec2(c,cyc);
}

vec3 calcNormal(vec3 p)
{
    vec2 e=vec2(.001,.0);
    return normalize(.000001+map(p)-vec3(map(p-e.xyy),map(p-e.yxy),map(p-e.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (fragCoord.xy * 2.0 -iResolution.xy) / min(iResolution.x, iResolution.y);
    p.y = -p. y;
	vec3 co = vec3(-0.8,0.,-12.);
    co.z *= hclamp(sin(iTime/16.))*2.;
    co.y += hclamp( cos(iTime/4.) )*hclamp( cos(iTime/4.) )*.5;

    co.xz *= rot(iTime/35.);
    vec3 target = vec3(0.,8.5,0.);
    target.y += 34.*(hclamp(sin(iTime/6.)));
    vec3 cd = normalize(target - co);
    vec3 cu = vec3(0.,1.,0.);
    vec3 cs = normalize(cross(cu , cd));
    cu = normalize(vec3(cross(cs,cd)));

    float fov = (1. - dot(p,vec2(1.3,1.9)*p))/2.;

    vec3 rd = normalize(vec3(p.x * cs + p.y * cu + fov * cd));

    vec3 skycolor = vec3(0.6,0.3,0.4);
    vec3 color = skycolor;
    vec2 cr = castRay(co,rd) ;

    float crp = cr.y/90.;

    //http://sayachang-bot.hateblo.jp/entry/2019/11/30/205926
    if(cr.x > 0.)
    {
    	vec3 N = calcNormal(co + cr.x  * rd);
        vec3 mate=vec3(.18);

        vec3 sun_dir=normalize(vec3(.8,.4,.2));
        sun_dir.xz *= rot(iTime/2.);
        float sun_dif=clamp(dot(N,sun_dir),.0,1.);
        float sun_sha=step(castRay(co+cr.x *rd+N*.001,sun_dir).x,.0);
        float sky_dif=clamp(.5+.5*dot(N,vec3(.0,1.,.0)),.0,1.);
        float bou_dif=clamp(.5+.5*dot(N,vec3(.0,-1.,.0)),.0,1.);

        color=mate*vec3(7.,4.5,3.)*sun_dif*sun_sha;
        color+=mate*vec3(.5,.8,.9)*sky_dif;
        color+=mate*vec3(.7,.3,.2)*bou_dif;
        color = mix(color , skycolor, crp * crp * crp);
    }

    color=pow(color, vec3(.4545));

    fragColor = vec4(color, 1.0);
}

