#define TAU 6.28318530718

float flipRange(float a, float fac) {
    return fac + a * (1.0 - 2.0*fac);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = (fragCoord - iResolution.xy / 2.0) / iResolution.x;
    coord *= 10.0;

    float t = iTime / 2.0;
    float flip = step(0.5, fract(t/2.0));
    float a = fract(t)*TAU/4.0;
    float r = 0.5;

    vec2 fc = coord - 0.5*flip;
    fc -= round(fc);
    fc *= mat2(cos(a), -sin(a), sin(a), cos(a));

    float d = abs(fc.x) + abs(fc.y);

    float fwd = fwidth(coord.x) / 2.0;
    float val = smoothstep(-fwd, fwd, d - r);
    val = flipRange(val, flip);

    fragColor = vec4(val);
}

