vec3 area_light(in vec3 p, in vec3 a, in vec3 b, in vec3 c, in vec3 d, out vec3 n)
{
    vec3 o = (a+b+c+d)/4.;
    vec3 ba = b-a;
    vec3 cb = c-b;
    vec3 dc = d-c;
    vec3 ad = a-d;
    n = normalize(cross(ba, ad));
    p = p-n*dot(p-o, n);
    vec3 pa = p-a;
    vec3 pb = p-b;
    vec3 pc = p-c;
    vec3 pd = p-d;
    float s = 4., l = 100000.;
    vec3 vo, vh, vd;
    if (sign(dot(cross(ba, n), pa)) <= -1. && length(pa) < l)
    {
        s--; l = length(pa); vo = a; vh = b; vd = pa;
    }
    if (sign(dot(cross(cb, n), pb)) <= -1. && length(pb) < l)
    {
        s--; l = length(pb); vo = b; vh = c; vd = pb;
    }
    if (sign(dot(cross(dc, n), pc)) <= -1. && length(pc) < l)
    {
        s--; l = length(pc); vo = c; vh = d; vd = pc;
    }
    if (sign(dot(cross(ad, n), pd)) <= -1. && length(pd) < l)
    {
        s--; l = length(pd); vo = d; vh = a; vd = pd;
    }
    if (s < 4.)
    {
        float t = clamp(dot(vh-vo, vd)/dot(vh-vo, vh-vo), 0., 1.);
        return mix(vo, vh, t);
    }
    else
        return p;
}

// Quad function from IQ
// https://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float dot2( vec3 v ) { return dot(v,v); }
float quad( vec3 p, vec3 a, vec3 b, vec3 c, vec3 d )
{
  vec3 ba = b - a; vec3 pa = p - a;
  vec3 cb = c - b; vec3 pb = p - b;
  vec3 dc = d - c; vec3 pc = p - c;
  vec3 ad = a - d; vec3 pd = p - d;
  vec3 nor = cross( ba, ad );

  return sqrt(
    (sign(dot(cross(ba,nor),pa)) +
     sign(dot(cross(cb,nor),pb)) +
     sign(dot(cross(dc,nor),pc)) +
     sign(dot(cross(ad,nor),pd))<3.0)
     ?
     min( min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(dc*clamp(dot(dc,pc)/dot2(dc),0.0,1.0)-pc) ),
     dot2(ad*clamp(dot(ad,pd)/dot2(ad),0.0,1.0)-pd) )
     :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) );
}

vec2 opU(in vec2 a, in vec2 b)
{
    return a.x<b.x?a:b;
}

vec2 scene(in vec3 p)
{
    vec2 plane1 = vec2(p.y, 1.5);
    vec2 quad1 = vec2(quad(p, A, B, C, D), 2.5);
    vec2 quad2 = vec2(quad(p, AA, BB, CC, DD), 2.5);
    vec4 sphere = vec4(0, -1, 3, 2.5);
    vec2 sphereDist = vec2(length(p - sphere.xyz) - sphere.w, 1.5);

    vec2 sc1 = opU(sphereDist, quad1);
    vec2 sc2 = opU(sc1, quad2);
    return opU(plane1, sc2);
}

vec3 getNormal(in vec3 p)
{
    float d = scene(p).x;
    vec2 e = vec2(.1, 0.);
    vec3 n = d - vec3
        			(
                        scene(p-e.xyy).x,
                        scene(p-e.yxy).x,
                        scene(p-e.yyx).x
    				);
    return normalize(n);
}

vec2 marcher(in vec3 o, in vec3 d)
{
    float t = 0.;
    for (int i = 0; i < 100; i++)
    {
        vec2 s = scene(o + d * t);
        if (s.x < .01)
            return vec2(t, s.y);
        t += s.x * .8;
    }
    return vec2(-1.5);
}

vec3 computeAreaLight(vec3 p, vec3 p1, vec3 p2, vec3 p3, vec3 p4) {
    vec3 n = vec3(0.);
    vec3 pl = area_light(p, p1, p2, p3, p4, n);

    //float df = dot(normalize(p-pl), n);

    float l = length(p-pl);
    float kc = .9;
    float kl = .7;
    float kq = .15;
    float attenuation = 1. / (kc + kl * l + kq * l * l);

    return lightColor * attenuation; // *df;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.);

    vec3 o = vec3(0., 3., 10.);
    vec3 d = vec3(uv, -1.);

    float T = sin(iTime);
    mat2 rot = mat2(cos(T), sin(T), -sin(T), cos(T));

    A.xz *= rot;
    B.xz *= rot;
    C.xz *= rot;
    D.xz *= rot;

    vec2 m = marcher(o, d);
    int index = int(floor(m.y));
    if (index != -1)
    {
        vec3 p = o + d * m.x;
        if (index == 1)
        {
            col += computeAreaLight(p, A, B, C, D);
            col += computeAreaLight(p, AA, BB, CC, DD);
        }
        if (index == 2)
        {
            col += lightColor;
        }
    }

    fragColor = vec4(sqrt(col),1.0);
}

float random (in vec2 _st) {
    return fract(sin(dot(_st.xy, vec2(0.9,-0.5)))*757.153);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 _st) {
    vec2 i = floor(_st);
    vec2 f = fract(_st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3. - 2. * f);

    return mix(a, b, u.x) + (c - a)* u.y * (1. - u.x) + (d - b) * u.x * u.y;
}

float fbm ( in vec2 _st) {
    float v = sin(iTime*0.3)*0.1;
    float a = 0.1;
    vec2 shift = vec2(100.);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(1.0), -sin(0.5), acos(0.5));
    for (int i = 0; i < 3; ++i) {
        v += a * noise(_st);
        _st = rot * _st * 2.0 + shift;
        a *= 2.8;
    }
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = (fragCoord * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y) * 0.8;

    vec2 coord = st;
    coord.x += 0.2*iTime;
    coord.y += 0.2*iTime;

    float len = length(coord) - 3.;     

    vec3 color = vec3(0.);

    vec2 q = vec2(0.);
    q.x = fbm( st + 1.0);
    q.y = fbm( st + vec2(-0.450,0.650));

    vec2 r = vec2(0.);
    r.x = fbm( st + 1.0*q + vec2(0.570,0.520)+ 0.1*iTime );
    r.y = fbm( st + 1.0*q + vec2(0.340,-0.570)+ 0.07*iTime);

    color = mix(color, cos(len + vec3(0.2, 0.0, 0.5)), 1.0);
    color = mix(vec3(0.730,0.237,0.003), vec3(0.667,0.295,0.005), color);

    float f = fbm(st+r);
    fragColor = vec4(2.0*(f*f*f+.6*f*f+.5*f)*color,1.);
}

