/**
 * Part 6 Challenges:
 * - Make a scene of your own! Try to use the rotation transforms, the CSG primitives,
 *   and the geometric primitives. Remember you can use vector subtraction for translation,
 *   and component-wise vector multiplication for scaling.
 */

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 1000.0;
const float EPSILON = 0.0001;

vec3 hash33(vec3 p3)
{	
    return fract((p3.zyx*sin(p3.x)+2.0)*(p3.zxy*cos(p3.x)+2.0+sin((p3.x+p3.y+p3.z)*2.0)));
}


/**
 * Signed distance function describing the scene.
 * 
 * Absolute value of the return value indicates the distance to the surface.
 * Sign indicates whether the point is inside or outside the surface,
 * negative indicating inside.
 */
float sceneSDF(vec3 samplePoint) {    
    return length(hash33(sin(samplePoint)/10.0))-sin(length(samplePoint))/3.33;
}

/**
 * Return the shortest distance from the eyepoint to the scene surface along
 * the marching direction. If no part of the surface is found between start and end,
 * return end.
 * 
 * eye: the eye point, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 * start: the starting distance away from the eye
 * end: the max distance away from the ey to march before giving up
 */
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}


/**
 * Return the normalized direction to march in from the eye point for a single pixel.
 * 
 * fieldOfView: vertical field of view in degrees
 * size: resolution of the output image
 * fragCoord: the x,y coordinate of the pixel in the output image
 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

/**
 * Using the gradient of the SDF, estimate the normal on the surface at point p.
 */
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

/**
 * Lighting contribution of a single point light source via Phong illumination.
 * 
 * The vec3 returned is the RGB color of the light's contribution.
 *
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 * lightPos: the position of the light
 * lightIntensity: color/intensity of the light
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        // Light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}

/**
 * Lighting via Phong illumination.
 * 
 * The vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = vec3(4.0,
                          2.0,
                          4.0);
    vec3 light1Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);

    vec3 light2Pos = vec3(2.0 * sin(0.37),
                          2.0 * cos(0.37),
                          2.0);
    vec3 light2Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light2Pos,
                                  light2Intensity);    
    return color;
}

/**
 * Return a transform matrix that will transform a ray from view space
 * to world coordinates, given the eye point, the camera target, and an up vector.
 *
 * This assumes that the center of the camera is aligned with the negative z axis in
 * view space when calculating the ray marching direction. See rayDirection.
 */
mat3 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat3(s, u, -f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(8.0+iTime*10.0, 5.0, 7.0*iTime);

    mat3 viewToWorld = viewMatrix(eye, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));

    vec3 worldDir = viewToWorld * viewDir;

    float dist = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
		return;
    }

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist * worldDir;

    // Use the surface normal as the ambient color of the material
    vec3 K_a = hash33(p/(500.0));
    vec3 K_d = K_a;
    vec3 K_s = vec3(1.0, 1.0, 1.0);
    float shininess = 10.0;

    vec3 color = phongIllumination(K_a, K_d, K_s, shininess, p, eye);

    fragColor = vec4(color, 1.0);
}

const float bigmag = 3000.;
float scale = 0.;
const float maxtimescale = 15.;

vec2 cmpxmul(in vec2 a, in vec2 b) {
	return vec2(a.x * b.x - a.y * b.y, a.y * b.x + a.x * b.y);
}

vec2 calcm(vec2 num) {
    float i=0.;
    vec2 num2 = vec2(0.,0.);

    while (length(num2) < bigmag && i < 500.) {
    	num2 = cmpxmul(num2,num2) + num/scale;
        i+=1.;
    }

	return vec2(1.-length(num2)/bigmag, i);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    scale = 29. + (cos(iTime/20. - 3.142)+1.)*maxtimescale;
    fragCoord.xy = (fragCoord.xy/iResolution.xy - vec2(.5))*iResolution.xy;

    vec3 camPos = vec3( vec2(-.000136705,0.00198882)*scale * iResolution.xy , pow(1.5, scale-20.)/10.);
    vec2 fragOffset = (fragCoord-camPos.xy)/camPos.z;

	fragColor = vec4(calcm(camPos.xy + fragOffset) / vec2(1.,200.), 0., 1.);
}

#define time iTime
const float PI = 3.14159265;
const float HARF_PI = 1.57079632;

// Background shader based on : 
// https://www.shadertoy.com/view/4sVBDm
// ---------------------------------------------------------------------

//noise background
const float noiseIntensity = 2.8;
const float noiseDefinition = 0.6;
const vec2 glowPos = vec2(-2., 0.);

float random(vec2 co){
    return fract(sin(dot(co.xy ,vec2(-0.950,-0.910))) * 43758.5453);
}

float noise( in vec2 p ){
    p*=noiseIntensity;
    vec2 i = floor( p );
    vec2 f = fract( p );
	vec2 u = f*f*(3.0-2.0*f);
    return mix( mix( random( i + vec2(0.0,0.0) ), 
                     random( i + vec2(1.0,0.0) ), u.x),
                mix( random( i + vec2(0.0,1.0) ), 
                     random( i + vec2(1.0,1.0) ), u.x), u.y);
}

float fbm( in vec2 uv ){	
	uv *= 5.0;
    mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
    float f  = 0.5*noise( uv ); uv = m*uv;
    f += 5.*noise( uv ); uv = m*uv;
    f += 5.*noise( uv ); uv = m*uv;    
	f = 0.1 + .5*f;
    return f;
}

vec3 bg(vec2 uv ){
    float velocity = time * 0.5;
    float intensity = sin(uv.x*noise(uv)*7.+velocity*3.)*noise(uv*10.)*1.0+.3;
    uv.y -= 2.;
    vec2 bp = uv+glowPos;
    uv *= noiseDefinition;

    //ripple
    float rb = fbm(vec2(uv.x*.5-velocity*.03, uv.y))*.1;
    uv += rb;

    //coloring
    float rz = fbm(uv*.9+vec2(velocity*.35, 0.0));
    rz *= dot(bp*intensity,bp);

    //bazooca line
    rz *= sin(uv.x*0.1+velocity*0.8);

    //lightning
    rz *= 5.*sin(uv.x*0.1+velocity*sin(time));

    vec3 bgColor = vec3(cos(time),0.45 * sin(time*0.2),sin(time*0.3));
    vec3 col = bgColor/(.1-rz);
    return sqrt(abs(col));
}

// IFS MengerSponge is based on gam0022.net
// https://gam0022.net/blog/2019/06/25/unity-raymarching/
// ---------------------------------------------------------------------
vec3 trans(vec3 p){
    return mod(p, 4.0) -2.0;
}

#define ITERATIONS 4

vec3 _MengerOffset = vec3(1.000,0.072,0.414);
float _MengerFold = 3.;

// IFS MengerSpongeãDistanceFunction
float dMenger(vec3 z0, vec3 offset, float scale) {
    vec4 z = vec4(z0, 1.0);
    for (int n = 0; n < ITERATIONS; n++) {
        z = abs(z);

        if (z.x < z.y) z.xy = z.yx;
        if (z.x < z.z) z.xz = z.zx;
        if (z.y < z.z) z.yz = z.zy;

        z *= scale;
        z.xyz -= offset * (scale - 1.0);

        if (z.z < -0.5 * offset.z * (scale - 1.0))
            z.z += offset.z * (scale - 1.0);
    }
    return (length(max(abs(z.xyz) - vec3(1.0, 1.0, 1.0), 0.0)) - 0.05) / z.w;
}

// 2D rotate
mat2 rotate(in float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, s, -s, c);
}

// Deformation used folding
// https://www.shadertoy.com/view/Mlf3Wj
vec2 foldRotate(in vec2 p, in float s) {
    float a = PI / s - atan(p.x, p.y);// PI/s = offset, atan(p.x, p.y) : XY to Polar coordinates
    float n = PI*2. / s;// Unit angle
    a = floor(a / n) * n;
    p *= rotate(a);
    return p;
}

// Final DistanceFunction
float map(vec3 pos) {
    pos.xy = foldRotate(pos.xy, _MengerFold);
    pos.yz = foldRotate(pos.yz, _MengerFold);
    vec3 p = trans(pos);
    float _MengerScale = -(1.1 +0.7*smoothstep(0.0,2.0,1.-cos(time*0.1)));
    return dMenger(p, _MengerOffset, _MengerScale);
}

// Normal and Camera
// baced on Kazuya Hiruma @edo_m18
// https://qiita.com/edo_m18/items/1532aceb9d470174caaf#%E3%83%90%E3%83%AA%E3%83%A5%E3%83%BC%E3%83%8E%E3%82%A4%E3%82%BA
// ---------------------------------------------------------------------
// Normal
vec3 getNormal(vec3 p){
    const vec2 e = vec2(0.001, 0.0);
	return normalize(vec3(
		map(p + e.xyy) - map(p - e.xyy),
		map(p + e.yxy) - map(p - e.yxy),
		map(p + e.yyx) - map(p - e.yyx)));
}

// Camera
mat3 camera(vec3 ro, vec3 ta){
    vec3 cw = normalize(ta - ro);// camera forward, ro = Ray origin, ta = Target
    vec3 cp = vec3(0.0, 1.0, 0.0);
    vec3 cu = cross(cw, cp);
    vec3 cv = cross(cu, cw);
    return mat3(cu, cv, cw);
}

// lighting
vec3 getMetalColor(vec3 n, vec3 rd, vec2 p, vec2 uv){

    vec3 refDir = reflect(rd, n);// reflect direction
    float specular = max(0.0, dot(refDir, vec3(-0.017,0.473,-0.617)));// light position
    vec3 SPECULAR_COLOR = 30.0*pow(specular, 1.5)*(vec3(0.195,0.157,0.144));// light color
    vec3 bgCol = bg(p)*(2.-abs(uv.y*2.));
    vec3 spCol = mix(bgCol, SPECULAR_COLOR, 0.5);

    return mix(spCol, refDir, 0.2);
}

// fog
// based on Atsushi Asakura @aa_debdeb
// https://qiita.com/aa_debdeb/items/b78975c5bcb063e28a08
float exp2Fog(float d, float density) {
	float dd = d * density;
	return exp(-dd * dd);
}

