void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;// background

    // Wide-angle lens is baced on Menger Sponge Variation
    // https://www.shadertoy.com/view/ldyGWm
    // background
    vec3 st = (vec3(2.0 * fragCoord - iResolution.xy, iResolution.y));
    st = normalize(vec3(st.xy, sqrt(max(st.z * st.z - dot(st.xy, st.xy) *4.*sin(time*0.4),0.))));

    // camera  
    vec3 ta1 = vec3(0.0,0.0,-1.0);// target1
    vec3 ta2 = vec3(cos(time*0.4+HARF_PI),0.0,time+sin(time*0.4+HARF_PI));// target2

    float moveSwitch =  step(0.942, smoothstep(0.0,2.0,1.-cos(time*0.1)));
    vec3 ta = mix(ta1, ta2, moveSwitch);//if(smooth...<=0.942){ta1} else {ta2}

	vec3 ro = vec3(0.0, 0.0, time);// ray origin = camera position

	mat3 c = camera(ro, ta);

    // ray
    float fl = 1.0;// focalLength
	vec3 rd = c * normalize(vec3(p, fl));// ray direction = normalize(vec3(cu*p.x + cv*p.y + cw*fl));

    // marching loop
    float d;
    float t = 0.0;
    vec3  rPos = ro;
    for(int i = 0; i < 64; i++){
        d = map(rPos);
        t += d;
        rPos = ro + rd * t;
    }

    //color
    vec3 normal = getNormal(rPos);
    vec3 col1 = getMetalColor(normal, rd, st.xy, uv);
    vec3 col2 = getMetalColor(normal, rd, p, uv);
    vec3 color = mix(col1, col2, moveSwitch);//if(smooth...<=0.942){col1} else {col2}

    //fog
    vec3 fogColor = vec3(1.000,0.976,0.822);
    float f = exp2Fog(d, 8.);
		color = mix(fogColor, color, f);

	fragColor = vec4(color, 1.0);
}

const float INFINITY = 1.0 / 0.0;
const float M_PI = 3.1415926f;
const float earthRadius = 6360e3;
const float atmosphereRadius = 6420e3;
const float Hr = 7994.;
const float Hm = 1200.;
const vec3 betaR = vec3(3.8e-6, 13.5e-6,33.1e-6);
const vec3 betaM = vec3(21e-6f);
// from 'the graphics codex'
// Ray-Sphere Intersection
bool raySphereIntersect(vec3 P, vec3 w, vec3 C, float r, out float t0, out float t1) { 
    vec3 v = P - C;

    float b = 2.0 * dot(w, v);
    float c = dot(v,v) - r * r;

    float d = b * b - 4.0 * c;

    if(d<0.0) return false;

    float s = sqrt(d);
    t0 = (-b-s) * 0.5;
    t1 = (-b+s) * 0.5f;

    return true;
}

vec3 computeIncidentLight(vec3 o, vec3 d, float tmin, float tmax, vec3 sunDirection)
{
    float t0, t1;
    if(!raySphereIntersect(o, d, vec3(0.0), atmosphereRadius, t0, t1) || t1 < 0.) return vec3(0.0);
    if(t0>tmin && t0>0.) tmin = t0;
    if(t1<tmax) tmax = t1;
    uint numSamples = 16u;
    uint numSamplesLight = 8u;
    float segmentLength = (tmax-tmin)/float(numSamples);
    float tCurrent = tmin;
    vec3 sumR = vec3(0.0);
    vec3 sumM = vec3(0.0);
    float opticalDepthR = 0.0;
    float opticalDepthM = 0.0;
    float mu = dot(d, sunDirection);
    float phaseR = 3.f / (16.f * M_PI) * ( 1. + mu * mu);
    float g = 0.76;
    float phaseM = 3. / (8. * M_PI) * ((1.-g*g)*(1. + mu*mu)) / ((2. + g*g) * pow(1. + g*g - 2. * g * mu, 1.5));

    for(uint i = 0u; i < numSamples; ++i) {
        vec3 samplePosition = o + (tCurrent + segmentLength * 0.5) * d;
        float height = length(samplePosition) - earthRadius;
        float hr = exp(-height/ Hr) * segmentLength;
        float hm = exp(-height/Hm) * segmentLength;
        opticalDepthR += hr;
        opticalDepthM += hm;
        float t0Light;
        float t1Light;
        raySphereIntersect(samplePosition, sunDirection, vec3(0.0), atmosphereRadius, t0Light, t1Light);
        float segmentLengthLight = t1Light / float(numSamplesLight);
        float opticalDepthLightR = 0.;
        float opticalDepthLightM = 0.;
        float tCurrentLight = 0.;
        uint j;
        for(j=0u;j<numSamplesLight;++j){
            vec3 samplePositionLight = samplePosition + (tCurrentLight + segmentLengthLight * 0.5) * sunDirection;
            float heightLight = length(samplePositionLight) - earthRadius;
            if(heightLight < 0.0) break;
            opticalDepthLightR += exp(-heightLight / Hr) * segmentLengthLight;
            opticalDepthLightM += exp(-heightLight / Hm) * segmentLengthLight;
            tCurrentLight += segmentLengthLight;
        }
        if(j==numSamplesLight) {
            vec3 tau = vec3(betaR * (opticalDepthR + opticalDepthLightR) + betaM * 1.1 * (opticalDepthM + opticalDepthLightM));
            vec3 attenuation = exp(-tau);
            sumR += attenuation * hr;
            sumM += attenuation * hm;
        }	
        tCurrent += segmentLength;
    }

    return vec3(sumR * betaR * phaseR + sumM * betaM * phaseM) * 20.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy  * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float angle = M_PI / 2.0 * (fract(float(iFrame)/180.)*2.-1.);
    vec3  sunDirection = vec3(sin(angle), cos(angle), 0.0);

    float x = uv.x;
    float y = uv.y;
    float z2 = x*x + y*y;

    vec3 col = vec3(0.0);
    if(z2 <= 1.){
        float phi = atan(y,x);
        float theta = acos(1.-z2);
        vec3 dir = vec3(sin(theta)*cos(phi), cos(theta), sin(theta)*sin(phi));
        col = computeIncidentLight(vec3(0.0, earthRadius + 1., 0.0), dir, 0., INFINITY, sunDirection);
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

