//  1 out, 3 in...
float hash13(vec3 p3) {
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float rand(float n){return fract(sin(n) * 43758.5453123);}

float noise(float p){
	float fl = floor(p);
  float fc = fract(p);
	return mix(rand(fl), rand(fl + 1.0), fc);
}

float sdSphere(vec3 center, float rad, vec3 p) {
    return length(p - center) - rad;
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float sdOctahedron( vec3 p, float s) {
  p = abs(p);
  return (p.x+p.y+p.z-s)*0.57735027;
}

float sdOctahedronSquash( vec3 p, float s, float ys) {
  p = abs(p) * vec3(1.0, ys, 1.0);
  return (p.x+p.y+p.z-s)*0.57735027;
}

float scene(vec3 p, float type) {
    float modul = 4.0;
    float hmod  = modul * 0.5;

    vec3 realp = p;

    p.y += noise(sin(p.x * 0.01 + iTime * 0.4) * 10.0) * 2.0; 
    p.z += noise(sin(p.z * 0.01 + iTime * 0.4) * 10.0) * 2.0; 



    vec3 octahedron_index = (p + hmod) - mod((p+hmod), modul);
    octahedron_index.y = 0.0;
    // vec3 octahedron_index = p - mod(p, modul);
    // octahedron_index.y = 0.0;


    float pt = (p.z + hmod) - mod((p.z + hmod), modul);
    float pq = (p.x + 128.0 + hmod) - mod((p.x + 128.0 + hmod), modul);

    p.x = mod(p.x + hmod, modul) - hmod;
    p.y = p.y;
    p.z = mod(p.z + hmod, modul) - hmod;



    // float mint = sphere(vec3(0.0, 0.0, 8.0), 5.0, p);
    // float octahedron_index = 
    float octahedron_height = noise(pt * 0.25 + pq * 0.25 + iTime * 0.2);
    float octahedron  = sdOctahedronSquash(p, 2.0, octahedron_height * 2.0 + 0.4);






    p = p + vec3(2.0, 0.0, 2.0);
    p.x = mod(p.x + hmod, modul) - hmod;
    p.y = p.y;
    p.z = mod(p.z + hmod, modul) - hmod;
    pt = (p.z + hmod) - mod((p.z + hmod), modul);
    pq = (p.x + 128.0 + hmod) - mod((p.x + 128.0 + hmod), modul);
    octahedron_height = noise(pt * 0.15 + pq * 0.15);

    float octahedron2 = sdOctahedronSquash(p, 2.0, octahedron_height * 2.0 + 0.4);








    vec3 tp = realp;
    tp.y += noise(iTime * 0.5 + octahedron_index.x * 17.0 + octahedron_index.z * 23.0) * 8.0;
    float hmod2 = hmod * 1.2;
    float modul2 = modul * 1.2;
    tp.x = mod(tp.x + hmod2, modul2) - hmod2;
    tp.y = tp.y - 8.0;
    tp.z = mod(tp.z + hmod2, modul2) - hmod2;
    float octahedron3 = sdOctahedronSquash(tp, 0.2, 0.5);
    // remove octahedron at the center
    if(length(realp.xz - vec2(0.0, 0.0)) < 0.5) octahedron3 = 999999.0;






    float plane      = p.y;

    float mint = opSmoothUnion(octahedron, octahedron2, sin(realp.x * 2.0 + iTime) * 0.5 + 0.5);
    if(octahedron2 < mint) mint = octahedron2;
    if(octahedron3 < mint) mint = octahedron3;
    if(plane < mint) mint = plane;


    float sphere = sdSphere(vec3(0.0, 2.0 + (sin(iTime) * 0.5 + 0.5) * 3.0, 0.0), 0.75, realp); 
    if(sphere < mint && type > 0.0) mint = sphere;

	return mint;
}


float shadowRay(vec3 ro, vec3 lightPos) {
    float epsilon = 0.01;
    float dist = length(lightPos - ro);
    vec3 rd = normalize(lightPos - ro);


    vec3 p = ro + rd * 0.15;
    for(float i = 0.0; i < 50.0; i++) {
        float t = scene(p, 0.0);

        if(t < 0.0) {
            p += rd * t;
            return 0.0;
        }
        if(abs(t) < epsilon) {
            p += rd * t;
            break;
        }

        p += rd * t;
    }

    float hitDist = length(p - ro);
    if(dist - hitDist < 0.4) return 1.0;
    return 0.0;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;





    vec2 ndv = (uv * 2.0 - 1.0) * vec2(iResolution.x / iResolution.y, 1.0);
    float fov = 1.5;

    vec3 target = vec3(0.0);
    // vec3 ro = vec3(-8.0, 6.0, -6.0);
    vec3 ro = vec3(sin(iTime * 0.2) * 4.0, 3.0, cos(iTime * 0.2) * 4.0);
    float rot = 0.5;
	// vec3 ro = vec3(sin(rot) * 4.0, 3.0, cos(rot) * 4.0);

    ro *= 3.0;


    vec3 w  = normalize(target - ro);
    vec3 u  = normalize(cross(w, vec3(0.0, 1.0, 0.0)));
    vec3 v  = normalize(cross(u, w));


    vec3 rd = normalize(vec3(ndv, 1.0 * fov)); 
    rd = w * rd.z + v * rd.y + u * rd.x;

    vec3 col = vec3(0.0);

    float epsilon = 0.03;
    vec3 lightPos = vec3(0.0, 2.0 + (sin(iTime) * 0.5 + 0.5) * 3.0, 0.0);
    float diffuse = 1.0;
    vec3 n;

    vec3 p = ro;
    for(float i = 0.0; i < 50.0; i++) {
        float t = scene(p, 1.0);

        if(abs(t) < epsilon) {

            float px = scene(p + vec3(+epsilon, 0.0, 0.0), 1.0);
            float py = scene(p + vec3(0.0, +epsilon, 0.0), 1.0);
            float pz = scene(p + vec3(0.0, 0.0, +epsilon), 1.0);


            n = normalize(vec3(px - t, py - t, pz - t));
            // n.z = -n.z;

            vec3 lightDir = normalize(lightPos - p);

            float attenuation = 1.0 / (pow(length(lightPos - p), 2.0)); 

            diffuse = max(dot(n, lightDir), 0.0) * attenuation * 10.0;

            //p = p + rd * t * 0.85;
            break;
        } else {
        	p = p + rd * t * 0.85;
        }

        if(i > 28.0) {
           diffuse = 0.0;
        }
    }


    // shadow ray
    float srr = max(shadowRay(p, lightPos), 0.075);
    col = vec3(diffuse * srr + 0.0025) * vec3(1.0, 0.6, 0.2);
    col *= texture(iChannel1, p.xz * 0.05 + p.y * 0.01).rgb * 0.75;


    if(length(lightPos - p) < 1.2) {
       col *= vec3(2030.0);
    }


    float gamma = 2.2;
    float exposure = 1.5;
	col *= pow(2.0, exposure);
    col = max(vec3(0.), col - vec3(0.004));
	col = (col * (6.2 * col + .5)) / (col * (6.2 * col + 1.7) + 0.06);
	col = pow(col, vec3(1. / gamma));


    // Output to screen
    fragColor = vec4(col,1.0);
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),sin(_angle),cos(_angle));
}

#define MAXITER 810
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv = uv*2.5-vec2(.5,0.);
    float t = iTime * .15;
	float angle = t;    
    uv *= rotate2d(angle);
    float scale = 1.5;
    uv *= 1.-sin(scale);
    uv.x -= scale * (sin(scale)/2.+.03);
    uv.y -= scale * (sin(scale)/5.-.30);
	vec2 c = uv;
    vec2 z = c;
    int escape = 0;
    for(int i = 0;i<MAXITER;i++){
		float tempx = z.x*z.x - z.y * z.y + c.x;
        z.y = 2. * z.x * z.y + c.y;
        z.x = tempx;
        if(length(z)>2.){
           escape = i;
            break;
        }
    }

    float sl = (float(escape) + log2(length(z)))/float(MAXITER);
    vec3 col = vec3(sin(sl)*2.*cos(iTime), sin(sl)/3.,cos(sl)/4.);
    col += sl;
    if(length(z)<2.0){
      col = vec3(1.);   
    }
    fragColor = vec4(col,1.0);
}

// 2D rotate
mat2 rotate(in float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, s, -s, c);
}

