void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Wide-angle lens is baced on Menger Sponge Variation
    // https://www.shadertoy.com/view/ldyGWm
    vec3 st = (vec3(2.0 * fragCoord - iResolution.xy, iResolution.y));
    st = normalize(vec3(st.xy, sqrt(max(st.z * st.z - dot(st.xy, st.xy) *4.*sin(iTime*0.5),0.)))); 

    st.xy *= rotate(iTime*0.5);

    vec3 bgCol = bg(st.xy)*(1.9-abs(st.y*2.));

	fragColor = vec4(bgCol, 1.0);
}

const float cloudscale = 2.5; // 1.1
const float speed = 0.03;
const float clouddark = 0.5;
const float cloudlight = 0.3;
const float cloudcover = 0.2;
const float cloudalpha = 8.0;
const float skytint = 0.5;
const vec3 skycolour1 = vec3(0.2, 0.4, 0.6);
const vec3 skycolour2 = vec3(0.4, 0.7, 1.0);

const mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );

// <personal favorite noise funcs>
float hash(vec2 uv) {
	return fract(sin(dot(uv, vec2(123.45, 678.9))) * 987654.321);
}

float noise(vec2 uv) {
	vec2 i = floor(uv);
	vec2 f = fract(uv);
	vec2 b = smoothstep(0.0, 1.0, f);
	return 2.0 * mix(
		mix(hash(i), hash(i + vec2(1.0, 0.0)), b.x),
		mix(hash(i + vec2(0.0, 1.0)), hash(i + vec2(1.0, 1.0)), b.x), b.y) - 1.0;
}
// </personal favorite noise funcs>

// common part of FBMs...
float fbm(bool do_abs, vec2 n, float amp_init, float amp_step, float q, float base_octave) {
    float time = iTime * speed * base_octave;

    n *= vec2(iResolution.x/iResolution.y,1.0) * cloudscale * base_octave;
    n -= q - time;

	float total = 0.0, amplitude = amp_init;
	for (int i = 0; i < 7; i++) {
        float v = noise(n) * amplitude;
        v = do_abs ? abs(v) : v;

		total += v;

		n = m * n + time;
		amplitude *= amp_step;
	}
	return total;
}

float calcF(vec2 p, float q) {
    //ridged noise shape
	float r = fbm(true, p, 0.8, 0.7, q, 1.0);

    //noise shape
	float f = fbm(false, p, 0.7, 0.6, q, 1.0);

    f *= r + f;
    f = cloudcover + cloudalpha*f*r;
    return f;
}

float calcC(vec2 p, float q) {
    //noise colour
    float c = fbm(false, p, 0.4, 0.6, q, 2.0);

    //noise ridge colour
    float c1 = fbm(true, p, 0.4, 0.6, q, 3.0);

    return c + c1;
}

// -----------------------------------------------

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = fragCoord.xy / iResolution.xy;

    // f, c, q are not sure yet...

    // q is used to subtract from uv with time in fbm()...???
    float q = fbm(false, p, 0.1, 0.4, 0.0, 1.0);

    // f part(?)
	float f = calcF(p, q);

    // c part(color?)
    float c = calcC(p, q);

    // sky color is just a vertical screen gradietion
    vec3 skycolour = mix(skycolour2, skycolour1, p.y);
    vec3 cloudcolour = vec3(1.1, 1.1, 0.9) * clamp((clouddark + cloudlight*c), 0.0, 1.0);

    // The key strategy of shader would be here:
    // mix sky color and cloud color by (f + c).
    // The cloud color is calculated from darken sky color (by skytint) + cloud color.
    // Not sure f + c meaning yet...
    vec3 result = mix(skycolour, clamp(skytint * skycolour + cloudcolour, 0.0, 1.0), clamp(f + c, 0.0, 1.0));

	fragColor = vec4( result, 1.0 );
}

