#define time iTime
#define PI acos(-1.)

// taken from YX here : https://www.shadertoy.com/view/tdlXW4
// rough shadertoy approximation of the bonzomatic noise texture
vec4 texNoise(vec2 uv)
{
    float f = 0.;
    f += texture(iChannel0, uv*.125).r*.5;
    f += texture(iChannel0, uv*.25).r*.25;
    f += texture(iChannel0, uv*.5).r*.125;
    f += texture(iChannel0, uv*1.).r*.125;
    f=pow(f,1.2);
    return vec4(f*.45+.05);
}

float hash21(vec2 x)
{return fract(sin(dot(x,vec2(32.6,13.4)))*1254.4);}

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float linedist(vec3 p, vec3 a, vec3 b) 
{
    float k = dot(p-a, b-a)/dot(b-a,b-a);
    return distance(p, mix(a, b, clamp(k, 0., 1.)));
}

float face, body; 
float g1=0.;
float SDF (vec3 p)
{
    float r = 0.5;
    float sy = 1.+sin(time*15.)*0.1+0.1;
    float sxz = 1./sy;
    vec3 resize = vec3(sxz,sy,sxz);
    p.y +=r ;
    p *= resize;
    p.y -= r;
    vec3 pp = p;

    // corps

    p.x -= texNoise(p.yz*0.1+time*0.5).r*0.1;
    p.y += texNoise(p.xz*0.08+time*0.5).r*0.25;
    float bd = length(p)-r; 
    // nez
    p.x *= 0.8; 
    bd = min(bd,(length(p+vec3(0.,0.08,r*1.2))-0.12)/length(vec3(0.8,0.,0.)));


    // yeux
    p = pp;
    p.x = abs(p.x)-0.15;
    p.x *= 1.3;
    float fd = length(p+vec3(0.,-0.07,r*.92))-0.1;

    // sourcils
    p = pp;
    p.x = abs(p.x)-0.18;
    p += vec3(0.,-0.28,r*0.9);
    p.xz *= rot(PI/6.);
    p.y += p.x*p.x*2.;
    fd = min(fd,max(length(p.yz)-(0.01-sqrt(p.x*p.x+0.001)*0.15),abs(p.x)-0.2));

    // moustache
    p = pp;
    p *= 1.1;
    p.y += cos(p.x*3.)*0.2;
    float line = linedist(p,vec3(-r,0.,-r),vec3(r,0.,-r))-(0.06-sqrt(p.x*p.x+0.001)*0.2) / 1.1;
    p = pp;  
    p *= 1.1;
    p.x = abs(p.x);
    float spheres = length(p+vec3(-0.35, 0.15, r))-0.05;
    spheres = min(spheres,length(p+vec3(-0.22, 0.25, r))-0.05);
    spheres = min(spheres,length(p+vec3(-0.08, 0.3, r))-0.05) / 1.1;
    float mustache = smin(line, spheres, 0.15);
    fd = min(fd, mustache);

    body = bd/length(resize);
    g1 += 0.001/(0.001+body*body);
    face = fd/length(resize);

    return min(body,face);
}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.01,0.);
    return normalize(SDF(p)-vec3(SDF(p-eps.xyy),SDF(p-eps.yxy),SDF(p-eps.yyx)));
} 

float spec (vec3 rd, vec3 n, vec3 l)
{
    vec3 h = normalize(l-rd);
    return pow(max(dot(h,n),0.),15.);
}

float bubble (vec2 uv, float r, vec2 offset)
{
    return smoothstep(r,r*0.9,length(uv+offset))*length(uv+offset);
}

vec3 background(vec2 uv)
{
    vec3 color = vec3(1.,0.5,0.8);
    color += bubble(uv, 0.25+sin(time*1.2)*0.01, vec2(-0.2, -0.1));
    uv.x = abs(uv.x)-.8;
    color += bubble(uv, 0.2+sin(time)*0.01, vec2(0.5));
    color += bubble(uv, 0.3+sin(time*1.5)*0.01, vec2(0.1, 0.3));
    color += bubble(uv, 0.25+sin(time*1.2)*0.01, vec2(0.4, -0.4));
    return color;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    float dither = hash21(uv);

    vec3 ro = vec3(0.,0.,-2.2),
        rd = normalize(vec3(uv,1.)),
        p = ro,
        l = vec3(1.,2.,-2.),
        col = background(uv)*0.8;

    float shad, d = 0.;
    bool hit = false;

    for (float i=0.; i<64.; i++)
    {
        d  = SDF(p);
        if (d<0.01)
        {
            hit = true;
            shad = i/64.;
            break;
        }   
        d *= 0.9+dither*0.1;
        p += d*rd;
    }


    if (hit)
    {
        float bo = body;
        float fa = face;
        vec3 n = getnorm(p);
        float fre = pow(max(1.-dot(-rd,n),0.),1.);
        if (d == bo) 
        {
            col = mix(vec3(0.8,0.8, 0.1),vec3(0.8,0.2,0.), smoothstep(0.2,0.9,fre));
            col += spec(rd, n, l)*(fre*2.);
        }
        if (d == fa) col = mix(vec3(0.05,0., 0.),vec3(0.8,0.1,0.),fre);
    }

    col += g1*0.1*vec3(1.,0.1,0.01);
    fragColor = vec4(sqrt(col),1.);
}

// Smoothclamp variant of "Circle_Inversion" by lil_chickehh. https://shadertoy.com/view/3s2fz3
// There, I AA and golfed (sorry...) it,
// and eiffie's suggested clamped inversion U / clamp(dot(U,U),.25,1.)
// That I here turned to #def smoothclamp(v,a,b) ( a+(b-a)*smoothstep(a,b,v) )

void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy,
         U = ( u+u - R) / R.y;
    U = cos(   21.*U / ( .3+smoothstep(.25,1.,dot(U,U)) )
             + 16.*sin(iTime*.2+vec2(0,33))  );
    U += U.y;
    O += .5 + U.x/fwidth(U.x);
}

#define S(a, b, t) smoothstep(a, b, t)

float createTaperBox(vec2 point, float bottomWidth, float topWidth, float yBottom, float yTop, float blur) {
    // Bottom edge    
    float m = S(-blur, blur, point.y - yBottom);
    // Top edge
    m *= S(blur, -blur, point.y - yTop);
    // Sides, mirrored
    point.x = abs(point.x);

    float w = mix(bottomWidth, topWidth, (point.y - yBottom) / (yTop - yBottom));
    m *= S(blur, -blur, point.x - w);

    return m;
}

vec4 createTree(vec2 uv, vec3 color, float blur) {

    // --- Trunk ---
    float m = createTaperBox(uv, 0.03, 0.03, -0.05, 0.25, blur);
    // --- Canopy 1 ---
    m += createTaperBox(uv, 0.2, 0.1, 0.25, 0.5, blur);
    // --- Canopy 2 ---
    m += createTaperBox(uv, 0.15, 0.05, 0.5, 0.75, blur);
    // --- Top ---
    m += createTaperBox(uv, 0.1, 0.0, 0.75, 1.0, blur);

    float shadow = createTaperBox(uv - vec2(0.2, 0.0), 0.1, 0.5, 0.15, 0.25, blur);
    shadow += createTaperBox(uv + vec2(0.25, 0.0), 0.1, 0.5, 0.45, 0.5, blur);
    shadow += createTaperBox(uv - vec2(0.2, 0.0), 0.1, 0.5, 0.7, 0.75, blur);
    color -= shadow * 0.8;
    //m = 1.0;

    return vec4(color, m);
}

float GetHeight(float x) {
    return sin(x * 0.435) + sin(x) * 0.3;
}

vec4 createLayer(vec2 uv, float blur) {
    vec4 color = vec4(0.0);

    float id = floor(uv.x);
    float n = fract(sin(id * 256.12) * 6854.3) * 2.0 - 1.0;
    float x = n * 0.3;
    float y = GetHeight(uv.x);

    // Ground
    float ground = S(blur, -blur, uv.y + y);
    color += ground;

    y = GetHeight(id + 0.5 + x);

    uv.x = fract(uv.x) - 0.5;
    vec4 tree = createTree((uv - vec2(x, -y)) * vec2(1.0, 1.0 + n * 0.25), vec3(1.0), blur);

    color = mix(color, tree, tree.a);
    color.a = max(ground, tree.a);
    return color;
}

float Hash21(vec2 point) {
    point = fract(point * vec2(1462.34, 1026.75));
    point += dot(point, point + 864.23);

    return fract(point.x * point.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
