#define N 40.
#define R iResolution.xy
#define M iMouse.xy
#define t iTime
#define T (0.5 + 0.5 * sin(t))
#define D(p) (((p) - 0.5 * R) / R.y)
#define inf 1e32

float distToV(vec2 p, vec2 o, vec2 v) {
    vec2 op = p - o;
    vec2 ep = op - v;
    float d = length(ep - dot(ep, v) / dot(v, v) * v);
    if (dot(op, v) < 0.) {
        d = length(op);
    }    
    if (dot(ep, -v) < 0.) {
        d = length(ep);
    }
    return d;
}

vec2 QuadraticBezier(vec2 a, vec2 b, vec2 c, float q) {
    vec2 qab = a + q * (b - a);
    vec2 qbc = b + q * (c - b);    
    return qab + q * (qbc - qab);
}

void mainImage(out vec4 o, in vec2 p)
{
    p = D(p);
    vec2 m = D(M);    
    vec2 a = vec2(-0.6, -0.3);
    vec2 b = vec2(m.x, m.y);
    vec2 c = vec2(0.6, -0.3);
    float s = inf;

    for (float i = 1.; i <= N; i++) {
        vec2 qb0 = QuadraticBezier(a, b, c, (i - 1.) / N);
        vec2 qb1 = QuadraticBezier(a, b, c, i / N);
        float d = distToV(p, qb0, qb1 - qb0);
        s = min(s, d);
    }

    vec2 tab = a + T * (b - a);
    vec2 tbc = b + T * (c - b);
    vec2 tr = tab + T * (tbc - tab);
    float dab = distToV(p, a, tab - a);
    float dbc = distToV(p, b, tbc - b);
    float dr = distToV(p, tab, tbc - tab);

    o = vec4(0.002 / s);
    o += vec4(0.003 / dab, 0.003 / dbc, 0.003 / dr, 0.);
    o += vec4(0.004 / length(p - tr));
}

const int MARCHES = 100;
const float EPS = 0.00001;
const vec2 EPS_VEC = vec2(EPS,0.);
const vec3 LIGHT_DIR = vec3(-0.1,0.5,-0.5);


float dot2(vec2 a, vec2 b) {
	return (a.x * b.x + a.y * b.y);
}

float dot3(vec3 a, vec3 b) {
	return (a.x * b.x) + (a.y * b.y) + (a.z * b.z);
}

vec3 sphereCenter(float time) {
    return vec3(0.,cos(0.5*time),5.) + vec3(sin(time),0.,2.*cos(0.5*time));
}

float harmonics(float time) {
	return floor(4.*mod(time,5.));
}

float time2(float p) {
    return 0.8 * sin(p);
}

float time() {
    return 0.5 * iTime + time2(iTime + time2(iTime+ time2(iTime)));
}



float displacement(vec3 pos, float t) {
    vec3 SPHERE_CENTER = sphereCenter(t);
    vec3 relPos = pos - SPHERE_CENTER;
    float theta = atan(relPos.y, relPos.x);
    float phi = atan(dot2(relPos.xy, relPos.xy), relPos.z);

    float harmonics_1 = harmonics(floor(t));
    float harmonics_2 = harmonics(floor(t)+1.);

    float displacement1 = sin(harmonics_1*theta) * sin(harmonics_1*phi);
    float displacement2 = sin(harmonics_2*theta) * sin(harmonics_2*phi);


    float alpha = fract(t);
    return mix(displacement1, displacement2, alpha);// smoothstep(displacement1, displacement2, fract(iTime));
}



float _dist(vec3 pos, float t) {
    vec3 SPHERE_CENTER = sphereCenter(t);
    vec3 relPos = pos - SPHERE_CENTER;
    float disp1 = displacement(pos, t);
	return length(relPos) - 0.5 + 0.2* (disp1);
}

float dist(vec3 pos) {
	//pos = mod(pos, 50.);
    float t = time();
    return _dist(pos, t);
}




vec3 normal(vec3 pos) {

    float dx = dist(pos + EPS_VEC.xyy) - dist(pos - EPS_VEC.xyy);
    float dy = dist(pos + EPS_VEC.yxy) - dist(pos - EPS_VEC.yxy);
    float dz = dist(pos + EPS_VEC.yyx) - dist(pos - EPS_VEC.yyx);
    return normalize(vec3(dx,dy,dz));
}


vec3 shade(vec3 pos, vec3 dir, float t) {
    //return vec3(length(normal(pos)));
    //return length(normal(pos));
	//return vec3(dot3(normal(pos), LIGHT_DIR));
    vec3 normal_vec = normal(pos);
    float shade = dot3(normal_vec, LIGHT_DIR);
    vec3 dir_to_sphere_center = normalize(pos - sphereCenter(t));
    float mat_blend = dot3(normal_vec, dir_to_sphere_center);
    return shade * vec3(sin(mat_blend)*cos(mat_blend), sin(mat_blend), cos(mat_blend));
    //return vec3(shade, shade, 1.-fract(iTime));
}

vec3 march(vec3 pos, vec3 dir) {
    float t = time();
    float d = 0.;
    float step_frac = .9;
    int max_marches = MARCHES;
    for(int i = 0; i < MARCHES; i++) {
        d = dist(pos);
        if (d < EPS) {
            return shade(pos,dir,t);
        }
        step_frac = max(0.2,min(1.,d));
    	pos.xyz += dir * d * step_frac;
    }
    return vec3(0.);
}

