void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // uv - center and fix aspect ratio
    vec2 uv = (fragCoord - iResolution.xy * 0.5)/iResolution.y;

    // uv - rotate 45 deg
	float a = 0.7853982; // 45 in radians
    float sa = sin(a);
    float ca = cos(a);
    uv *= mat2(ca, -sa, sa, ca);

    float time = iTime * 2.0;
    vec3 col = vec3(0);

    float divisions = 11.0;
    vec2 gv = fract(uv * divisions) - 0.5;
    vec2 id = floor(uv * divisions);
    gv.x *= mod(id.x, 2.0) == 0.0 ? 1.0 : -1.0;
    gv.y *= mod(id.y, 2.0) == 0.0 ? 1.0 : -1.0;

    float d = length(gv + vec2(sin(time) * 2.0, cos(time) * 2.0) * 0.25);
    float radius = 0.5 * (0.1 + sin(time * 0.25 + length(uv) * 3.12) * 0.5 + 0.5);
    float circle = smoothstep(radius + 0.02, radius - 0.02, d);


    col.rg = gv;

    col += d;
    col = pow(col, vec3(4.0));

    col = max(col, vec3(circle));


    fragColor = vec4(col,1.0);
}

vec2 fc(vec2 c, vec2 z) {
	return vec2(c.x + z.x * z.x - z.y * z.y, c.y + 2. * z.x * z.y);
}

vec2 mandelbrot(vec2 c) {
    int i=0;

    vec2 z = vec2(0., 0.);
    vec2 oz = vec2(0., 0.);

    while( (length(z) < 1.5) ) {

        oz = z;
        z = fc(c, z);
        i +=1;

        if (i > 100) {

            return vec2(0., 0.);
        }
    }

    return vec2(1.,1.) * float(i) / 100.;
}

