// R2 low-discrepancy sequence as described by Martin Roberts
// Ref: http://extremelearning.com.au/unreasonable-effectiveness-of-quasirandom-sequences
const float a1 = 0.61803398874989484820633884919255;
const float a2 = 0.56984029099805326591218186327522;
const float a3 = 0.54970047790197026694714643076746;
float R_2_1D(float n) {
	return (0.5 + a1 * n);
}
vec2 R_2_2D(float n) {
	return vec2(0.5 + a1 * n, 0.5 + a2 * n);
}
vec3 R_2_3D(float n) {
	return vec3(0.5 + a1 * n, 0.5 + a2 * n, 0.5 + a3 * n);
}

