void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float radius = sin(iTime)/2.;
    vec2 zero = vec2(sin(iTime)*0.3+0.5, cos(iTime)*0.3+0.5);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = length(uv-zero)+cos(iTime+vec3(0.,2.,.4));

    col.r += .5*atan(sin(iTime*0.002)*(uv.x+zero.x), length(uv-zero));
    col.b += atan(uv.y-zero.y, length(uv-zero));
    col.g -= .5*atan(cos(iTime*0.2)*(zero.y-uv.x), length(uv-zero))-col.b;

	col += length(uv-zero);

    col.r = (length(uv-zero)*col.b+sin(iTime*.2)*col.g);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define res iResolution.xy
#define tau 6.283
#define eps .01

float sdf(vec2 p) {
    float dist = length(p) - .5;
    dist = min(dist, p.y + 1.);
    dist = min(dist, 1. - p.y);
    dist = min(dist, p.x + res.x/res.y);
    dist = min(dist, res.x/res.y - p.x);
    return dist;
}

void mainImage(out vec4 col, in vec2 coord) {
    vec2 pos = (coord * 2. - res) / res.y;
    for(int i = 0; i < 32; i++) {
        float dist = sdf(pos)*2.;
        if(abs(dist) < .01) break;
        pos += dist * vec2(sin((vec2((i+1)*(i+1))*iTime*.001 + vec2(0,.25)) * tau));
    }
    col = vec4(normalize(pos) * .5 + .5, 0, 1);
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float rand(vec2 p) {
    return fract(sin(dot(p, vec2(3213.213123, 7879.789321)))*321312.890);
}

float noise(vec2 p) {
    vec2 i = floor(p);
    vec2 ii = i+1.0;
    vec2 f = fract(p);

    f = smoothstep(0.0, 1.0, f);

    return mix(mix(rand(vec2(i.x+0.0, i.y+0.0)), rand(vec2(i.x+1.0, i.y+0.0)), f.x),
               mix(rand(vec2(i.x+0.0, i.y+1.0)), rand(vec2(i.x+1.0, i.y+1.0)), f.x),
               f.y);
}

float map(vec3 p) {
    float d = 1e10;
    vec3 q;

    q = p;
    q.y -= -5.0;
    d = min(d, q.y + (noise(q.xz*3.0)*0.05)+noise(q.xz*0.5+vec2(13213.2))*0.5 );

    q = p;
    q.y -= 25.0;
    d = min(d, -q.y + (noise(q.xz*3.0)*0.05)+noise(q.xz*0.5+vec2(13213.2))*0.5 );

    q = p;
    q.y -= -2.5;
    q.xz = mod(q.xz, 6.0) - 3.0;
    d = min(d,
            sdRoundBox(q, vec3(0.2, 100.0, 0.2), 0.2) +
            noise( (p.yz+p.xy) )*0.1 +
            noise( (p.yz+p.xy)*20.0 )*0.01);

    return d;
}

vec3 getNormal(vec3 p) {
    vec2 eps = vec2(0.001, 0.0);
    return normalize(vec3(
        map(p+eps.xyy) - map(p-eps.xyy),
        map(p+eps.yxy) - map(p-eps.yxy),
        map(p+eps.yyx) - map(p-eps.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*fragCoord - iResolution.xy)/iResolution.yy;
	vec3 cPos = vec3(0.0, 0.0, -5.0);

    cPos = vec3(sin(iTime*0.5), 0.3+cos(iTime*0.4)*0.2, cos(iTime*0.5));
    cPos *= 10.5;

    vec3 cTar = vec3(0.0, 0.0, 0.0);
    cTar = vec3(sin(iTime), cos(iTime*0.7)*0.5+0.5, 0.0);
    cTar *= 10.5;


    vec3 cDir = normalize(cTar - cPos);
    vec3 wU = vec3(0.0, 1.0, 0.0);
    vec3 cR = cross(cDir, wU);
    vec3 cU = cross(cR, cDir);

    vec3 rd = normalize(p.x*cR + p.y*cU + cDir);
    vec3 ro = cPos;

    float t = 0.;
    vec3 rp;
    for(int i=0; i<99; i++) {
        rp = ro + rd*t;
        float d = map(rp);

        t += d;
    }

    float mt = iTime;
    float ph = fract(mt);

    vec3 col = vec3(0.0);
    vec3 lDir = normalize(vec3(0.5, 0.5, -0.5));
    float diff = max(0.0, dot(lDir, getNormal(rp)));

    col += (diff+0.1)*cDir*exp(-3.0*t*t)*100.0;

    float decay = exp(-0.1*t);
    col += decay*0.01;
    col += exp(-100.0*abs(fract(rp*5.0+0.1)-0.5))*decay;
    col += max(0.0, exp(-500.0*abs( fract(t*0.5)-ph )))*decay*vec3(0.3, 1.0, 0.4)*2.5;
    col = clamp(col, 0.0, 1.5);

    col = pow(col, vec3(0.45));
    col -= ( 1.0-exp(-0.2*length(p)) )*0.2;
    fragColor = vec4(col,1.0);
}

