vec3 nscene(vec3 p) //, float h, out float d)
{
    float h = .002;
    vec3 n = vec3( 
          dscene(p + vec3(h,0,0))
        , dscene(p + vec3(0,h,0))
        , dscene(p + vec3(0,0,h))
      ) - dscene(p);
    if (dot(n,n) < 1e-7) n = vec3(0,1,0);
    else n = normalize(n);
    return n;
