// ray scan method

// I'm sure compiler will figure it out and
// rewrite to same thing internally either way,
// but I've always had a soft spot for the
// identity x + x = 2*x, since historically
// x += x has been a cheaper operation than
// x *= 2 or even x <<= 1 in some cases.
// Nowadays with mads, probably not, in this case.
// it'll evaluate as (2.*s-r)/r.y, betcha.
vec2 StoQ(vec2 s, vec2 r)
{
	return (s + s - r) / r.y;
}

mat3 CameraMatrix(vec3 camFwd)
{
    vec3 w = normalize(camFwd)
       , u = normalize(cross(vec3(0, 1, 0), w))
       , v = normalize(cross(w, u));
    return mat3(u, v, w);
}
// then transform by pw = MC * pv;

vec3 sph2cart(vec3 a) // spherical radians theta, psi, radius -> cartesian
{
    float cx = cos(a.x), sx = sin(a.x)
        , cy = cos(a.y), sy = sin(a.y);
    return vec3(sx*cy, sy, cx*cy) * a.z;
}

vec3 OrbitCamera(vec2 angles)
{ // angles is yaw, pitch in radians
    angles.x *= 2.;
    angles.y = clamp(angles.y, -.4, .3);
    return sph2cart(vec3(angles * pi, 1.));
} // returns forward vector camera aims along

float Dither(vec2 p)
{
    return sin(dot(p, vec2(1./23., 1./23./17.)) * 23456.);
}

void mainImage(out vec4 c, vec2 p)
{
    rseed = fract(fract(iTime*.1)+dot(vec2(.09,.07),p));
    c = vec4(0,0,0,1);
    vec2 R = iResolution.xy
    , M = iMouse.xy
    , q = StoQ(   p, R)
    , m = StoQ(M.xy, R);
    if (length(M.xy) <= 1.)
        m = vec2(.2 + .02*iTime,.25); // unattended, mouse in bottom left corner?
    vec3 object_pos = vec3(0, radius, 0)
        , camera_dir = OrbitCamera(m)
        , camera_pos = object_pos - orbit_scale * radius * camera_dir;
    camera_pos.y = max(camera_pos.y, 1e-2); // do not go beneath ground
    camera_dir = normalize(object_pos - camera_pos); // redo LookAt
    mat3 cam = CameraMatrix(camera_dir);
    const float hfovy = pi/6.;
    vec3 vd = normalize(vec3(q, 1./sin(hfovy)))
    , ro = camera_pos
    , rd = normalize(cam * vd); // view ray
    c.rgb = RenderScene(ro, rd, iTime);
    c.rgb = pow(c.rgb, vec3(1./2.2)); // to sRGB gamut (approximately)
    c.rgb += .5/256. * Dither(p);
}

