// CONFIGURATION
#define M_PI 3.1415926535897932384626433832795
#define MaxVelocity 320.0
// function sampling range
vec2 xRange = vec2(-M_PI, M_PI);
// drawn range of the Y axis
vec2 yRange = vec2(-MaxVelocity, MaxVelocity) * 2.0;

// colours
const vec3 background = vec3(1.0, 1.0, 1.0);
const vec3 axes = vec3(0.0, 0.0, 0.0);

// function plot line thickness in pixels
const float thickness = 5.0;

// function to sample
#define Acceleration 10.0
#define DeltaTime 0.01

vec3 calcPrevVelocity()
{
    return vec3(MaxVelocity * 3.0 * (sin((M_PI / -2.0) + iTime * 0.25) + 1.0) * 3.0, 0, 0);
}

float f(float angle)
{
    vec3 prevVelocity = calcPrevVelocity();
    vec3 accelDir = vec3(cos(angle), sin(angle), 0);
    float projVel = dot(prevVelocity, accelDir);
    float accelVel = Acceleration * DeltaTime;

    if(projVel + accelVel > MaxVelocity) 
        accelVel = MaxVelocity - projVel;

    if(accelVel < 0.0)
        return 0.0;
    else
  	 	return projVel + accelVel;
}

float o()
{
    vec3 prevVelocity = calcPrevVelocity();
   	float prevLength = length(prevVelocity);
    float accelVel = Acceleration * DeltaTime;
    float projMax = MaxVelocity - accelVel;

    float theta;
    if (prevLength <= projMax)
        theta = acos(dot(vec3(1,0,0), prevVelocity) / prevLength);
    else
        theta = acos(projMax / prevLength);

    return theta;
}


// IMPLEMENTATION

// @return	Given relative real Y value converted to pixels.
float realYToPixels(float real)
{
	return real / (yRange.y - yRange.x) * iResolution.y;
}

// @return	Given relative real X value converted to pixels.
float realXToPixels(float real)
{
	return real / (xRange.y - xRange.x) * iResolution.x;
}

// @return	Alpha for a pixel which is dist away for a given max allowed radius.
float coverage(float dist, float radius)
{
	radius *= 0.5;
	return 1.0 - smoothstep(floor(radius), radius, dist);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 actualYRange = yRange;

	float x = xRange.x + (xRange.y - xRange.x) * (fragCoord.x / iResolution.x);
	float y = f(x);
    float optimalAngle = o();

    float actualY = actualYRange.x + (actualYRange.y - actualYRange.x) * (fragCoord.y / iResolution.y);
    vec4 plot;
    if(abs(x) - optimalAngle < 0.01 && optimalAngle - abs(x) < 0.01)
    {
        plot = vec4(1.0, 0.0, 0.0, 1);
    }
    else
    {
        // plot dot :)
        float dotX = xRange.x + fract(iTime) * (xRange.y - xRange.x);

        vec3 funcColour = vec3(0.0, 0.0, 1.0);
        // plot colour
        float distY = abs(y - actualY);
        plot = vec4(funcColour, coverage(realYToPixels(distY), thickness));
    }

    // axis colour
    vec4 xAxis = vec4(axes, coverage(realXToPixels(abs(x)), thickness));
    vec4 yAxis = vec4(axes, coverage(realYToPixels(abs(actualY)), thickness));
    vec4 axis = xAxis * xAxis.a + yAxis * yAxis.a;

	vec4 foreground = mix(axis, plot, plot.a);
	fragColor = mix(vec4(1.0), foreground, foreground.a);
}

#define ROTATION
#define LIGHT_SOURCE_ATTACHED_TO_CAMERA
#define LIGHTING

#define M_PI 3.1415926

// setting begin
const float FOV   = M_PI / 2.;
const float zNear = 1.;
const float zFar  = 100.;

const float colissiondist = 0.00001;
const int maxIter = 1000;
// setting end

vec3 viewpoint = vec3(0., 0., 6.);

mat3 rotatey(in float a) {
    float sina = sin(a);
    float cosa = cos(a);
    return mat3(cosa,  0., sina,
                0.,    1.,   0.,
                -sina, 0., cosa);
}

float f(in vec3 p) {
    return distance(p, vec3(0., 0., 0.)) - 2. + sin(p.x * 10.) * sin(iTime * 2.);
}

void dist(in vec3 p, out float mind, out vec3 color, out vec3 norm) {
    mind = f(p);
    color = vec3(1., 0., 0.);
    color = vec3(sin(p.x * 3.)  + sin(p.y * 5.)  * cos(p.z * 100.), 
                 sin(p.x * 30.) * sin(p.y * 1.)  + sin(p.z * 2.),
                 sin(p.x * 9.)  * cos(p.y * 25.) + sin(p.z * 700.));
    const float E = 0.000001;
    norm = normalize(vec3(f(p + vec3(E, 0., 0.)), f(p + vec3(0., E, 0.)), f(p + vec3(0., 0., E))));
}

vec3 ray_marching(vec3 orig, vec3 dir) {
    dir = normalize(dir);
    float len = 0.;
    vec3 bg = vec3(min(0.937255, max(0.2, sin(dir.x * 5.) + sin(dir.y * 10.) + cos(dir.x * dir.y * 10.))));

    for (int i = 0; i < maxIter; ++i) {
        float d;
        vec3 color;
        vec3 norm;
        dist(orig, d, color, norm);
        #ifdef LIGHTING
            #ifdef LIGHT_SOURCE_ATTACHED_TO_CAMERA
                if (d < colissiondist) return color * 
                    max(0.4, length(dot(norm, normalize(viewpoint - dir))));
            #else
                if (d < colissiondist) return color * 
                    max(0.4, length(dot(norm, normalize(vec3(0., 3., 4.) - dir))));
            #endif
        #else
            if (d < colissiondist) return color;
        #endif
        if (len > zFar) return bg;

        len += d;
        orig += dir * d;
    }
    return bg;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / vec2(min(iResolution.x, iResolution.y));
    uv -= (iResolution.xy / min(iResolution.x, iResolution.y) - vec2(1.)) / 2.;
    uv.y = 1. - uv.y;

    #ifdef ROTATION
        float a = iTime * 0.1;
    #else
        float a = 0.;
    #endif
    viewpoint.x = sin(a) * 7.;
    viewpoint.z = cos(a) * 7.;
    vec3 color = ray_marching(viewpoint, rotatey(-a + M_PI) * vec3(uv - vec2(0.5, 0.5) , zNear));

    fragColor = vec4(color, 1.);
}

#define BOX_CNT 7
#define EPS	0.0001
#define MAX_STEPS 100
#define MAX_DIST 200.0
#define NUMBER1 0
#define NUMBER2 1
#define PLANE	2

struct Intersect {
    int		idx;
    float	dist;};

struct Point	{
	vec3	pos;
    vec3	col;};

vec3 diffCol[] = vec3[](vec3(0.0, 1.0, 0.7),
						vec3(1.0, 1.0, 0.0),
						vec3(0.8, 0.0, 0.8));

float diff[] =  float[](0.4,  0.4,  0.5);
float spec[] =  float[](0.8,  0.9,  0.2);
float specPow[]=float[](64.0, 32.0, 4.0);

vec3 rotate(vec3 p, float angleX, float angleY, float angleZ)
{
    float cosA = cos(angleY);
	float sinA = sin(angleY);
	vec3 r = vec3(p.x, sinA * p.z + cosA * p.y, cosA * p.z - sinA * p.y);
    cosA = cos(angleX);
	sinA = sin(angleX);
	r = (-vec3(cosA * r.x - sinA * r.z, r.y, sinA * r.x + cosA * r.z));
    cosA = cos(angleZ);
	sinA = sin(angleZ);
	return (vec3(cosA * r.x - sinA * r.y, sinA * r.x + cosA * r.y, r.z));
}

//https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
vec2 repeat(in vec2 p, in float s, in vec2 lim)
{
    return p - s * clamp(round(p / s), -lim, lim);
}


float gyroidDist(vec3 p, float scale, float thickness, float bias)
{
	p *= scale;
    return abs(dot(sin(p), cos(p.zxy)) + bias) / (scale) - thickness;
}


float planeDist(in vec3 pos, in vec4 normal)
{
    return (dot(pos, normal.xyz) + normal.w);
}


float box(vec3 p)
{
	float b = 1.0;
	float rounding = 0.6;
	return length(max(abs(p) - b, 0.0)) - rounding;
}

float blob(float dist[BOX_CNT], int i1, int i2)
{
    float k = 2.2;
    float res = 0.0;
    for (int i = i1; i <= i2; i++)
        res += exp(-k * dist[i]);
	return -log(res)/k;
}


Intersect sceneDist(vec3 pos)
{   
    Intersect res;
	float SINt = sin(iTime * 0.5);

    float dist[BOX_CNT];
    vec3 p1 = pos + vec3( 6.0, -6.0, 0.0);
    vec3 p2 = pos + vec3( 3.0,  6.0, 0.0);
    vec3 p3 = pos + vec3( 0.0, -3.0, 0.0);
    vec3 p4 = pos + vec3( 9.0,  3.0, 0.0);
    vec3 p5 = pos + vec3( 4.35, 0.0, 0.0);

    vec3 p6 = pos + vec3(-7.0,  1.5, 0.0);
    vec3 p7 = pos + vec3(-4.0, -6.0, 0.0);

    p1.xy = repeat(p1.xy, 2.0, vec2(1.5, 0.0));
	p2.xy = repeat(p2.xy, 2.0, vec2(1.5, 0.0));
    p5.xy = repeat(p5.xy, 2.0, vec2(0.9, 0.0));
    p6.xy = repeat(p6.xy, 2.0, vec2(0.0, 2.2));

    //2
    dist[0] = box(rotate(p1, 0.0, 8.0 * clamp(SINt - 0.0, 0.0, 0.2), 0.0));
    dist[1] = box(rotate(p2, 0.0, 8.0 * clamp(SINt - 0.2, 0.0, 0.2), 0.0));
    dist[2] = box(rotate(p3, 0.0, 8.0 * clamp(SINt - 0.05, 0.0, 0.2), 0.0));
    dist[3] = box(rotate(p4, 0.0, 8.0 * clamp(SINt - 0.15, 0.0, 0.2), 0.0));
    dist[4] = box(rotate(p5, 0.0, 8.0 * clamp(SINt - 0.1, 0.0, 0.2), 0.0));
    //1
    dist[5] = box(rotate(p6, 8.0 * clamp(SINt - 0.3, 0.0, 0.2), 0.0, 0.0));
    dist[6] = box(rotate(p7, 0.0, 0.0, 8.0 * clamp(SINt - 0.25, 0.0, 0.2)));

    float distPlane = planeDist(pos, vec4(0.0, 1.0, 0.0, 100.0));

    float distBox = min(blob(dist, 0, 4), blob(dist, 4, 6));

    res.dist = min(distPlane, distBox);
    if (res.dist == distPlane)
        res.idx = PLANE;
    else if (SINt <= 0.4) 
    	res.idx = NUMBER1;
    else
        res.idx = NUMBER2;
    return (res);
}

vec3 getNormal(vec3 p)
{
    return normalize(vec3(
    sceneDist(vec3(p.x + EPS, p.y, p.z)).dist - sceneDist(vec3(p.x - EPS, p.y, p.z)).dist,
    sceneDist(vec3(p.x, p.y + EPS, p.z)).dist - sceneDist(vec3(p.x, p.y - EPS, p.z)).dist,
    sceneDist(vec3(p.x, p.y, p.z  + EPS)).dist - sceneDist(vec3(p.x, p.y, p.z - EPS)).dist));
}


Intersect rayMarch(vec3 ro, vec3 rd)
{
    Intersect d;
    Intersect intersect;
    for (int i = 0; i < MAX_STEPS; ++i)
    {
        d = sceneDist(ro + intersect.dist * rd);
        intersect.dist += d.dist;
        if (intersect.dist < EPS || intersect.dist >= MAX_DIST)
            break;
    }
    intersect.idx = d.idx;
    return (intersect);
}

float getShadow(in vec3 ro, in vec3 rd, in float min_t, in float max_t, in float k)
{
    float res = 1.0;
    for (float t = min_t; t < max_t;)
    {
        float dist = sceneDist(ro + rd * t).dist;
        if (dist < EPS)
            return 0.0;
        res = min(res, k * dist / t);
        t += dist;
    }
    return (res);
}

vec3 getLight(int idx, vec3 pos, vec3 eye, vec3 lightPos)
{
    vec3 diffuseCol = diffCol[idx];
    vec3 specularCol = vec3(1.0);
    vec3 Norm = getNormal(pos);
    vec3 Ldir = normalize(lightPos - pos);
    vec3 View = normalize(eye - pos);
    vec3 Refl = normalize(reflect(-Ldir, Norm));

    float dotLN = dot(Ldir, Norm);
    float dotRV = dot(Refl, View);

    // light not visible
    if (dotLN < 0.0)
        return vec3(0.0);

    // light reflection in opposite direction, apply only diffuse lighting
    if (dotRV < 0.0)
        return 1.7 * (diffuseCol * dotLN) * diff[idx];

    vec3 half_direction = normalize(normalize(Ldir) + View);
    float specular = pow(max(dot(half_direction, Norm), 0.0), specPow[idx]);
    return 1.7 * diffuseCol * dotLN * diff[idx] + specularCol * specular * spec[idx];
}

Point getColor(vec3 ro, vec3 rd, vec3 eye, vec3 lightPos, vec2 uv)
{
    Point point;
    Intersect intersect = rayMarch(ro, rd);
    point.pos = ro + intersect.dist * rd;
    if (intersect.dist > MAX_DIST - EPS)
    {
        float bg = gyroidDist(rotate(vec3(uv.x, uv.y, iTime*0.3), 0.0, cos(iTime*0.01), cos(iTime*0.01)), 28.0, 0.001, 3.0);
        point.col += abs(bg) * 6.0;
        point.col *= vec3(rd.y + 0.4, 0.05, rd.y + 0.7); //background
    }
    else
    {    
		//ambient
        vec3 ambientCol = vec3(0.5, 0.5, 0.5);    
    	vec3 ambientLight = 0.3 * vec3(1.0);
    	point.col = ambientLight * ambientCol;

    	//shadow
    	vec3 shadowDir = normalize(lightPos - point.pos);
    	float shadow = getShadow(point.pos + shadowDir, shadowDir, 0.0, MAX_DIST, 8.0);

    	//diffuse + specular
    	vec3 blinn_phong = getLight(intersect.idx, point.pos, eye, lightPos) * shadow;
    	point.col += blinn_phong;
    }
    return point;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
