    vec2 uv = aspectRatio * (fragCoord.xy / iResolution.xy - 0.5);
    vec2 mouse = 6.4 * (iMouse.xy / iResolution.xy);
	float SINt = 0.5 + 0.5 * sin(iTime * 0.6);
	vec3 ro = rotate(vec3(0.0, 0.0, 30.0), SINt - 0.4 , 0.0, 0.0);
    vec3 rd = -rotate(vec3(uv, 1.0), SINt - 0.4, 0.0, 0.0);  
	vec3 lightPos = rotate(vec3(5.0, -10.0, 80.0), 0.0, 0.0, 0.0);	  

    Point point = getColor(ro, rd, ro, lightPos, uv);
	vec3 color = point.col;   

    vec3 rd1 = rd - (2.0 * dot(rd, getNormal(point.pos)) * getNormal(point.pos));
    Point point2 = getColor(point.pos + rd1, rd1, ro, lightPos, uv);
    color = mix(color, point2.col, 0.2);

    fragColor = vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord)/iResolution.xy;
    // Time varying pixel color
    vec2 col = (uv*50.0);
    for(float i = 1.0; i <5.0; i++){
        uv += col;
        col = cos(uv.yx*(i+sin((uv.y-uv.x)/10.0+iTime)));
    }
    // Output to screen
    fragColor = vec4(col,1.0,1.0);
}

