#define FAR 100.0

#define antialiasing 16

#define rot(A) mat2(cos(A),-sin(A),sin(A),cos(A))
#define sqr(a) (a*a)
#define dot2(a) dot(a,a)

// Commutative smooth maximum function. Provided by Tomkh, and taken 
// from Alex Evans's (aka Statix) talk: 
// http://media.lolrus.mediamolecule.com/AlexEvans_SIGGRAPH-2015.pdf
// Credited to Dave Smith @media molecule.
float smax(float a, float b, float k){

   float f = max(0., 1. - abs(b - a)/k);
   return max(a, b) + k*.25*f*f;
}

// Commutative smooth minimum function. Provided by Tomkh, and taken 
// from Alex Evans's (aka Statix) talk: 
// http://media.lolrus.mediamolecule.com/AlexEvans_SIGGRAPH-2015.pdf
// Credited to Dave Smith @media molecule.
float smin(float a, float b, float k){

   float f = max(0., 1. - abs(b - a)/k);
   return min(a, b) - k*.25*f*f;
}

float plane(vec3 ro, vec3 rd, vec3 pln, float d) {

    float a = dot(ro,pln)-d;
    float b = dot(rd,pln);

    if (a*b < 0.0 && b < 0.0) {
        return -a/b;
    }

    return FAR;
}

vec2 path(float time) {
    time *= 0.1;
    //random sine waves that move the camera
    vec2 a = vec2(sin(time),cos(time*3.0));
    a += vec2(sin(time*0.1)*5.0,cos(time*1.0)*3.0);
    a *= sin(time*0.05);
    a *= 4.0;

    return a;
}

float isoMap(vec3 p, vec3 ro){

    p += 0.001;

    float l;

    vec2 path1 = p.xy;
    vec2 path2 = path(p.z)-p.xy;

    float tunnel = 7.0-smin(length(path1),length(path2), 3.0);

    l = tunnel;

    vec3 p2 = p*0.2;

    p2.x += iTime*0.1;
    p.z += 2.0;

	p2 = cos(p2*0.315*1.25 + sin(p2.zxy)); // 3D sinusoidal mutation.

    float n = dot(p2,vec3(1)); // Spherize. The result is some mutated, spherical blob-like shapes.

    l = smax(l,n-1.5, 3.0);

    return l;

}

// Interpolating along the edge connecting vertices v1 and v2 with respect to the isovalue.
vec3 inter(in vec3 p1, in vec3 p2, float v1, float v2){
    // The interpolated point will fall somewhere between the vertex points p1 and p2.
    // Obviously if the isovalue is closer to p1, then the interpolated point will be
    // closer to p1, and vice versa.
    return mix(p2, p1, v2/(v2 - v1));
}

float ray(vec3 ro, vec3 rd, inout vec3 normal, float maxdist)
{

    vec3 fro = floor(ro);
    vec3 lro = ro-fro;
    vec3 srd = sign(rd);
    vec3 ird = srd/rd;
    float d = 0.0;
    vec3 dists = abs((srd*0.5+0.5)-lro)*ird;
    vec3 n = vec3(0);

    mat4x3 tetraf = mat4x3(
        vec3( 1, 1, 1),
        vec3(-1, 1,-1),
        vec3(-1,-1, 1),
        vec3( 1,-1,-1));

    vec3 p = lro-0.5;
    vec3 flip = 1.0-mod(fro,2.0)*2.0;

    vec4 tetra1 = vec4(
        dot(rd,tetraf[0]),
        dot(rd,tetraf[1]),
        dot(rd,tetraf[2]),
        dot(rd,tetraf[3]));

    vec4 tetra3 = 1.0/max(abs(tetra1),0.001);

    vec4 tetra2 = vec4(
        dot(ro,tetraf[0]),
        dot(ro,tetraf[1]),
        dot(ro,tetraf[2]),
        dot(ro,tetraf[3]));

    vec4 tetra = abs((sign(tetra1)+1.0)-mod(tetra2,2.0))*tetra3;

    vec4 tetrac = step(0.5,vec4(
        dot(p,tetraf[0]*flip),
        dot(p,tetraf[1]*flip),
        dot(p,tetraf[2]*flip),
        dot(p,tetraf[3]*flip)));

    vec4 a = (1.0-2.0*tetrac);

    mat4x3 sp = 0.5-0.5*mat4x3(
        tetraf[0]*flip*a.x,
        tetraf[1]*flip*a.y,
        tetraf[2]*flip*a.z,
        tetraf[3]*flip*a.w);

    vec4 ps = vec4(
        isoMap(fro+sp[0], ro),
        isoMap(fro+sp[1], ro),
        isoMap(fro+sp[2], ro),
        isoMap(fro+sp[3], ro));

    int i2 = 0;

    for (int i = 0; i < 400; i++)
    {

        int index = int(dot(step(ps,vec4(0.0)),vec4(1,2,4,8)));
            /*(ps[0] < 0.0 ? 1 : 0)
             +(ps[1] < 0.0 ? 2 : 0)
             +(ps[2] < 0.0 ? 4 : 0)
             +(ps[3] < 0.0 ? 8 : 0);*/

        vec3 mask;
        if (dists.x < dists.y)
        {
            if (dists.x < dists.z)
            {
                mask = vec3(1,0,0);
            }
            else
            {
                mask = vec3(0,0,1);
            }
        }
        else
        {
            if (dists.y < dists.z)
            {
                mask = vec3(0,1,0);
            }
            else
            {
                mask = vec3(0,0,1);
            }
        }


        vec4 maskt;
        if (tetra.x < tetra.y)
        {
            if (tetra.x < tetra.z)
            {
                if (tetra.x < tetra.w)
                {
                    maskt = vec4(1,0,0,0);
                }
                else
                {
                    maskt = vec4(0,0,0,1);
                }
            }
            else
            {
                if (tetra.z < tetra.w)
                {
                    maskt = vec4(0,0,1,0);
                }
                else
                {
                    maskt = vec4(0,0,0,1);
                }
            }
        }
        else
        {
            if (tetra.y < tetra.z)
            {
                if (tetra.y < tetra.w)
                {
                    maskt = vec4(0,1,0,0);
                }
                else
                {
                    maskt = vec4(0,0,0,1);
                }
            }
            else
            {
                if (tetra.z < tetra.w)
                {
                    maskt = vec4(0,0,1,0);
                }
                else
                {
                    maskt = vec4(0,0,0,1);
                }
            }
        }

        float lv = dot(dists,mask);
        float lt = dot(maskt,tetra);

        float l = min(lv,lt);

        if (index != 0)
        {
            if (index != 15)
            {
                ivec3 v1, v2;
                float f = -1.0;
                if (index > 7) {
                    f = -f;
                    index = 15-index;
                }

                if(index == 1)
                {
                    v1 = ivec3(0,0,0);
                    v2 = ivec3(1,2,3);
                }    
                // Vertex 1 only is inside or outside.
                else if(index == 2)
                {
                    v1 = ivec3(1,1,1);
                    v2 = ivec3(0,3,2);
                }
                // Vertex 2 only is inside or outside.
                else if(index == 4)
                {
                    v1 = ivec3(2,2,2);
                    v2 = ivec3(0,1,3);
                } 
                // Vertex 3 only is inside or outside.
                else if(index == 7)
                {
                    v1 = ivec3(3,3,3);
                    v2 = ivec3(0,1,2);
                }
                // Vertices 0 and 1 are inside or vertices 2 and 3 are inside.
                else if(index == 3)
                {
                    v1 = ivec3(0,0,1);
                    v2 = ivec3(2,3,3);
                }
                // Vertices 0 and 2 are inside or vertices 1 and 3 are inside.   
                else if(index == 5)
                {
                    v1 = ivec3(0,2,0);
                    v2 = ivec3(1,3,3);
                } 
                // Vertices 1 and 2 are inside or vertices 0 and 3 are inside.
                else if(index == 6)
                {
                    v1 = ivec3(0,1,2);
                    v2 = ivec3(1,3,3);
                }

        		mat3 v = mat3(
                    inter(sp[v1.x], sp[v2.x], ps[v1.x], ps[v2.x]),
                    inter(sp[v1.y], sp[v2.y], ps[v1.y], ps[v2.y]),
                    inter(sp[v1.z], sp[v2.z], ps[v1.z], ps[v2.z]));

                vec3 n = cross(v[1]-v[0],v[2]-v[0])*f*(+dot(tetrac,vec4(1))*2.0-1.0)*flip.x*flip.y*flip.z;

                float pln = plane(lro-v[0],rd,n,0.0);

                if (pln < l)
                {
                    normal = n;
                    d += pln;
                    return d;
                }
            }
        }

        d += l;
        lro += rd*l;

        dists -= l;
        tetra -= l;

        int j;

        if (lv < lt)
        {
            normal = -mask*sign(rd);

            dists = dists+mask*ird;
            lro -= srd*mask;
            fro += srd*mask;

            vec3 p = lro-0.5;

            flip *= 1.0-mask*2.0;

            for (int j = 0; j < 4; j++)
            {
            	sp[j] = abs(mask-sp[j]);
            }

            j = int(abs(dot(mask,vec3(-7,-5,2))+dot(tetrac,vec4(0,1,2,3))))%4;
            // mask.x  mask.y  mask.z
            //  w 3     y 1     z 2   tetrac.x
            //  z 2     x 0     w 3   tetrac.y
            //  y 1     w 3     x 0   tetrac.z
            //  x 0     z 2     y 1   tetrac.w
        }
        else
        {
            vec4 mask = maskt;

            if (flip.x > 0.0) {
                mask = mask.wzyx;
            }

            if (flip.y > 0.0) {
                mask = mask.yxwz;
            }

            if (flip.z > 0.0) {
                mask = mask.zwxy;
            }

            tetra += tetra3*maskt*2.0;
            tetrac = abs(tetrac-mask);


        	j = int(dot(mask,vec4(0,1,2,3)));
            // maskt.x maskt.y maskt.z maskt.w
            //   0       1       2       3

            sp[j] = 1.0-sp[j];
        }
        ps[j] = isoMap(fro+sp[j], ro);

        if (d > maxdist) return maxdist;
    }

    return d;


    /*vec4 dists = vec4(
        plane(ro,rd,vec3( 1, 1, 1),1.0),
        plane(ro,rd,vec3( 1,-1,-1),1.0),
        plane(ro,rd,vec3(-1, 1,-1),1.0),
        plane(ro,rd,vec3(-1,-1, 1),1.0));

    return vec4(min(min(min(dists.x,dists.y),dists.z),dists.w),0,0,0);*/
}

