//hash function by Dave_Hoskins https://www.shadertoy.com/view/4djSRW
#define HASHSCALE3 vec3(.131, .130, .1973)
vec3 hash31(float p)
{
   vec3 p3 = fract(vec3(p) * HASHSCALE3);
   p3 += dot(p3, p3.yzx+19.19);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}

//shading
vec3 shade(vec3 p, vec3 d, vec3 n, float material, float num, float dist) {
    vec3 color = vec3(1);

    //vec3 f = floor(p*0.01+sin(p.yzx)+sin(p.zxy));
    //color = sin(f+sin(p))*(sin(p)*0.25+0.5)+0.5;

    vec3 light = vec3(0,sin(iTime)*4.0,iTime*4.0+8.0);

    vec3 ldir = light-p;
    float llen = length(ldir);
    ldir /= llen;

    float shadow = dot(ldir,n)*0.8;
    shadow /= llen*llen*0.02+1.0;

    if (shadow > 0.0) {
        vec3 nothing;
        float l = ray(p+ldir*0.001,ldir, nothing, llen);
        if (l < llen) shadow *= 0.0;
    }

    shadow = max(shadow,0.02);

    color *= shadow;
    float fog = max(4.0-dist/FAR*4.0,0.0);
    color *= fog;

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    vec3 ro = vec3(path(iTime*4.0),iTime*4.0);

    vec3 rd = normalize(vec3(uv,1)+0.001);

    if (length(iMouse.xy) < 50.0) {
        vec3 lookat = vec3(path(iTime*4.0+2.0),iTime*4.0+2.0);
        vec3 forward = normalize(lookat-ro);
        vec3 left = normalize(cross(vec3(path(iTime*4.0+1.0),0),forward));
        vec3 up = cross(forward,left);

        rd = rd.x*left+rd.y*up+rd.z*forward;

    } else {
        rd.zy *= rot(iMouse.y/iResolution.y*3.14-3.14*0.5);
        rd.zx *= rot(iMouse.x/iResolution.x*3.14*2.0-3.14);
    }

    int id = 3;
    float tetranum;
    vec3 cell;
    vec3 nor;
    float d = ray(ro,rd, nor, FAR);
    nor = normalize(nor);

    // Output to screen
    vec3 sun = normalize(vec3(1));
    vec3 col = vec3(0);
    if (d < FAR)
    {
        vec3 p = ro+rd*d;

        col = shade(p, rd, nor, cell.x+cell.y*0.348+cell.z*0.483, tetranum, d);
    } else {
        float sunl = max(dot(sun,rd),0.0);
        sunl = pow(sunl,100.0);

        col = vec3(0);//mix(vec3(0.2,0.2,0.8),vec3(1.2,1.0,0.2),sunl);
    }


    col = clamp(col,0.0,1.0);
    col = pow(col,vec3(1.0/2.2));
    fragColor = vec4(sqrt(col),1);
}

