vec2 map(vec3 p)
{
	vec2 result = vec2(10.);

    float time = iTime * 1.0;
    //float sc = fract(time);

    vec3 q = p;
	q.y -= 0.3;
    //float index = floor((q.y + 0.3) * 5.0);
    float index2 = mod(floor(time), 5.0);
    //float x = (sin(iTime * 2.0 * M_PI));
    float x = fract(time);
    float y = smoothstep(0.0, 0.25, fract(time));
    //q.x += (index == index2) ? x * 0.5 : 0.0;
    //q.x += index * 0.4;

    for(float i = 0.; i < 5.; i++){
    	vec3 q = p;
		q.y -= 0.3;
        bool isIndex = (index2 == i);
        q.x += isIndex ? x * 15. : 0.;
        q.x += (index2 <= i) ? 0. : 15.;
        q.y += isIndex ? 0.2*(index2) : (y+index2)*0.2;
        //q.x += x;
        //q.x += i * 0.2;
        float b = sdRoundBox(q + vec3(0.,0.2 - i * 0.2,0.), vec3(1.0, 0.2, 1.0), 0.01);
    	vec2 boze = opI(sdBoze(q, 2.5, 1.0), vec2(b, MAT_CS));
        //vec2 boze = opU(sdBoze(q, 2.5, 1.0), vec2(b, MAT_CS));
        result = opU(result, boze);
    }
    // hammer
    vec3 q2 = p;
    float htime = time * M_PI;
    q2 = rotate(q2, M_PI * 0.5, vec3(0,0,1));
    //float c = sdCylinder(p, vec3(0.1,0.1,0.1));
    q2.x += 0.1;
    q2.yz += vec2(-0.5, 1.5);
    q2.yz += vec2(abs(sin(htime)), abs(cos(htime))) * -1.5;
    q2 = rotate(q2, M_HALFPI - (cos(htime*2.) * 0.5 + 0.5) * M_HALFPI, vec3(1,0,0));
    //q2 = rotate(q2, M_PI - (sin(htime*2.) * 0.5 + 0.5)*M_HALFPI, vec3(1,0,0));
    float c = sdCappedCylinder(q2, 0.1, 0.2);
	result = opU(result, vec2(c, MAT_CS));

    // next boze
    vec3 q3 = p;
	float y2 = index2 == 4. ? y : 0.;
    q3.y += -2. + y2 * 1.7;
    result = opU(result, sdBoze(q3, 2.5, 1.0));

    // background
    vec2 bg1 = vec2(sdPlane(p + vec3(0., 0., 0.), vec4(0,1,0,0)), MAT_BG);
    //vec2 bg2 = vec2(sdPlane(p - vec3(0., 1.0, 0.), vec4(0,-1,0,0)), MAT_BG);

    result = opU(bg1, result);
    //result = opSU(bg2, result,  0.25);

    //result.x *= s;

    return result;
}

vec3 norm(vec3 p)
{
    vec2 e=vec2(.001,.0);
    return normalize(.000001+map(p).x-vec3(map(p-e.xyy).x,map(p-e.yxy).x,map(p-e.yyx).x));
}

float shadow(in vec3 p, in vec3 l)
{
    float t = 0.15;
    float t_max = 20.0;

    float res = 1.0;
    for (int i = 0; i < 16; ++i)
    {
        if (t > t_max) break;

        float d = map(p + t*l).x;
        if (d < 0.01)
        {
            return 0.0;
        }
        t += d;
        res = min(res, 1.0 * d / t);
    }

    return res;
}


float ndfGGX(float NdotH, float roughness)
{
	float alpha   = roughness * roughness;
	float alphaSq = alpha * alpha;

	float denom = (NdotH * NdotH) * (alphaSq - 1.0) + 1.0;
	return alphaSq / (M_PI * denom * denom);
}

float gaSchlickG1(float theta, float k)
{
	return theta / (theta * (1.0 - k) + k);
}

float gaSchlickGGX(float NdotL, float NdotV, float roughness)
{
	float r = roughness + 1.0;
	float k = (r * r) / 8.0;
	return gaSchlickG1(NdotL, k) * gaSchlickG1(NdotV, k);
}

vec3 fresnelSchlick_roughness(vec3 F0, float cosTheta, float roughness) {
    return F0 + (max(vec3(1.0 - roughness), F0) - F0) * pow(1.0 - cosTheta, 5.0);
}

vec3 shade(vec3 pos, vec3 albedo, float metalness, float roughness, vec3 N, vec3 V, vec3 L, vec3 Lradiance)
{
	vec3 H = normalize(L + V);
	float NdotV = max(0.0, dot(N, V));
	float NdotL = max(0.0, dot(N, L));
	float NdotH = max(0.0, dot(N, H));

	vec3 F0 = mix(vec3(0.04), albedo, metalness);

	vec3 F  = fresnelSchlick_roughness(F0, max(0.0, dot(H, L)), roughness);
	float D = ndfGGX(NdotH, roughness);
	float G = gaSchlickGGX(NdotL, NdotV, roughness);

	vec3 kd = mix(vec3(1.0) - F, vec3(0.0), metalness);

    float shadow = shadow(pos, L);
	vec3 diffuseBRDF = kd * albedo / M_PI;
	vec3 specularBRDF = (F * D * G) / max(0.0001, 4.0 * NdotL * NdotV);

	return (diffuseBRDF + specularBRDF) * Lradiance * NdotL * shadow;
}

// Unreal Engine Ambient BRDF Approx
// https://www.unrealengine.com/en-US/blog/physically-based-shading-on-mobile?lang=en-US
vec3 EnvBRDFApprox( vec3 SpecularColor, float Roughness, float NoV )
{
	const vec4 c0 = vec4( -1, -0.0275, -0.572, 0.022 );
	const vec4 c1 = vec4( 1, 0.0425, 1.04, -0.04 );
	vec4 r = Roughness * c0 + c1;
	float a004 = min( r.x * r.x, exp2( -9.28 * NoV ) ) * r.x + r.y;
	vec2 AB = vec2( -1.04, 1.04 ) * a004 + r.zw;
	return SpecularColor * AB.x + AB.y;
}

// Specular Occlusion
// https://media.contentapi.ea.com/content/dam/eacom/frostbite/files/course-notes-moving-frostbite-to-pbr-v2.pdf
float so(float NoV, float ao, float roughness) {
    return clamp(pow(NoV + ao, exp2(-16.0 * roughness - 1.0)) - 1.0 + ao, 0.0, 1.0);
}

vec3 calcAmbient(vec3 pos, vec3 albedo, float metalness, float roughness, vec3 N, vec3 V, float t)
{
	vec3 F0 = mix(vec3(0.04), albedo, metalness);
    vec3 F  = fresnelSchlick_roughness(F0, max(0.0, dot(N, V)), roughness);
    vec3 kd = mix(vec3(1.0) - F, vec3(0.0), metalness);

	float aoRange = t/20.0;
	float occlusion = max( 0.0, 1.0 - map( pos + N*aoRange ).x/aoRange );
	occlusion = min(exp2( -.8 * pow(occlusion, 2.0) ), 1.0);

    vec3 ambientColor = vec3(0.5);

    vec3 diffuseAmbient = kd * albedo * ambientColor * min(1.0, 0.75+0.5*N.y) * 3.0;
    vec3 R = reflect(-V, N);

    vec3 col = mix(vec3(1.0) * pow( 1.0-max(-R.y,0.0), 4.0), ambientColor, pow(roughness, 0.5));
    vec3 ref = EnvBRDFApprox(F0, roughness, max(dot(N, V), 0.0));
    vec3 specularAmbient = col * ref;

    diffuseAmbient *= occlusion;
    specularAmbient *= so(max(0.0, dot(N, V)), occlusion, roughness);

    return vec3(diffuseAmbient + specularAmbient);
}

