#define MAX_MARCH 100

vec3 materialize(vec3 p, vec3 ray, float depth, vec2 mat)
{
    vec3 col = vec3(0.12, 0., 0.4);
    //vec3 col = min(vec3(0.12, 0., 0.4) + sinebow(fract(iTime * 1.5)) * vec3(0.1), 1.);
    vec3 nor = norm(p);
    vec3 sky = vec3(0.0,0.0,0.0);
    //vec3 sky = min(sinebow(fract(iTime * 2.5 + 0.5)) * vec3(0.35) + vec3(0.3) + pow(max(1.29 - p.y, 0.), 5.), 1.);
    //if(p.y >= 0.29) 
    if(depth > 100.)
    {
    	col = sky;
    } else {
        if(mat.y > 0.0){
            vec2 index = floor(p.xz + 0.5);

            float roughness, metalness;
            if(mat.y == MAT_BLACK) {
                col = vec3(0.0, 0.0, 0.0);
                roughness = 0.0;
                metalness = 0.0;
            } else if(mat.y == MAT_FACE) {
                col = vec3(1.0, 0.8, 0.6);
                roughness = 0.2;
                metalness = 0.3;
            } else if(mat.y == MAT_BROW) {
                col = vec3(1.0, 0, 0);
                roughness = 0.9;
                metalness = 0.3;
            } else if(mat.y == MAT_CHEEP) {
                col = vec3(1.0, 0.3, 0.5);
                roughness = 0.1;
                metalness = 0.6;
        	} else if(mat.y == MAT_BG) {
                //col = min(sinebow(fract((index.x + index.y) * 0.175 + iTime * 0.5 + 0.5)) * vec3(0.3), 1.);
                col = mod(index.x + index.y, 2.0) == 0.0 ? vec3(1.) : vec3(0.);
                roughness = 0.1;
                metalness = 0.9;
            } else if(mat.y == MAT_CS) {
                col = min(sinebow(iTime * 3.5), 1.);
                //col = mod(index.x + index.y, 2.0) == 0.0 ? vec3(1.) : vec3(0.);
                roughness = 0.2;
                metalness = 0.3;
            }


            vec3 result = vec3(0.);
            result += shade(p, col, metalness, roughness, nor, -ray, normalize(vec3(1.)), vec3(1.0, 0.98, 0.95) * 10.)*1.0;
            result += calcAmbient(p, col, metalness, roughness, nor, -ray, depth);
            col = result;
        }


        //float fog = min(1.0, (1.0 / float(MAX_MARCH)) * float(mat.x))*5.0;
    	vec3  fog2 = vec3(0.01) * pow(depth, 2.);
    	//col += vec3(0.01, 0.01, 0.05) * fog;
    	//col += fog2 + sky * 0.1;
        col -= fog2;
        col = max(col, 0.);
    }

    return col;
}

vec3 trace(vec3 p, vec3 ray)
{
    float t = 0.0;
    vec3 pos;
    vec2 mat;

    //float planeDistance = GetIntersectLength(p, ray, vec3(0.,0.3,0.), vec3(0.,1.,0.));
    //t = planeDistance;

    int count = 0;
    for (int i = 0; i < MAX_MARCH; i++) {
        pos = p + ray * t;
        mat = map(pos);
        if (mat.x <= 0.000001) {
        	break;
        }
        t += mat.x;
        count++;
    }

    p = p + t * ray;
    mat.x = float(count);
    return materialize(p, ray, t, mat);
}

mat3 camera(vec3 ro, vec3 ta, float cr )
{
	vec3 cw = normalize(ta - ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

float luminance(vec3 col)
{
    return dot(vec3(0.298912, 0.586611, 0.114478), col);
}

vec3 reinhard(vec3 col, float exposure, float white) {
    col *= exposure;
    white *= exposure;
    float lum = luminance(col);
    return (col * (lum / (white * white) + 1.0) / (lum + 1.0));
}

