float sdCappedCylinder(vec3 p, vec3 a, vec3 b, float r)
{
  vec3  ba = b - a;
  vec3  pa = p - a;
  float baba = dot(ba,ba);
  float paba = dot(pa,ba);
  float x = length(pa*baba-ba*paba) - r*baba;
  float y = abs(paba-baba*0.5)-baba*0.5;
  float x2 = x*x;
  float y2 = y*y*baba;
  float d = (max(x,y)<0.0)?-min(x2,y2):(((x>0.0)?x2:0.0)+((y>0.0)?y2:0.0));
  return sign(d)*sqrt(abs(d))/baba;
}
float sdTriPrism( vec3 p, vec2 h )
{
  vec3 q = abs(p);
  return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}
float sdEllipsoid( vec3 p, vec3 r )
{
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}

float vexel(vec3 p,vec3 b, float r) {

        vec3 q = abs(p) -b;
        return length(max(q,0.0) + min(max(q.x,max(q.y,q.z)),0.0)) - r;
}
#define ITER 64.
#define PI 3.141592

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
float sdVerticalCapsule( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}
mat2 r(float a){
    float c=cos(a),s=sin(a);
    return mat2(c,s,-s,c);
}
float sdPlane( vec3 p, vec4 n )
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}
float stripes(vec3 p) {

    float s = sin(atan(p.x,p.y)*21.) > 0.85 && p.z <-0.15 && sin(8.*iTime+p.z*50.) >-0.5 ? -sdCappedCylinder(p,vec3(0.,0.,1.),vec3(0.,0.,-1.),.89) : 10000.;
    return s;

}
float radiologo(vec3 p) {

    float side = 100000.;
    for(float i=0.;i<3.; i++){

        vec3 pos = p;
        pos.xy*=r(i*(2.*PI/3.));
        pos+=vec3(0,.5,-0.);



        side = min(side,sdTriPrism(pos,vec2(.51,.1) ));
    }


    side = max(-sdCappedCylinder(p,vec3(0,0,-.20),vec3(0,0,.20),.15),side);

    float border = max( sdCappedCylinder(p,vec3(0,0,-.20),vec3(0,0,.20),.89), -sdCappedCylinder(p,vec3(0,0,-.25),vec3(0,0,.25),.75)) ;
    side = max(side,-border);
    float center = sdCappedCylinder(p,vec3(0,0,-.10),vec3(0,0,.10),.1);
    return min(min(side,center),stripes(p));
}


float SDF(vec3 p) {

     return radiologo(p+vec3(0.,0.,-4.0));
}
