float Rect(vec2 uv, float width, float height, float blur, float centreHeight)	//Scren height = .5
{
    uv.y -= height*centreHeight;
    float col = smoothstep(width+blur,width,abs(uv.x));
    col *= smoothstep(height+blur,height,abs(uv.y));

	return col;
}

vec4 glowingRect(vec2 uv, vec4 col, float scale)
{
    return vec4(col*(Rect(uv,.05*scale,.05*scale,0.05*scale,0.)+(col*(0.08*scale)/length(uv*.6))));
}

float Hash21(float pos)
{
	return fract(sin(pos*123.32)*351.102+153.15);
}

vec3 tiledWindows(vec2 pos, vec3 baseCol, float blur, vec2 scale, float a)
{
    float glowScale, glowAmount, windowsMask;
    vec4 wndGlow;
    vec2 tiledPos = pos;
    for (float i = 0.; i < 2.; i++)
    {
        for (float j = 0.; j < 2.; j++)
        {
            tiledPos = pos+vec2((j*.25)+.125,(i*.25)+.125);
            tiledPos *= vec2(2.,2.);
            tiledPos = vec2(fract(tiledPos.x)-.5,fract(tiledPos.y)-.5);
            tiledPos /= vec2(2.,2.);
            glowScale = (2.-(scale.x*.1))*.5;
            windowsMask = (i==1.) ? a : floor(a);
            wndGlow = glowingRect(tiledPos,vec4(vec3(1.,.8,.5),windowsMask),glowScale);
            glowAmount = wndGlow.a*(abs(1.-(blur*2.)));
            baseCol = max(baseCol,mix(baseCol,wndGlow.rgb,glowAmount));
        }
    }
    return baseCol;
}

vec4 cityLayer(vec2 uv, vec2 scale, vec3 col, float blur)
{
    blur += 0.0001;
    uv *= scale;
    float a = 0.;
    float id = floor(uv.x);
    uv.x = fract(uv.x);
    vec2 pos = vec2(uv.x-.5,uv.y+.5);
    float height = ((Hash21(id)+.8)*.3+1.)*.5*.31*scale.x;
    a += Rect(pos,.5,height,blur,1.);	//Max height = 3.1

    uv = ((uv/scale)-.5)*2.+1.;
    //col += (uv.y*.6)*.2;
    col -= max(((1.-uv.y*.5))*.05,0.);

    pos.y -= (height*2.);// - (Hash21(id+126.321)*.5);
    pos.x += .5;

    float savedPosX = pos.x;

    for (float j = 0.; j < WINDOWS_COLL; j++)
    {
        pos.y += .2;
        pos.x = savedPosX;
        for (float i = 0.; i < WINDOWS_ROW; i++)
        {
            pos.x -= .2;
            float glowScale = (2.-(scale.x*.1))*.5;
            vec4 wndGlow = glowingRect(pos,vec4(vec3(1.,.8,.5),1.),glowScale);
            float glowAmount = wndGlow.a*(abs(1.-(blur*2.)));
            col = mix(col,wndGlow.rgb,glowAmount);
        }
    }

    //col = tiledWindows(pos, col, blur, scale, a);

    return vec4(col,a);
}

