vec3 getNormal(vec3 p){
	vec2 t = vec2(0.001,0.);
	return normalize(map(p).x - vec3(
    	map(p-t.xyy).x,
    	map(p-t.yxy).x,
    	map(p-t.yyx).x
    ));
}

