mat2 rot(in float rads) {
    return mat2(sin(rads), cos(rads), -cos(rads), sin(rads));
}

float trapezoid(in float t) {
    /*
            t    f(t)
            0    0
            1    1
            2    1
            3    0
    */

    if (t < 1.0) {
        return t;
    }
    if (t < 2.0) {
        return 1.0;
    }
    if (t < 3.0) {
        return 1.0 - (t - 2.0);
    }
    return 0.0;
}

