#define SHOW_GRADIENT 0

vec4 permute(const in vec4 x) 
{ 
    return mod(((x * 34.0) + 1.0) * x * 1.0, 289.0); 
}
vec2 smootherStep(const in vec2 x) 
{ 
    vec2 x2 = x * x;
    return x2 * x * (x * (x * 6.0 - 15.0) + 10.0); 
}

float noise(const in vec2 pos, vec2 scale)
{
    // classic Perlin noise based on Stefan Gustavson
    vec2 p = pos * scale;
    vec4 i = mod(floor(p.xyxy) + vec4(0.0, 0.0, 1.0, 1.0), scale.xyxy);
    vec4 f = fract(p.xyxy) - vec4(0.0, 0.0, 1.0, 1.0);
    i = mod(i, 289.0); // avoid truncation effects in permutation

    vec4 ix = i.xzxz;
    vec4 iy = i.yyww;
    vec4 fx = f.xzxz;
    vec4 fy = f.yyww;

    vec4 ixy = permute(permute(ix) + iy);
    vec4 gx = 2.0 * fract(ixy * 0.0243902439) - 1.0; // 1/41 = 0.024...
    vec4 gy = abs(gx) - 0.5;
    vec4 tx = floor(gx + 0.5);
    gx = gx - tx;

    vec2 g00 = vec2(gx.x,gy.x);
    vec2 g10 = vec2(gx.y,gy.y);
    vec2 g01 = vec2(gx.z,gy.z);
    vec2 g11 = vec2(gx.w,gy.w);

    vec4 norm = 1.79284291400159 - 0.85373472095314 * 
    vec4(dot(g00, g00), dot(g01, g01), dot(g10, g10), dot(g11, g11));
    g00 *= norm.x;
    g01 *= norm.y;
    g10 *= norm.z;
    g11 *= norm.w;
    float n00 = dot(g00, vec2(fx.x, fy.x));
    float n10 = dot(g10, vec2(fx.y, fy.y));
    float n01 = dot(g01, vec2(fx.z, fy.z));
    float n11 = dot(g11, vec2(fx.w, fy.w));

    vec2 fade_xy = smootherStep(f.xy);
    vec2 n_x = mix(vec2(n00, n01), vec2(n10, n11), fade_xy.x);
    float n_xy = mix(n_x.x, n_x.y, fade_xy.y);
    return 2.3 * n_xy * 0.5 + 0.5;
}

float powFactor;
float gradientFactor;
float perlinNoise(const in vec2 pos, vec2 scale)
{
    // classic Perlin noise based on Stefan Gustavson
    vec2 p = pos * scale;
    vec4 i = mod(floor(p.xyxy) + vec4(0.0, 0.0, 1.0, 1.0), scale.xyxy);
    vec4 f = fract(p.xyxy) - vec4(0.0, 0.0, 1.0, 1.0);
    i = mod(i, 289.0); // avoid truncation effects in permutation

    vec4 ix = i.xzxz;
    vec4 iy = i.yyww;
    vec4 fx = f.xzxz;
    vec4 fy = f.yyww;

    vec4 ixy = permute(permute(ix) + iy);
    vec4 gx = 2.0 * fract(ixy * 0.0243902439) - 1.0; // 1/41 = 0.024...
    vec4 gy = abs(gx) - 0.5;
    vec4 tx = floor(gx + 0.5);
    gx = gx - tx;

    // rotate gradients
    gx = cos(gx * gradientFactor);
    gy = sin(gy * gradientFactor * 2.0);

    vec2 g00 = vec2(gx.x,gy.x);
    vec2 g10 = vec2(gx.y,gy.y);
    vec2 g01 = vec2(gx.z,gy.z);
    vec2 g11 = vec2(gx.w,gy.w);

    vec4 norm = 1.79284291400159 - 0.85373472095314 * 
    vec4(dot(g00, g00), dot(g01, g01), dot(g10, g10), dot(g11, g11));
    g00 *= norm.x;
    g01 *= norm.y;
    g10 *= norm.z;
    g11 *= norm.w;
    float n00 = dot(g00, vec2(fx.x, fy.x));
    float n10 = dot(g10, vec2(fx.y, fy.y));
    float n01 = dot(g01, vec2(fx.z, fy.z));
    float n11 = dot(g11, vec2(fx.w, fy.w));

    vec2 fade_xy = smootherStep(f.xy);
    vec2 n_x = mix(vec2(n00, n01), vec2(n10, n11), fade_xy.x);
    float n_xy = mix(n_x.x, n_x.y, fade_xy.y);
    return max(pow(2.3 * n_xy * 0.5 + 0.5, powFactor), 0.001);
}

vec2 perlinGradient(vec2 uv, vec2 scale, float spread)
{
    vec3 offset = vec3(1.0 / iResolution.xy, 0.0) * spread;
    vec2 grad = vec2(0.0);
    grad.x -= perlinNoise(uv - offset.xy, scale) * 1.0;
    grad.x -= perlinNoise(uv - offset.xz, scale) * 2.0;
    grad.x -= perlinNoise(uv + offset.xy * vec2(-1.0, 1.0), scale) * 1.0;
    grad.x += perlinNoise(uv + offset.xy * vec2(1.0, -1.0), scale) * 1.0;
    grad.x += perlinNoise(uv + offset.xz, scale) * 2.0;
    grad.x += perlinNoise(uv + offset.xy, scale) * 1.0;
    grad.y -= perlinNoise(uv - offset.xy, scale) * 1.0;
    grad.y -= perlinNoise(uv - offset.zy, scale) * 2.0;
    grad.y -= perlinNoise(uv + offset.xy * vec2(1.0, -1.0), scale) * 1.0;
    grad.y += perlinNoise(uv + offset.xy * vec2(-1.0, 1.0), scale) * 1.0;
    grad.y += perlinNoise(uv + offset.zy, scale) * 2.0;
    grad.y += perlinNoise(uv + offset.xy, scale) * 1.0;

    return grad;  
}	

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.y;

    vec2 cells = vec2(20.0);
    float spread = 1.0;
    float radius = 0.25;
    float mask = length(uv - iMouse.xy / iResolution.y) - radius;
    mask = step(mask, 0.0) * abs(mask) / radius;

    float strength = 0.35 * noise(uv + iTime * 0.175 + mask, vec2(4.0)) - mask * 0.125;
    float signStrength = sin(iTime * 0.5);
    float phase = uv.x * sin(iTime * 0.25) + uv.y * cos(iTime * 0.5) + iTime * 1.15 + noise(uv + iTime, vec2(2.0, 3.0));
	powFactor = 3.0 + (1.0 - mask) * 8.0 * abs(sin(phase));  
    gradientFactor = iTime * 2.0 + 1.0;

    vec2 grad = perlinGradient(uv, cells, spread);
	float n = perlinNoise(uv + grad * strength * signStrength, cells);
    fragColor = vec4(vec3(n), 1.0);

#if SHOW_GRADIENT == 1    
    fragColor = vec4(grad * 0.5 + 0.5, 0.0 ,1.0);
#endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 u = 2.*(2.*fragCoord-iResolution.xy)/iResolution.y;
    float pi = 3.14159265359;
    float p = 1.+sin(iTime)*.05;
    u *= p;
    vec2 v = u;
    vec2 m = vec2(.25,.6);  if(iMouse.z>0.)m=iMouse.xy/iResolution.xy;
    float e = 16.*m.x-8.;
    for(float i = 0.; i < 8.*m.y; ++i)
    {
        u = sin(atan(u.y,u.x)*e+vec2(0.,pi*.5))*pow(length(u),e*.6);
        u+= v/p;
    }
    fragColor = vec4(exp(dot(u,u)*-.1));
}

