// Takes in direction, rotates it by a radians
// result of applying a y rotation matrix to the vec3
// rotates about y-axis
vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

// b consists of width, height, and depth VECTORS (center to edge)
float BoxSDF(vec3 p, vec3 b) {
  vec3 q = abs(p) - b;
  return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

// point, radius, center
float SphereSDF(vec3 p, float r, vec3 c) {
    return distance(p, c) - r;
}

float PlaneSDF(vec3 p, vec4 n)
{
    return dot(p,n.xyz)+n.w;
}

#define BOX_SDF BoxSDF(pos + vec3(2.5, 0.0, 0.0), vec3(1.0))
#define SPHERE_SDF SphereSDF(pos, 1.5, vec3(2.5, 0.0, 0.0))
#define PLANE_SDF PlaneSDF(pos, vec4(0.0, 1.0, 0.0, 3.0))

#define BOX 0
#define SPHERE 1
#define PLANE 2

const vec3 a = vec3(0.5, 0.5, 0.5);
const vec3 b = vec3(0.5, 0.5, 0.5);
const vec3 c = vec3(2.0, 1.0, 1.0);
const vec3 d = vec3(0.5, 0.2, 0.25);

vec3 colorPaletteWorley(float t) {
    return a + b * cos(6.28 * (t * c + d));
}

const vec3 a1 = vec3(0.5, 0.5, 0.5);
const vec3 b1 = vec3(0.5, 0.5, 0.5);
const vec3 c1 = vec3(1.0, 1.0, 1.0);
const vec3 d1 = vec3(0.0, 0.33, 0.67);

vec3 colorPalettePerlin(float t) {
    return a1 + b1 * cos(6.28 * (t * c1 + d1));
}

const vec3 a2 = vec3(0.5, 0.5, 0.5);
const vec3 b2 = vec3(0.5, 0.5, 0.5);
const vec3 c2 = vec3(1.0, 1.0, 1.0);
const vec3 d2 = vec3(0.0, 0.1, 0.2);

vec3 colorPaletteFBM(float t) {
    return a2 + b2 * cos(6.28 * (t * c2 + d2));
}

vec3 ComputeMaterial(int objHit, vec3 pos, vec3 nor, vec3 light, vec3 view)
{
    switch(objHit)
    {
        case BOX:
        pos = pos * 5.;
        pos = (cos(iTime / 10.) * 10.) + pos;
        return colorPalettePerlin(perlinNoise3D(pos));
        break;
    	case SPHERE:

        // vector from center of sphere to point of intersection
        pos = normalize(pos - vec3(2.5, 0.0, 0.0));

        float phi = atan(pos.z, pos.x);
        if (phi < 0.0)
        {
            phi += 2. * 3.14159;
        }
        float theta = acos(pos.y);

        vec2 newUV = vec2(1. - phi / (2. * 3.14159), 1. - theta / 3.14159);

        newUV *= 7.;
        newUV = (cos(iTime / 10.) * 10.) + newUV;

    	return colorPaletteFBM(fbm2D(newUV.x, newUV.y));
    	break;
    	case PLANE:

        // https://gamedev.stackexchange.com/questions/172352/finding-texture-coordinates-for-plane
        vec3 e1 = normalize(cross(vec3(0.,1.,0.), vec3(1.,0.,0.)));
        vec3 e2 = normalize(cross(vec3(0.,1.,0.), e1));
        float u = dot(e1, pos);
		float v = dot(e2, pos);

    	return colorPaletteWorley(WorleyNoise2D(vec2(u, v) / 20.0));
    	break;
    	case -1:
    	// Background
    	return vec3(0.0);
    	break;
	}
	return vec3(1.0);    
}

float SceneSDF(vec3 pos, vec3 lightPos)
{
    float t = BOX_SDF;
    t = min(t,SPHERE_SDF);
    t = min(t,PLANE_SDF);
    return t;
}

vec3 ComputeNormal(vec3 pos, vec3 lightPos) {
    vec2 offset = vec2(0.0, 0.001);
    return normalize( vec3( SceneSDF(pos + offset.yxx, lightPos) - SceneSDF(pos - offset.yxx, lightPos),
                            SceneSDF(pos + offset.xyx, lightPos) - SceneSDF(pos - offset.xyx, lightPos),
                            SceneSDF(pos + offset.xxy, lightPos) - SceneSDF(pos - offset.xxy, lightPos)
                          )
                    );
}

void SceneSDF(vec3 pos, out float t, out int objHit, vec3 lightPos)
{
    t = BOX_SDF;
    objHit = BOX;

    float t2;
    if((t2 = SPHERE_SDF) < t)
    {
        t = t2;
        objHit = SPHERE;
    }
    if((t2 = PLANE_SDF) < t)
    {
        t = t2;
        objHit = PLANE;
    }
}

void RayMarch(vec3 origin, vec3 dir, out float t, out int objHit, vec3 lightPos)
{
    // starts slightly > 0
    // so that if we march from a different point in the scene that isnt the camera
    // we dont immediately intersect ourselves
    t = 0.001;
    // ray march not to a certain distance but for a certain number of iterations
    for (int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
        // minimum distance that pos is from all SDFs in the scene
        float m;
        SceneSDF(pos, m, objHit, lightPos);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }
    // hit nothing
    t = -1.0;
    objHit = -1;
}

Intersection SceneIntersection(vec3 eye, vec3 dir, vec3 lightPos)
{
    float t;
    int objHit;
    // Marches along ray to find point of intersection
    RayMarch(eye, dir, t, objHit, lightPos);

    vec3 isect = eye + t * dir;
    vec3 nor = ComputeNormal(isect, lightPos);
    vec3 lightDir = normalize(lightPos - isect);

    vec3 surfaceColor = ComputeMaterial(objHit, isect, nor, lightDir, normalize(eye - isect));

    vec3 normalAsColor = 0.5 * (nor + vec3(1.0));

    return Intersection(isect, t, objHit, surfaceColor);
}

const vec3 worldUp = vec3(0,1,0);
void RayCast(vec2 ndc, out vec3 eye, out vec3 dir, out vec3 ref)
{
    eye = vec3(0.0, 5.0, 15.0);
    ref = vec3(0.0, 0.0, 0.0);

    float len = tan(3.14159 * 0.08) * distance(eye, ref);

    vec3 H = normalize(cross(worldUp, ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * iResolution.x / iResolution.y;
    vec3 p = ref + ndc.x * H + ndc.y * V;
    dir = normalize(p - eye);
}

// mainImage: present in every single shader program on shadertoy
// outputs: the fragment color for the fragment it is operating on
// takes in: the pixel space coordinates of the fragment it is coloring
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // iResolution: width and height of screen in pixels (on bottom toolbar of image output)
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Convert to range -1 to 1 for x and y
    // Normalized Device Coordinates w.r.t. the center of the screen being (0,0)
    uv = (uv * 2.0) - vec2(1.0);

    Intersection aaIsects[4];
    vec3 eye, dir, ref;
    int idx = 0;
    for (float i = 0.0; i < 1.0; i += 0.5)
    {
        for (float j = 0.0; j < 1.0; j += 0.5)
        {
            RayCast(uv + vec2(i, j) / iResolution.xy, eye, dir, ref);
            aaIsects[idx++] = SceneIntersection(eye, dir, LIGHT_POS);
        }
    }

    vec3 avgColor = vec3(0.0);
    for (int i = 0; i < 4; ++i)
    {
        avgColor += aaIsects[i].color;
    }
    avgColor *= 0.25;

    fragColor = vec4(avgColor * step(0.0, aaIsects[0].t), 1.);
}

mat2 rotate2d(float angle)
{
    return mat2(cos(angle),-sin(angle), sin(angle), cos(angle));
}

float rect (vec2 uv, vec2 pos, float blurVal, vec2 size)
{
	float value = smoothstep(pos.x - size.x/2., (pos.x - size.x/2.) + blurVal, uv.x) 
        		- smoothstep(pos.x + size.x/2., (pos.x + size.x/2.) + blurVal,uv.x);

    return value *= smoothstep(pos.y, pos.y + blurVal, uv.y) 
        		  - smoothstep(pos.y + size.y, (pos.y+size.y) + blurVal, uv.y);
}

float circle(vec2 uv,float radius, vec2 pos)
{

    float d = distance(pos,uv);
    float value = step(radius,d);

    return value; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	float ratio = iResolution.x/iResolution.y;

   	vec2 bladePos = vec2(.495*ratio,.48);
    vec2 handlePos = vec2(.499*ratio,.34);
    vec2 shinePos = vec2(.494*ratio,.48);
    vec2 circlePos = vec2(.499*ratio,.44);
    vec2 hiltPos = vec2(.5*ratio,.48);
    vec2 bottomPos = vec2(.5*ratio,.34);

    uv.x *= ratio;

    uv-=bladePos;
    uv*= rotate2d(sin(iTime)*5.);
    uv+=bladePos;

    vec3 blade = vec3(rect(uv, bladePos, 0.02, vec2(.029, 0.7)));
    vec3 handle = vec3(rect(uv, handlePos, 0.0, vec2(.029, 0.15)));
    vec3 shine = vec3(rect(uv, shinePos, 0.02, vec2(.01, 0.7)));
    float button = circle(uv, 0.01, circlePos);
    vec3 hilt = vec3(rect(uv, hiltPos, 0.0, vec2(.05, .015)));
    vec3 bottom = vec3(rect(uv, bottomPos, 0.0, vec2(.05, .015)));

    vec3 col = mix(vec3(.0), vec3(0., 0., 1.), blade);
    col = mix(col, vec3(1., 1., 1.), shine);
    col += mix(col, vec3(.3), handle);
    col += mix(col, vec3(.0), button);
    col = mix(col, vec3(.2), hilt);
    col = mix(col, vec3(.2), bottom);

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Specialty Perlin ease-in/out function
vec2 soften(vec2 t)
{
    vec2 t3 = t * t * t;
    vec2 t4 = t3 * t;
    vec2 t5 = t4 * t;
    return 6.0f * t5 - 
           15.0f * t4 +
           10.0f * t3;
}

// Slow, high-quality 2D hash adapted from nimitz's
// WebGL2 hash collection
// (https://www.shadertoy.com/view/Xt3cDn)
vec2 hash22(uvec2 p)
{
    const uint PRIME32_2 = 2246822519U, PRIME32_3 = 3266489917U;
	const uint PRIME32_4 = 668265263U, PRIME32_5 = 374761393U;
    uint h32 = p.y + PRIME32_5 + p.x*PRIME32_3;
    h32 = PRIME32_4*((h32 << 17) | (h32 >> (32 - 17))); //Initial testing suggests this line could be omitted for extra perf
    h32 = PRIME32_2*(h32^(h32 >> 15));
    h32 = PRIME32_3*(h32^(h32 >> 13));
    h32 = h32^(h32 >> 16);
    uvec2 rz = uvec2(h32, h32*48271U);
    return vec2(rz.xy & uvec2(0x7fffffffU)) / float(0x7fffffff);
}

// Perlin noise function, taking corner + pixel positions as input
float Perlin(vec2 uv, vec2 fragCoord)
{
    // Find corner coordinates
    vec4 lwrUpr = vec4(floor(uv), ceil(uv)); 
    mat4x2 crnrs = mat4x2(lwrUpr.xw, lwrUpr.zw,
                          lwrUpr.xy, lwrUpr.zy);

    // Generate gradients at each corner
    vec2 mUV = iMouse.zw / iResolution.xy;
    vec2 scTime = abs(vec2(sin(iTime) + mUV.x, cos(iTime) + mUV.y)) + vec2(0.75);
    mat4x2 dirs = mat4x2(hash22(uvec2(floatBitsToUint(crnrs[0]))) * scTime,
                         hash22(uvec2(floatBitsToUint(crnrs[1]))) * scTime,
                         hash22(uvec2(floatBitsToUint(crnrs[2]))) * scTime,
                         hash22(uvec2(floatBitsToUint(crnrs[3]))) * scTime);

    // Shift gradients into [-1...0...1]
    dirs *= 2.0f;
    dirs -= mat4x2(vec2(1.0f), vec2(1.0f), 
                   vec2(1.0f), vec2(1.0f));

    // Normalize
    dirs[0] = normalize(dirs[0]);
    dirs[1] = normalize(dirs[1]);
    dirs[2] = normalize(dirs[2]);
    dirs[3] = normalize(dirs[3]);

    // Find per-cell pixel offset
    vec2 offs = mod(uv, 1.0f);

    // Compute gradient weights for each corner; take each offset relative
    // to corners on the square in-line
    vec4 values = vec4(dot(dirs[0], (offs - vec2(0.0f, 1.0f))),
                       dot(dirs[1], (offs - vec2(1.0f))),
                       dot(dirs[2], (offs - vec2(0.0f))),
                       dot(dirs[3], (offs - vec2(1.0f, 0.0f))));

    // Return smoothly interpolated values
    vec2 softXY = soften(offs);
    return mix(mix(values.z, 
                   values.w, softXY.x),
               mix(values.x, 
                   values.y, softXY.x),
               softXY.y);
}

#define GEOM_NOISE
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to [1.0 / [cellSize]])
    const float cellSize = 0.0825f;
    vec2 uv = vec2(fragCoord / iResolution.y);
    uv = uv / cellSize;

    // Generate fractional noise
    float f = 0.0f;
    float ampl = 1.87f; // Output noise intensity
    for (int i = 0; i < 2; i += 1)
    {
        float p = Perlin(uv/* + texture(iChannel0, uv).rg*/, fragCoord);
    	f = ampl * p * (1.0f - abs(p));
        #ifdef GEOM_NOISE
        	uv = uv * p;
        #endif
    }

    // Output to screen
    fragColor = vec4(vec3(f), 1.0);
}

