{
    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
#else
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
#endif

    float t = iTime * M_PI2 * 0.1047;
	//vec3 ro = vec3(cos(iTime)*2.0, 0.25, sin(iTime)*2.0 );
    //vec3 ro = vec3(0.0, -1.0, -2.1);
    //vec3 ro = vec3(0.0, -0.75, -2.1);
    //vec3 ro = vec3(0.0, -0.75 + sin(iTime * 1.5)*0.25, -2.1);
    vec3 ro = vec3(cos(t)*2.0, -0.75 + sin(t)*0.25, sin(t)*2.0 );
    vec3 ta = vec3(0., -0.05, 0.);

    //mat3 c = camera(ro, ta, cos(t * 0.35)*0.25);
    mat3 c = camera(ro, ta, cos(t*2.0)*0.25);
    //mat3 c = camera(ro, ta, 0.);
    vec3 ray = c * normalize(vec3(p, 3.5));
    vec3 col = trace(ro, ray);

    tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif
    fragColor = vec4(tot,1.0);
}

