#define PI acos(-1.)
#define time fract(iTime*0.3)

float easeInOutExpo(float x)
{
    return x == 0.
        ? 0.
        : x == 1.
            ? 1.
            : x < 0.5 ? pow(2., 20. * x - 10.) / 2.
                : (2. - pow(2., -20. * x + 10.)) / 2.;
}

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

float box (vec3 p, vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0.,max(q.x,max(q.y,q.z)))+length(max(q,0.));
}

float sc (vec3 p, float d)
{
    p = abs(p);
    p = max(p.xyz,p.yzx);
    return min(p.x,min(p.y,p.z))-d;
}

float cage (vec3 p)
{
    float per = 15.;
    p.y -= easeInOutExpo(time*PI/3.)*per;
    p.y = mod(p.y-per*0.5,per)-per*0.5;
    vec3 pp = p;
    float size = 1.8;
    float cube = max(-sc(p,size*0.8),box(p,vec3(size)));

    p.z = abs(p.z)-size*0.9;
    p.x = abs(abs(p.x)-0.65)-0.3;
    float b1 = box(p,vec3(0.12,size,0.1));

    p = pp;
    p.xz *= rot(PI/2.);
    p.z = abs(p.z)-size*0.9;
    p.x = abs(abs(p.x)-0.65)-0.3;
    float b2 = box(p,vec3(0.12,size,0.1));

    return min(min(b2,b1),cube);
}

float gem (vec3 p)
{
    float d = dot(p,normalize(sign(p)))-(.2+sqrt(sin(time*PI)*0.5));
    return d;
}

float SDF (vec3 p)
{
    vec3 pp = p;    
    pp.yz *= rot(-atan(1./sqrt(2.)));
    pp .xz *= rot(PI/4.);
    p = mix(p,pp,easeInOutExpo(clamp(sin(time*PI)*3.-1.,0.,1.)));
    return min(gem(p),cage(p));
}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.001,0.);
    return normalize(SDF(p)-vec3(SDF(p-eps.xyy),SDF(p-eps.yxy),SDF(p-eps.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    vec3 ro = vec3(uv*3.5,-30.),
        rd = vec3(0.,0.,1.),
        p = ro,
        l = normalize(vec3(1.,2.,-4.)),
        col = vec3(0.4,0.55,0.5);

    float d=0.;
    bool hit = false;
    for (float i=0.; i<100.;i++)
    {
        d = SDF(p);
        if (d < 0.001)
        {
            hit = true;
            break;
        }
        p += d*rd*0.8;
    }

    if (hit)
    {
        vec3 n = getnorm(p);
        col = vec3(max(dot(n,l),0.));
    }

    fragColor = vec4(col,1.0);
}

#define PI (3.14)
#define ITERATIONS (5)

float cubeSdf(vec2 p, vec2 halves) {
    vec2 edgeDist = abs(p) - halves;
    return length(max(edgeDist, 0.)) 
        + min(max(edgeDist.x, edgeDist.y), 0.);
}

vec2 fold(vec2 uv, float theta) {
	vec2 normal = vec2(sin(-theta), cos(-theta));
    // proj(uv, n) = dot(uv, n) / dot(n, n)
    // n has norm 1
    float d = dot(uv, normal);
    return uv - (2. * max(d, 0.) * normal);
}

vec2 vicsekSnowflake(vec2 uv, float time) {
    // Period 0 -- fold angle from (PI / 4) -> (PI / 6)
    // angle denominator increment ranges 0->2 from 0->1
    // Period 1 -- Translate 1 from 0.5 to 1.5
    time = mod(time, 4.);
    float denominatorIncrement = 
        // Transition from 0 to 1
        ((1. - step(1., time)) * fract(time) * 2.)
        // After 1 stay at 2, but stay back down after 3
        + ((step(1., time) * 2.) * (1. - step(3., time)))
        // After 2 decrease from 2 back to 0
        - ((step(2., time) * fract(time) * 2.) * (1. - step(3., time)));


    float translateIncrement = 
        // Transition from 1 to 2
        ((1. - step(2., time)) * step(1., time) * fract(time))
    	// After 2 stay at 1
        + step(2., time)
    	// After 3 decrease to 0
    	- (step(3., time) * fract(time));


    for (int i = 0; i < ITERATIONS; i++) {
        uv *= 3.;
        uv = abs(uv);
    	uv = fold(uv, PI / (4. + denominatorIncrement));
    	uv.x -= 0.5 + translateIncrement;
    	uv.x = abs(uv.x);
    	uv.x -= 0.5;
    }
    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    uv = vicsekSnowflake(uv, iTime* 0.8);

    float s = cubeSdf(uv, vec2(0.5));
    float pixelSize = (1. * pow(3., float(ITERATIONS))) / iResolution.x;
    float c = smoothstep(-pixelSize, pixelSize, s);

    vec3 color = vec3(uv.x, uv.y, 0.);
    color = vec3(0.105,0.154,0.300);
    color = mix(color, vec3(0.81,0.93,1.), 1.-c);

    fragColor = vec4(color,1.0);
}

const float FOVY = 3.14159 * 0.25;
const int RAY_STEPS = 256;
const float PI = 3.14159;
const float TWO_PI = 3.14159 * 2.0;
const vec3 matteWhite = vec3(0.85, 0.81, 0.78);
const vec3 matteRed = vec3(0.63, 0.065, 0.05);
const vec3 matteGreen = vec3(0.14, 0.45, 0.091);
const vec3 lightPos = vec3(-4.0, 10.5, -10);
const float SHADOW_HARDNESS = 6.0;


//Intersection
struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object; // unique ID for every object
};    

// Box with side lengths b
float cube(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}

// SquarePlane SDF
float plane(vec3 p, vec4 n)
{
    n = normalize(n);
    return dot(p, n.xyz) + n.w;
}

// Sphere with radius r at center c
float sphere(vec3 p, float r, vec3 c)
{
    return distance(p, c) - r;
}

#define BACK_WALL 0
#define LEFT_WALL 1
#define RIGHT_WALL 2
#define FLOOR 3
#define SPHERE1 4
#define SHORT_CUBE 5
#define BIG_CUBE 6
#define BACK_WALL_SDF plane(pos, vec4(0.0, 0.0, -1.0, 15.0))
#define LEFT_WALL_SDF plane(pos, vec4(1.0, 0.0, 0.0, 5.0))
#define RIGHT_WALL_SDF plane(pos, vec4(-1.0, 0.0, 0.0, 5.0))
#define CEILING_SDF plane(pos, vec4(0.0, -1.0, 0.0, 7.5))
#define FLOOR_SDF plane(pos, vec4(0.0, 1.0, 0.0, 4.0))
#define SPHERE1_SDF sphere(rotateY(pos, 15.0 * 3.14159 / 180.0), 3.5, vec3(-7.0, 2.0, -1.0))
#define SHORT_CUBE_SDF cube(rotateY(pos + vec3(-2, 1.5, -0.75), -17.5 * 3.14159 / 180.0), vec3(2.2, 3, 2.2))

vec3 rotateY(vec3 p, float a)
{
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);   
}

// function to create whole scene
void sceneMap3D(vec3 pos, out float t, out int obj)
{
    t = FLOOR_SDF;
    obj = FLOOR;

    float t2;
    if ((t2 = SPHERE1_SDF) < t) {
        t = t2;
        obj = SPHERE1;
    }
    if ((t2 = SHORT_CUBE_SDF) < t) {
        t = t2;
        obj = SHORT_CUBE;
    }
}

float sceneMap3D(vec3 pos)
{
    float t = FLOOR_SDF;

    float t2;
    if ((t2 = SPHERE1_SDF) < t) {
        t = t2;
    }
    if ((t2 = SHORT_CUBE_SDF) < t) {
        t = t2;
    }
    return t;
}


// Soft Shadow for Penumbra Effect
float softShadow(vec3 dir, vec3 origin, float min_t, float k) {
    float res = 1.0;
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i) {
        float m = sceneMap3D(origin + t * dir);
        if(m < 0.0001) {
            return 0.0;
        }
        res = min(res, k * m / t);
        t += m;
    }
    return res;
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj)
{
    t = 0.001;
    for (int i = 0; i < RAY_STEPS; ++i) {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, hitObj);
        if (m < 0.001) {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 lightVec, vec3 view)
{
    switch(hitObj) {
        case FLOOR:
        return matteWhite;
        break;
        case SPHERE1:
        return vec3(0.85, 0.41, 0.2);
        break;
        case SHORT_CUBE:
        return vec3(0.1, 0.31, 0.6);
        break;
        case -1:
        return vec3(0, 0, 0);
        break;
    }
    return vec3(0, 0, 0);
}

vec3 computeNormal(vec3 pos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize(vec3(sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                         sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                         sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}

Intersection sdf3D(vec3 dir, vec3 eye)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj);

    vec3 isect = eye + (t * dir);
    vec3 nor = computeNormal(isect);

    vec3 lightDir = normalize(lightPos - isect);
    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect));
    vec3 warmDir = rotateY(normalize(lightDir), sin(iTime * 0.25));

    float warmDot = max(0.0, dot(nor, warmDir));

    vec3 overallColor = warmDot * vec3(1.0, 0.88, 0.7) * surfaceColor * softShadow(warmDir, isect, 0.1, SHADOW_HARDNESS);

    vec3 sdfColor = overallColor;

    return Intersection(t, sdfColor, isect, hitObj);
}

// Returns direction of ray
vec3 rayCast(vec3 eye, vec3 ref, vec2 ndc)
{
    vec3 F = ref - eye;
    vec3 R = normalize(cross(vec3(0, 1, 0), F));
    vec3 U = normalize(cross(R, -F));

    vec3 V = U * length(F) * tan(FOVY * 0.5);
    vec3 H = R * length(F) * tan(FOVY * 0.5) * float(iResolution.x) / iResolution.y;

    vec3 p = ref + ndc.x * H + ndc.y * V;

    return normalize(p - eye);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Convert range to [-1, 1]
    uv = uv * 2.0 - vec2(1.0, 1.0);

    vec3 eye = vec3(9, 5.5, -24);
    vec3 ref = vec3(-2, 1.5, 0);

    vec3 rayDir = rayCast(eye, ref, uv);

    Intersection isect = sdf3D(rayDir, eye);

    fragColor = vec4(isect.color, 1.0);
}

