    d=min(d, box(p2, vec3(1,0.3*id,1)));
    //d=max(d, -box(p2, vec3(4,id*130,4)));

  }

  vec3 p3 = p;
  float t3 = time*.13;
  p3.xz *= rot(t3);
  p3.xy *= rot(t3*1.3);
  p3=repeat(p3, 5.);
  float d2 = box(p3, vec3(1.7));
  //d = max(d, -d2*1.3);
  d = min(d, d-d2*0.1);//*curve(time, .3));

