  float t4 = time*1.33;
  p4.xz=repeat(p4.xz, 200.);
  p4.yz *= rot(t4);
  p4.xz *= rot(t4*1.3);

  at += 0.04/(1.2+abs(length(p4.xz)-17.));

  vec3 p5 = p;
  float t5 = time*1.23;
  p5.xz=repeat(p5.xz, 200.);
  p5.yz *= rot(t5*.7);
  p5.xy *= rot(t5);

  at2 += 0.04/(1.2+abs(box(p5,vec3(37))));

  //d -= sin(time);

  return d*.7;
}
