float grid2(vec3 p) {
  float v=0.0;
  p *= 0.004;
  for(float i=0.; i<3.; ++i) {
    p *= 1.7;
    p.xz *= rot(0.3+i);
    p.xy *= rot(0.4+i*1.3);
    p += vec3(0.1,0.3,-.13)*(i+1.);
    vec3 g=abs(fract(p)-.5)*2.0;
    //v-=g.x*g.y*g.z*.7;
    v-=min(g.x,min(g.y,g.z))*.7;
  }
  return v;
}

