float super(vec3 p) {
    return sqrt(length(p*p));
}

float box(vec3 p, vec3 d) {
    vec3 q = abs(p)-d;
    return super(max(vec3(0), q)) + min(0., max(q.x, max(q.y, q.z)));
}

float linedist(vec3 p, vec3 a, vec3 b) {
    float k = dot(p-a,b-a)/dot(b-a,b-a);
    return distance(p, mix(a,b,clamp(k,0.,1.)));
}

vec3 erot(vec3 p, vec3 ax, float ro) {
    return mix(dot(ax,p)*ax, p, cos(ro))+sin(ro)*cross(ax,p);
}

float smin(float a, float b, float k) {
    float h = max(0., k-abs(b-a))/k;
    return min(a,b)-h*h*h*k/6.;
}

int mat;
float domino(vec3 p, mat3 topholes, mat3 bottomholes) {
    float body = box(p, vec3(0.0,0.4,0.9))-0.2;
    float holes = linedist(p, vec3(.2,0.45,0), vec3(0.2,-0.45,0))-0.04; //divider
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 3; j++) {
            vec2 holepos = vec2(float(i-1), float(j-1))*0.3;
            if (topholes[i][j] == 1.) {
                holes = min(holes, length(p-vec3(.2,holepos+vec2(0.,0.55)))-0.08);
            }
            if (bottomholes[i][j] == 1.) {
                holes = min(holes, length(p-vec3(.2,holepos+vec2(0.,-0.55)))-0.08);
            }
        }
    }
    mat = -body < holes ? 1 : 0;
    return -smin(-body, holes, 0.05);
}

float scene(vec3 p) {
    float table = box(p-vec3(0,0,-1.2), vec3(3,3,0.05))-0.05;
    float d1 = domino(erot(p-vec3(-1,0,0),vec3(0,0,1),-0.05), mat3(0,0,1,0,1,0,1,0,0), mat3(1,0,1,0,1,0,1,0,1));
    int d1mat = mat;
    float d2 = domino(erot(p-vec3(1,0,0),vec3(0,0,1),0.1), mat3(1,0,1,0,0,0,1,0,1), mat3(0,0,0,0,1,0,0,0,0));
    int d2mat = mat;
    float dominos = min(d1,d2);
    mat = d1 < d2 ? d1mat : d2mat;
    mat = dominos < table ? mat : 2;
    return min(dominos,table);
}

vec3 norm(vec3 p) {
    mat3 k = mat3(p,p,p)-mat3(0.001);
    return normalize(scene(p) - vec3(scene(k[0]),scene(k[1]),scene(k[2])) );
}

vec3 srgb(float r, float g, float b) {
    return vec3(r*r,g*g,b*b);
}

vec3 srgb(float k) {
    return vec3(k*k);
}

vec3 trilinear_texture(vec3 p, vec3 n) {
    return mix(mix(texture(iChannel0,p.xz), texture(iChannel0,p.yz), n.x*n.x), texture(iChannel0,p.xy), n.z*n.z).xyz;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 mouse = (iMouse.xy - 0.5*iResolution.xy)/iResolution.y;

    //when centricity is negative, instead of camera rays diverging from the
    //image plane, they instead converge. you can image this as the image plane
    //being a refractive object that causes the camera rays to switch directions
    float centricity = cos(iTime)*0.5;
    vec3 cam = normalize(vec3(1,uv*centricity));
    vec3 init = vec3(-5,uv*(4.-centricity*2.));

   	float yrot = 0.1;
    float zrot = 3.14;
    if (iMouse.z > 0.) {
        yrot += -4.*mouse.y;
        zrot += 4.*mouse.x;
    }
    cam = erot(cam, vec3(0,1,0), yrot);
    init = erot(init, vec3(0,1,0), yrot);
    cam = erot(cam, vec3(0,0,1), zrot);
    init = erot(init, vec3(0,0,1), zrot);

    vec3 p = init;
    bool hit = false;
    for (int i = 0; i < 500 && !hit; i++) {
        float dist = scene(p);
        hit = dist*dist < 1e-6;
        p+=dist*cam;
        if (distance(p, init) > 100.) break;
    }
    int locmat = mat;
	vec3 n = norm(p);
    vec3 r = reflect(cam, n);
    float ao = smoothstep(-1.,1.,scene(p+n*0.1)/.1);
    float specao = smoothstep(0.,1.,scene(p+r*0.3)/.3);
    float diff = ao*length(sin(n*2.5)*0.5+0.5)/sqrt(3.);
    float spec = length(sin(r*2.5)*0.5+0.5)/sqrt(3.);
    float diffbright = locmat == 0 ? 0.05 : 1.;
    float specbright = locmat == 2 ? 0.1 : 1.5;

    p += trilinear_texture(p*0.5,n)*0.1;
    vec3 tex = trilinear_texture(p,n)*0.25 + trilinear_texture(p*0.5,n)*0.5 + trilinear_texture(p*0.2,n);
    vec3 diffcol = locmat == 2 ? (tex.x*0.5+0.1)*srgb(0.9, 0.85, 0.8) : srgb(0.95);
    specbright += tex.x*tex.x*0.2;
    vec3 col = mix(diffcol*0.1, diffcol, diff)*diffbright + pow(spec, 10.)*specbright*specao;
    fragColor.xyz = hit ? col : vec3(pow(length(sin(cam*2.5)*0.5+0.5)/sqrt(3.), 10.)+0.01);
    fragColor.xyz = sqrt(fragColor.xyz);
}

vec4 quaternion(vec3 axis,float angle){
	float halfang=angle/2.;
    return vec4(axis*sin(halfang),cos(halfang));
}

vec4 quaternionMultiply(vec4 q1,vec4 q2){
	return vec4(q1.xyz*q2.w+q1.w*q2.xyz+cross(q1.xyz,q2.xyz),q1.w*q2.w-dot(q1.xyz,q2.xyz));
}

vec3 rotation(vec4 q,vec3 pos){
	return pos+2.*cross(q.xyz,cross(q.xyz,pos)+q.w*pos);
}

//Note: we need dimension.x > dimension.y here
float sdfCross(vec2 dimension,vec3 p){
    vec3 xmaxP=abs(p);
    xmaxP=xmaxP.x<xmaxP.y?xmaxP.yzx:xmaxP;
    xmaxP=xmaxP.x<xmaxP.y?xmaxP.yzx:xmaxP;
    xmaxP=xmaxP.x<xmaxP.z?xmaxP.zxy:xmaxP;
    vec3 diff=xmaxP-dimension.xyy;
    float maxdiff=max(max(diff.x,diff.y),diff.z);
    vec3 connection=maxdiff>0.?diff:vec3(0.,-maxdiff,dimension.y-xmaxP.x);
	return sign(maxdiff)*length(max(connection,0.));
}

float sdfcell(vec3 p,vec2 cell){
    vec4 qUpDown=quaternion(vec3(1,0,0),radians(-cell.y*30.));
    vec4 qLeftRight=quaternion(vec3(0,0,1),radians(iTime*10.+cell.x*30.));
    vec4 q=quaternionMultiply(qLeftRight,qUpDown);
	return sdfCross(vec2(0.9,0.3),rotation(q,p))+.1;
}

float sdf(vec3 p){
    vec2 cell=floor((p.xz/2.0));
    vec2 offset=fract((p.xz/2.0))*2.;
	return min(min(sdfcell(vec3(offset.x,p.y,offset.y),cell+vec2(0.,0.)),
              sdfcell(vec3(offset.x-2.,p.y,offset.y),cell+vec2(1.,0.))),min(
              sdfcell(vec3(offset.x,p.y,offset.y-2.),cell+vec2(0.,1.)),
              sdfcell(vec3(offset.x-2.,p.y,offset.y-2.),cell+vec2(1.,1.))));
    //return length(p)-1.0;
}

vec3 rayDirection(in vec2 fragCoord){
    float zoom=0.6;
    vec2 offset=fragCoord/iResolution.xy-0.5;
    offset.y/=iResolution.x/iResolution.y;
    vec3 rawDir=vec3(offset/zoom,-1);
    vec2 mouseAngles=iMouse.xy/iResolution.xy-0.5;
    vec4 qUpDown=quaternion(vec3(1,0,0),radians(90.+mouseAngles.y*10.0));
    vec4 qLeftRight=quaternion(vec3(0,0,1),radians(0.-mouseAngles.x*10.0));
    vec4 q=quaternionMultiply(qLeftRight,qUpDown);
    return normalize(rotation(q,rawDir));
}

#define delta 0.00025
vec3 normal(vec3 pos){
    vec2 e=vec2(1.,-1.);
    vec3 rawNormal=e.xxx*sdf(pos+delta*e.xxx);
    rawNormal+=e.xyy*sdf(pos+delta*e.xyy);
    rawNormal+=e.yyx*sdf(pos+delta*e.yyx);
    rawNormal+=e.xxx*sdf(pos+delta*e.xxx);
    return normalize(rawNormal);
}

#define iteration 128
vec3 rayMarching(vec3 start,vec3 dir){
    float tmin=(-1.0-start.y)/dir.y;
    vec3 pos=tmin*dir+start;
    float t=tmin;
    if(tmin<0.){
        return pos;
    }
    for(int i=0;i<iteration;i++){
        float step1=sdf(pos);
        if(abs(step1)<.000001*t){
        	return pos;
        }
        t+=step1;
        pos=t*dir+start;
    }
    return pos;
}

float lighting(vec3 pos){
    vec3 light=vec3(0.+sin(2.*iTime),-2.0,0.-cos(2.*iTime));
    float diffuse=clamp(dot(normalize(light-pos),normal(pos)),0.,1.);
	return diffuse*.5+.5;
}

vec3 color(vec3 pos){
	return pos.y>1.?vec3(0,0,0):vec3(1.,.5,0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 cameraPos=vec3(0.+.25*sin(iTime),-8.0,0.-.25*cos(iTime));
    vec3 dir=rayDirection(fragCoord);
    vec3 pos=rayMarching(cameraPos,dir);
    fragColor =vec4(lighting(pos)*color(pos),1);
}

float n21(vec2 n)
{
	return fract(sin(dot(n, vec2(12.9898,78.233))) * 43758.5453);
}

mat2 rot(float a)
{
	return mat2(
    	cos(a), -sin(a),
        sin(a), cos(a)
    );
}

float grid(vec2 uv)
{
	return n21(uv) < .1 ? 1. : 0.;
}

float line(vec2 uv, vec2 lo, vec2 ld)
{
    uv -= lo;
	vec2 p = dot(uv, ld) / dot(ld, ld) * ld;
    return length(p - uv);
}

float line_segment(vec2 uv, vec2 lo, vec2 ld)
{
	uv -= lo;
	vec2 p = dot(uv, ld) / dot(ld, ld) * ld;
    float l = clamp(length(p), 0., length(ld));
    p = normalize(ld) * l;
    return length(p - uv);
}

vec2 line_intersect_line(vec2 s1, vec2 v1, vec2 s2, vec2 v2)
{
    float b = (-s1.y - v1.y * ((s2.x - s1.x) / v1.x) + s2.y) / (v2.x / v1.x * v1.y - v2.y);
    return s2 + v2 * b;
}

void cube_line(vec2 n, out vec2 o, out vec2 d)
{
	d = -n.yx;
    o = clamp(n, vec2(0, 0), vec2(1, 1));
}

bool traverse_voxel(vec2 ro, vec2 rd, out vec2 id, out vec2 p, out vec2 n)
{
    const int len = 128;
    //const int size = 16;
    int x = int(floor(ro.x));
    int y = int(floor(ro.y));
    int stepX = int(sign(rd.x));
    int stepY = int(sign(rd.y));
    int cstepX = clamp(stepX, 0, 1);
    int cstepY = clamp(stepY, 0, 1);
    //int outX = size * stepX;
    //int outY = size * stepY;
    float tDeltaX = abs(rd.y);
    float tDeltaY = abs(rd.x);
    float tMaxX = tDeltaX - fract(ro.x * sign(rd.x)) * tDeltaX;
    float tMaxY = tDeltaY - fract(ro.y * sign(rd.y)) * tDeltaY;
    int status = -1; // unresolved
    int i = 0;

    do {
        if(tMaxX < tMaxY) {
            tMaxX += tDeltaX;
            x += stepX;
            n = vec2(-stepX, 0);
        } else {
            tMaxY += tDeltaY;
            y += stepY;
            n = vec2(0, -stepY);
        }

        if (grid(vec2(x, y)) == 1.) {
            status = 0; // hit
            id = vec2(x, y);
        }

        //if (x == outX || y == outY) status = 1; // outside
    } while(status == -1 && i++ < len);

    if (status == 0) {
        vec2 lo, ld;
        cube_line(n, lo, ld);
        p = line_intersect_line(ro, rd, id + lo, ld);

        return true;
    }
    return false;
}

void bounce(vec2 uv, vec2 ro, vec2 rd, vec2 id, float scale, inout vec3 col)
{
    for (int i = 0; i < 8; i++) {
        vec2 pid, p, n;
        col.rg += traverse_voxel(ro, rd, pid, p, n) ? (id == pid ? 1. : 0.) : 0.;
        col.b += smoothstep(scale / iResolution.y * 2., .0, line_segment(uv, ro, rd * length(p - ro)));
        ro = p + n * .001;
        rd = reflect(rd, n);
    }
}

void mainImage(out vec4 frag_color, in vec2 frag_coord)
{
    float scale = 32.;
    vec2 uv = frag_coord / iResolution.y * scale;
    vec2 id = floor(uv);
