    //float sphere3 = sdSphere(pos-vec3(0.0, 0.0, 0.75), 0.1);
    //d = min(d, sphere3);
    float plane = sdPlane(pos-vec3(0, -10.0, 0));
    d = min(d, plane);
    d *= 0.6;
	//return min(sphere1, sphere2);
	return d;
}

mat3 rotation(vec2 angles) {
    mat3 id = mat3(1, 0, 0, 0, 1, 0, 0, 0, 1);
    {
        vec3 u = vec3(cos(angles.x), 0, sin(angles.x));
        vec3 v = vec3(0, 1, 0);
        vec3 w = vec3(-sin(angles.x), 0, cos(angles.x));
        id = id * mat3(u, v, w);
	}
    {
        vec3 u = vec3(1, 0, 0);
        vec3 v = vec3(0, cos(angles.y), -sin(angles.y));
        vec3 w = vec3(0, sin(angles.y), cos(angles.y));
        id = id * mat3(u, v, w);
	}
    return id;
}
