#define scale 10.0


float random(vec2 st)
{
 	return fract(sin(dot(st, vec2(12.43, 67.88)))*94436.3453);   
}

float noise(vec2 uv)
{
	vec2 i = floor(uv);
    vec2 f = fract(uv);
    vec2 u = smoothstep(0.0, 1.0, f);

    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    return mix( mix(a, b, u.x),
                mix(c, d, u.x), u.y);
}


mat2 rotate2d(float angle)
{
    return mat2(cos(angle), -sin(angle),
                sin(angle),  cos(angle));
}

vec2 line(in vec2 uv, float b, float c)
{
 	vec2 f = fract(uv);
    return smoothstep(b - c, b, f) - smoothstep(b, b + c, f);
}

float smoothLine(in vec2 uv)
{
    vec2 f = fract(uv);
    vec2 u = smoothstep(0.0, 1.0, f);
    float b1 = noise(uv - vec2(0.0, -1.0));
    float b2 = noise(uv - vec2(0.0, 0.0));
    float dist = (1.0 - abs(b2 - u.y)) * abs(b2 - u.y);
    return  1.0 - dist;
}

vec3 planet( in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    uv *= scale;
    //uv = rotate2d(3.2*(uv.x - 14.2) * (uv.y / 8.0) * 3.1415 / 180.0) * uv;
    uv = rotate2d(21.0 * 3.1415 / 180.0) * uv;

    vec2 pos = vec2(uv.x + iTime, uv.y);
    float pattern = line(pos, noise(pos), 0.07).y;
    float colorPattern = smoothLine(pos);

    return vec3(pattern * .2, (1.0 - colorPattern) + pattern * .2, pattern * .2);
}

vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

vec2 random3( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(103.1,311.7)),dot(p,vec2(183.5,183.3))))*43758.5453);
}

vec3 star(in vec2 uv, float _scale)   
{

    uv *= _scale;

    vec2 i_st = floor(uv);
    vec2 f_st = fract(uv);

    float min_dist = 1.0;
    float max_dist = 1.0;
    float min_dist2 = 1.0;

    for (int y = -1; y <= 1; ++y)
    {
    	for (int x = -1; x <= 1; ++x)
        {
        	vec2 neighbor = vec2(float(x), float(y));

            vec2 point = random2(i_st + neighbor);
            point = 0.5 + 0.5*sin(iTime + 6.2831*point);

            vec2 point2 = random3(i_st + neighbor);
            point2 = 0.5 + 0.5*sin(iTime + 8.2831*point2);

            vec2 point3 = random2(i_st + neighbor);
            point2 = 0.5 + 0.5*sin(iTime + 1.2831*point3);

            vec2 diff = neighbor + point - f_st;
            vec2 diff2 = neighbor + point2 - f_st;
            vec2 diff3 = neighbor + point3 - f_st;

            float dist = abs(length(diff));
            float dist2 = abs(length(diff2));
            float dist3 = abs(length(diff3));

            min_dist = min(min_dist, dist);
            max_dist = min(max_dist, dist2);
            min_dist2 = min(min_dist2, dist3);
        }
    }
    return vec3(min_dist + max_dist + min_dist2);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv.x *= iResolution.x/iResolution.y;

    vec3 color;
    vec3 starCol = vec3(0.6, 0.6, 0.6);
    vec2 center = vec2(0.85, 0.5);

    float shadow = distance(center + 0.4, uv);
    float shadow2 = distance(center + vec2(-0.5, -0.5), uv);

    vec3 stars = star(0.6*uv, 50.0);
    vec3 circleForeground = (1.0 - step(0.4, distance(center, uv))) * vec3(1.0);// (planet(fragCoord) * 0.4);
    vec3 circleBackground = (step(0.4, distance(center, uv))) * vec3(1.0);

    vec2 ringPos = rotate2d(30.0 * 3.1415 / 180.0) * (uv + 1.25);
    float ring = line(ringPos, 0.5, 0.07).y * 10.0;

    starCol -= stars;
    circleBackground *= starCol * abs(sin(iTime / 10.0 + uv.x));
    circleForeground *= shadow2 * shadow * planet(fragCoord);
    color = circleBackground + circleForeground;

    fragColor = vec4(color, 1.0);
}

