#define M_PI 3.1415926535897932384626433832795
#define AMPLITUDE 0.2
#define PHASE 2. * M_PI
#define THICKNESS 0.03
#define FREQUENCY 1.
#define WAVE_LENGTH 0.1


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.y -= 0.5;  

    float phase = PHASE + uv.x / WAVE_LENGTH;

    int tx = int(uv.x*512.0);
	float fft  = texelFetch(iChannel0, ivec2(tx,0), 0).x; 
    float wave = texelFetch(iChannel0, ivec2(tx,1), 0).x;

	float amplitude = AMPLITUDE + wave/50.;
    float frequency = FREQUENCY + wave/200.;

    vec3 col = vec3(0., 0., 0.);

    float waveValue = amplitude * sin(2. * M_PI * frequency * iTime + phase);

    if (uv.y < waveValue + THICKNESS
       && uv.y > waveValue - THICKNESS)
    {
        col = vec3(1. - (abs(waveValue - uv.y) / THICKNESS));
        col.g += sin(iTime)*wave;
        col.r += cos(iTime)*wave;
        col.b += tan(iTime)*wave;
    }

    // Output to screen
    fragColor = vec4(col, 1.0);
}

