float rand (in vec2 _st) {
    return fract(sin(dot(_st.xy, vec2(-0.370,0.200+0.0002*sin(iTime*0.1))))*757.161);
}

float noise (in vec2 _st) {
	const vec2 d = vec2(0.0, 1.0);
    vec2 b = floor(_st);
    vec2 f = smoothstep(vec2(0.), vec2(0.5), fract(_st));
    float m1 = mix(rand(b), rand(b + d.yx), f.x);
    float m2 = mix(rand(b + d.xy), rand(b + d.yy), f.x);
	return mix(m1, m2, f.x);
}

float fbm ( in vec2 _st) {
    float v = 0.23 + 0.05 * sin(iTime*0.2);
    float a = .5;
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(0.5)+sin(iTime*0.01), -sin(0.5), acos(0.5));
    for (int i = 0; i < 5; ++i) {
        v += a * noise(_st);
        _st = rot * _st * 8.0;
        a *= 0.32;
    }
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = (fragCoord * 2. - iResolution.xy) / min(iResolution.x, iResolution.y) * 1.7;

    vec2 co = st;
    co.x += 0.9*sin(iTime);
    co.y += 4.;

    float len;    
    for (int i = 0; i < 3; i++) {
        len = length(co);
        co.x +=  sin(co.y + iTime * 0.620)*0.;
        co.y +=  cos(co.x + iTime * 0.164)*0.1;
    }
    len -= 3.;

    vec3 col = vec3(0.);

    vec2 q = vec2(0.);
    q.x = fbm( st + 1.0);
    q.y = fbm( st + vec2(-0.450,0.650));

    vec2 r = vec2(0.);
    r.x = fbm( st + 1.0*q + vec2(0.570,0.520)+ 0.16*iTime );
    r.y = fbm( st + 1.0*q + vec2(0.340,-0.570)+ 0.1*iTime);

    for (float i = 0.; i < 3.; i++) {
        r += 1.0 / abs(mod(st.xy+vec2(0.4*iTime,0.), 0.732* i) * 10.) * 1.;// Glass block grid
    }
    float f = fbm(st+r);

    col = mix(col, cos(len + vec3(0.0, 1.0, 0.3)), 1.0);
    col = mix(vec3(0.562,0.680,0.482), vec3(0.357,0.518,0.600), col);

    fragColor = vec4(2.0*(f*f*f*f+.6*f*f+.5*f)*col,1.);
}

