vec3 nbispirale(vec3 p, float h)
{
    return normalize((vec3(sdf(p + vec3(h,0.,0.)), sdf(p + vec3(0.,h,0.)), sdf(p + vec3(0.,0.,h))) - sdf(p)) / h);
}

float raymarch(vec3 ro, vec3 rd, int maxsteps)
{
    float ret = -1.;
    float t = 0.0;
    for (int i = 0; i < maxsteps; ++i) {
        vec3 p = ro + rd * t;
        float d = sdf(p);
        if (d <= 1e-7) {
          ret = t;
          break;
        }
        t += max(d, .0005);
    }
    if (ret < 0. && rd.y < 0.)
        ret = ro.y / -rd.y; // rescue to plane
    return ret;
}

