/**
 * Part 6 Challenges:
 * - Make a scene of your own! Try to use the rotation transforms, the CSG primitives,
 *   and the geometric primitives. Remember you can use vector subtraction for translation,
 *   and component-wise vector multiplication for scaling.
 */

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.00001;

vec3 hash33(vec3 p3)
{	
    //this is from https://www.shadertoy.com/view/4djSRW
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += (p3, p3.yxz+10.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);

}

/**
 * Rotation matrix around the Y axis.
 */
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}


float sphereSDF(vec3 p, float r) {
    return length(hash33(sin(p)/10.0))-sin(length(p))/3.33;
}


/**
 * Signed distance function describing the scene.
 * 
 * Absolute value of the return value indicates the distance to the surface.
 * Sign indicates whether the point is inside or outside the surface,
 * negative indicating inside.
 */
float sceneSDF(vec3 samplePoint) {    
    //Slowly spin the whole scene
    samplePoint = samplePoint+vec3(0.0,0.0,iTime);

    return sphereSDF(samplePoint,1.0);
}

/**
 * Return the shortest distance from the eyepoint to the scene surface along
 * the marching direction. If no part of the surface is found between start and end,
 * return end.
 * 
 * eye: the eye point, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 * start: the starting distance away from the eye
 * end: the max distance away from the ey to march before giving up
 */
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}


/**
 * Return the normalized direction to march in from the eye point for a single pixel.
 * 
 * fieldOfView: vertical field of view in degrees
 * size: resolution of the output image
 * fragCoord: the x,y coordinate of the pixel in the output image
 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

/**
 * Using the gradient of the SDF, estimate the normal on the surface at point p.
 */
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

/**
 * Lighting contribution of a single point light source via Phong illumination.
 * 
 * The vec3 returned is the RGB color of the light's contribution.
 *
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 * lightPos: the position of the light
 * lightIntensity: color/intensity of the light
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        // Light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}

/**
 * Lighting via Phong illumination.
 * 
 * The vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = vec3(4.0,
                          2.0,
                          4.0);
    vec3 light1Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);

    vec3 light2Pos = vec3(2.0 * sin(0.37),
                          2.0 * cos(0.37),
                          2.0);
    vec3 light2Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light2Pos,
                                  light2Intensity);    
    return color;
}

/**
 * Return a transform matrix that will transform a ray from view space
 * to world coordinates, given the eye point, the camera target, and an up vector.
 *
 * This assumes that the center of the camera is aligned with the negative z axis in
 * view space when calculating the ray marching direction. See rayDirection.
 */
mat3 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat3(s, u, -f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(8.0, 5.0, 7.0);

    mat3 viewToWorld = viewMatrix(eye, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));

    vec3 worldDir = viewToWorld * viewDir;

    float dist = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
		return;
    }

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist * worldDir;

    // Use the surface normal as the ambient color of the material
    vec3 K_a = hash33(p/10.0);
    vec3 K_d = K_a;
    vec3 K_s = vec3(1.0, 1.0, 1.0);
    float shininess = 10.0;

    vec3 color = phongIllumination(K_a, K_d, K_s, shininess, p, eye);

    fragColor = vec4(color, 1.0);
}

float DistLine(vec3 ro, vec3 rd, vec3 p) {
    // the distance from the point p to the ray line in 3D space
    return length(cross(p - ro, rd))/length(rd);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy;

    //set origin to middle of screen
    uv -= .5;

    //sets u axis so that the right aspect ratio is in terms of uv
	uv.x *= iResolution.x/iResolution.y;

    // set the ray origin (camera position) 2 units toward the screen
    vec3 ro = vec3(0., 0., -2.);

	// set the ray direction, subtract ro to get intersection point
	vec3 rd = vec3(uv.x, uv.y, 0.) - ro;

    // per frame static variable
    float t = iTime;

    // set point with formula to move in a circle along the xz plane
    vec3 p = vec3(sin(t), 0, 2. + cos(t));

    // call the function that calculates distance
    float d = DistLine(ro, rd, p);

    //performs smooth Hermite interpolation between 0 and 1
    d = smoothstep(.1, .09, d);

    // Output to screen
    fragColor = vec4(d);
}

#define PI 3.14159

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy)-.5;
	uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.);//vec3(1.-fract(iTime/20.)-uv.x*uv.y);

    float signal = max(
       abs (cos(uv.x*20.*(.5*sin(iTime/2.)+1.)*PI+iTime*4.)+.3),
       abs (sin(uv.y*20.*(.5*sin(iTime/2.)+1.)*PI-iTime*3.)+.3)
    );

    vec3 interestingColor = vec3(
        0.,
        signal*.3, 
        signal*.4
    );

    col = mix(col, vec3(interestingColor), signal);


    // Output to screen
    fragColor = vec4(col,1.0);
}

#define sLenght(a) dot((a),(a))

vec2 cInv(vec2 c){
	float sl = dot(c,c);
	return vec2(c.x / sl, -c.y / sl);
}

vec2 cMul(vec2 a, vec2 b){
	vec4 t = a.xyxy * b.xyyx;
	return vec2(t.x - t.y, t.z + t.w);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	float zoom = (sin(iTime/4.0)+1.0) * 8.0 + 1.0;
    vec2 p = -(zoom/2.0) + zoom * fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x/iResolution.y;

	float t = iTime/2.0;

	//setting roots of a third grade poly using a Lissajous curve
    vec2 root1 = vec2(sin(t + 0.00000000) , sin(2.0 * t + 0.00000000));
	vec2 root2 = vec2(sin(t + 2.09439510) , sin(2.0 * t + 2.09439510));
	vec2 root3 = vec2(sin(t + 4.18879020) , sin(2.0 * t + 4.18879020));

	const int maxIters = 16;
	float tolerance = 1e-8;
	vec2 r1,r2,r3;
	vec2 prevP = p;
	float iters = 0.0;

	r1 = p-root1;
	r2 = p-root2;
	r3 = p-root3;

	for(int iterations = 0; iterations < maxIters; iterations++ ){
		prevP = p;
		//newton-rhapson method iteration
		p = p - cInv( cInv(r1) + cInv(r2) + cInv(r3) );

		//check how near we are from the nearest root
		r1 = p-root1;
		r2 = p-root2;
		r3 = p-root3;
		if (min(sLenght(r1),min(sLenght(r2),sLenght(r3))) < tolerance ) {break;}
		iters += 1.0;
	}
	float co = iters;

	//the code could be more compact but repeating code makes changing the gradients easier
	float l1 = length(p-root1);
	float l2 = length(p-root2);
	float l3 = length(p-root3);
	float dist0,dist1;
	float tol = log(tolerance);
    if (l1 <= l2 && l1 <= l3){
		dist0 = log(sLenght(prevP-root1));
		dist1 = log(sLenght(p-root1));
		if (dist1 < tol && dist0 > tol) { co += (tol - dist0) / (dist1 - dist0); }
		co = clamp(co/float(maxIters),0.0,1.0);

		//gradient for root1
		fragColor = vec4(
			0.5+0.89*cos(6.2831855*co+0.0),
			0.5+0.48000002*cos(25.132742*co+2.0734513),
			0.5+0.38*cos(50.265484*co+4.1469026),
			1.0);

	}else if (l2 <= l1 && l2 <= l3){
		dist0 = log(sLenght(prevP-root2));
		dist1 = log(sLenght(p-root2));
		if (dist1 < tol && dist0 > tol) { co += (tol - dist0) / (dist1 - dist0); }
		co = clamp(co/float(maxIters),0.0,1.0);

		//gradient for root2 
		fragColor = vec4(
			0.5+0.6*cos(2.0106194*co+-1.5079645),
			0.5+1.0*cos(4.5867257*co+2.576106),
			0.5+0.82*cos(3.015929*co+2.3247786),
			1.0);

	}else if (l3 <= l2 && l3 <= l1){
		dist0 = log(sLenght(prevP-root3));
		dist1 = log(sLenght(p-root3));
		if (dist1 < tol && dist0 > tol) { co += (tol - dist0) / (dist1 - dist0); }
		co = clamp(co/float(maxIters),0.0,1.0);

		//gradient for root3 
		fragColor = vec4( .5+.5*cos(6.2831*co+0.0),
                         .5+.5*cos(6.2831*co+0.4),
                         .5+.5*cos(6.2831*co+0.7),
                         1.0 );

	}else{
		fragColor = vec4(0.0 , 0.0, 0.0, 1.0 );
	}
}

