    float ds = 1.0 / iResolution.y;
    z *= 9.0; ds *= 9.0;
    vec3 zh = vec3(z, 1.0);

    vec2[12] c; float[12] r;
    float r1 = pow(1.5 + 0.5 * sqrt(5.0) - sqrt(1.5 * sqrt(5.0) + 2.5), 0.5);
    r[0] = r1; c[0] = vec2(0.0);
    float r2 = r1 * sqrt(sqrt(5.0));
