        float theta = tau * 0.2 * float(i);
        vec2 eit = vec2(cos(theta), sin(theta));
        r[i+1] = r2;
        c[i+1] = x2 * eit;
        r[i+6] = r3;
        c[i+6] = x3 * eit;
    }
    float r4 = -1.0 / r1;
    r[11] = r4; c[11] = vec2(0.0);


    float period = -8.0 * log(r1);
    float d = mod(iTime * 0.2, period) - period * 0.5;
    zh *= exp(-d); ds *= exp(-d);

    bool[4] flips=bool[](false,false,false,false);
    float[4] ts=float[](1.0,1.0,1.0,1.0);
    for (int i=0; i<6; i++) {
        for (int j=0; j<12; j++) {
            flips[m[j]] = flips[m[j]] ^^ flip(zh, ds, c[j], r[j], ts[m[j]]);
        }
    }
    vec3 col = vec3(0.5);
    for (int i=0; i<4; i++) {
        if (flips[i]) {
            ts[i] = -ts[i];
        }
        col += colorizers[i] * ts[i];
    }
    fragColor = vec4(col,1.0);
    fragColor = pow(fragColor, vec4(1.0/2.2));
}

