float sq(in float n) {
    return n * n;
}

float f(in vec3 p) {
    float power = 8. + sin(iTime * 1.) * 5.;
    vec3 z = p;
    float dr = 1.;
    float r = 0.;

    for (int i = 0; i < 10; ++i) {
        r = length(z);
        if (r > 4.)
            break;

        float theta = acos(z.z / r) * power;
        float phi   = atan(z.y / z.x) * power;
        float zr    = pow(r, power);
        dr = pow(r, power - 1.) * power * dr + 1.;

        z = zr * vec3(sin(theta) * cos(phi), sin(phi) * sin(theta), cos(theta));
        z += p;
    }
    return 0.5 * log(r) * r / dr;
}

float map(in vec3 p) {
    return f(p);
}

void mapColor(in vec3 p, out float mind, out vec3 color, out vec3 norm) {
    mind = map(p);

    color = vec3(max(0.1, min(0.9, 1. - (1. - sin(p.x * 100.)) *
                                        (1. - sin(p.y * 100.)) *
                                        (1. - sin(p.z * 100.)))));

    // get normal
    const float E = 0.0001;
    norm = normalize(vec3(f(p + vec3(E, 0., 0.)), f(p + vec3(0., E, 0.)), f(p + vec3(0., 0., E))));
}

vec3 ray_marching(vec3 orig, vec3 dir) {
    dir = normalize(dir);
    float len = 0.;
    //vec3 bg = vec3(min(0.937255, max(0.2, sin(dir.x * 5.) + sin(dir.y * 10.) + cos(dir.x * dir.y * 10.))));
    vec3 bg = vec3(0.2);

    for (int i = 0; i < maxIter; ++i) {
        float d;
        vec3 color;
        vec3 norm;
        mapColor(orig, d, color, norm);
        if (d < colissiondist) return color;
        if (len > zFar) return bg;

        len += d;
        orig += dir * d;
    }
    return bg;
}

mat3 rotatey(in float a) {
    float sina = sin(a);
    float cosa = cos(a);
    return mat3(cosa,  0., sina,
                0.,    1.,   0.,
                -sina, 0., cosa);
}

