// & mercury.sexy for these ops

vec2 opU2(vec2 x, vec2 y) {
    return vec2(min(x.x, y.x), x.x < y.x ? x.y : y.y);
}

vec3 polar(vec3 cart) {
    return vec3(length(cart), atan(cart.y, cart.z), atan(cart.z, cart.x));
}

void pR(inout vec2 p, float a) {
    p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

float fOpIntersectionRound(float a, float b, float r) {
    vec2 u = max(vec2(r + a,r + b), vec2(0));
    return min(-r, max (a, b)) + length(u);
}

float fOpDifferenceRound (float a, float b, float r) {
    return fOpIntersectionRound(a, -b, r);
}

float fOpIntersectionChamfer(float a, float b, float r) {
    return max(max(a, b), (a + r + b)*sqrt(0.5));
}

// Difference can be built from Intersection or Union:
float fOpDifferenceChamfer (float a, float b, float r) {
    return fOpIntersectionChamfer(a, -b, r);
}

float fOpPipe(float a, float b, float r) {
    return length(vec2(a, b)) - r;
}



#define GDFVector3 normalize(vec3(1, 1, 1 ))
#define GDFVector4 normalize(vec3(-1, 1, 1))
#define GDFVector5 normalize(vec3(1, -1, 1))
#define GDFVector6 normalize(vec3(1, 1, -1))

#define GDFVector7 normalize(vec3(0, 1, PHI+1.))
#define GDFVector8 normalize(vec3(0, -1, PHI+1.))
#define GDFVector9 normalize(vec3(PHI+1., 0, 1))
#define GDFVector10 normalize(vec3(-PHI-1., 0, 1))
#define GDFVector11 normalize(vec3(1, PHI+1., 0))
#define GDFVector12 normalize(vec3(-1, PHI+1., 0))

#define GDFVector13 normalize(vec3(0, PHI, 1))
#define GDFVector14 normalize(vec3(0, -PHI, 1))
#define GDFVector15 normalize(vec3(1, 0, PHI))
#define GDFVector16 normalize(vec3(-1, 0, PHI))
#define GDFVector17 normalize(vec3(PHI, 1, 0))
#define GDFVector18 normalize(vec3(-PHI, 1, 0))

#define fGDFBegin float d = 0.;
#define fGDF(v) d = max(d, abs(dot(p, v)));
#define fGDFEnd return d - r;

#define PI 3.14159265359
#define PHI (1.618033988749895)
#define TAU 6.283185307179586

float fDodecahedron(vec3 p, float r) {
    fGDFBegin
    fGDF(GDFVector13) fGDF(GDFVector14) fGDF(GDFVector15) fGDF(GDFVector16)
    fGDF(GDFVector17) fGDF(GDFVector18)
    fGDFEnd
}

float pModPolar(inout vec2 p, float repetitions) {
    float angle = 2.0*PI/repetitions;
    float a = atan(p.y, p.x) + angle/2.;
    float r = length(p);
    float c = floor(a/angle);
    a = mod(a,angle) - angle/2.;
    p = vec2(cos(a), sin(a))*r;
    // For an odd number of repetitions, fix cell index of the cell in -x direction
    // (cell index would be e.g. -5 and 5 in the two halves of the cell):
    if (abs(c) >= (repetitions/2.0)) c = abs(c);
    return c;
}

vec2 sdf(vec3 _p) {
    vec3 p = _p;
    p.y -= 0.2;
    pR(p.yz, 0.7);
    vec3 pol = polar(p);
    float n = texture(iChannel0, pol.yz * 0.06 + vec2(0., 1.0 * 0.1)).r ;
    float n2 = texture(iChannel0, pol.zy * (1.0 + n) + vec2(0., 1.0 * 0.2)).r;

    vec3 pol2 = pol;
    pol2.yz = mod(pol2.yz * 1.0, PI * 4.0);
    vec3 dp = p;
    dp.y -= 0.5;
    pModPolar(dp.yx,2.0);
    pR(dp.xz, sin(iTime));
    vec2 sp = vec2(fDodecahedron(dp, 0.44), 1.0);

    pR(dp.yz, cos(iTime));
    sp.x = fOpDifferenceRound(fDodecahedron(dp, 0.5), sp.x, 0.1);

    pR(dp.yz, sin(-iTime));
    sp.x = fOpDifferenceRound(fDodecahedron(dp, 0.5), sp.x, 0.1);

    sp.x = fOpDifferenceRound(sp.x, sp.x * 1.34 * pol.r, 0.);

    float n3 = texture(iChannel0, fract(pol.xz * 0.4 + iTime * 0.1)).r;
    n3 = n3 * n3 * n3;
    vec2 mirror = vec2(_p.y + 0.1 + n3 * 0.1, 0.0);
    mirror.x = fOpIntersectionRound(length(p) - 0.8, mirror.x, 0.06);

    vec2 ring = vec2(length(p - vec3(0.0, -0.3, 0.0)) - 0.79, 1.0);
    ring.x = fOpPipe(ring.x, mirror.x, 0.1);

    return opU2(sp, opU2(ring, mirror));
}


bool march(vec3 ro, vec3 rd, out vec3 hitPos, out float mat) {
    float t = 0.0;
    for(int i = 0; i < 50; i++) {
        vec2 d = sdf(ro);
        t += d.x;
        ro += rd * d.x;
        if(d.x < 0.001) {
            hitPos = ro;
            mat = d.y;
            return true;
        }
    }
    return false;
}

vec3 estimateNormal(vec3 p) {
    float h = 0.0001;
    vec2 k = vec2(1.0, -1.0);
    return normalize(
        k.xyy * sdf(p + h * k.xyy).x +
        k.yxy * sdf(p + h * k.yxy).x +
        k.yyx * sdf(p + h * k.yyx).x +
        k.xxx * sdf(p + h * k.xxx).x
    );
}



