#define MANDEL_ITERATIONS 5
#define MANDEL_BAIL_DIST 16.
#define MANDEL_POWER 8.0

mat2 Rot2d(float a) {
  float s = sin(a);
  float c = cos(a);
  return mat2(c, - s, s, c);
}

float sdMandelBulb(vec3 p){
  vec3 z = p;
  float dr = 1.;
  float r = 0.;
  for(int i = 0; i<MANDEL_ITERATIONS;i++){
    r = length(z);
    if(r>MANDEL_BAIL_DIST) break;

    // spherical coordinates
    float theta = acos(z.z/r);
    float phi = atan(z.y, z.x);
    dr = pow(r, MANDEL_POWER)*MANDEL_POWER*dr +1.0;

    float zr = pow(r, MANDEL_POWER);
    theta = theta*MANDEL_POWER;
    phi = phi*MANDEL_POWER;

    z = zr*vec3(sin(theta)*cos(phi), sin(phi)*sin(theta), cos(theta))+p;

  }
  return 0.5*log2(r)*r/dr;
}

float sdSphere(vec3 p, vec4 sphere) {
  return length(p - sphere.xyz) - sphere.w;
}

float sdBox(vec3 p, vec3 box) {
  p = abs(p) - box;
  return length(max(p, 0.0)) + min(max(p.x, max(p.y, p.z)), 0.0);
}

float sdGyroid(vec3 p, float scale, float thickness, float bias) {
  p *= scale;
  return abs(dot(sin(p), cos(p.zxy)) + bias) / scale - thickness;
}

float sdAAPlane(vec3 p){
