bool sphereinter(Object obj,Ray ray)
{
float a,b,c,d,spht0,spht1,t;
vec3 P;

P=ray.p-obj.info.v1;
c=dot(P,P)-obj.info.f1*obj.info.f1;
b=2.0*dot(ray.v,P);
a=dot(ray.v,ray.v);
d=b*b-4.0*a*c;
if (d<=0.0) { return(false); }
d=sqrt(d);
if (a==0.0) { return(false); }
spht0=(-b+d)/(a+a);
spht1=(-b-d)/(a+a);
if ((spht1<spht0)&&(spht1>SMALL)) t=spht1;
else t=spht0;
if (t>SMALL)
	{
	gpt=P+t*ray.v;
	gt=t;
	return(true);
	}
return(false);
}

vec3 spherenorm(Object obj,Ray ray)
{
vec3 n,pt;

n=gpt;
gpt+=obj.info.v1;
n=normalize(n);
return(n);
}

bool triinter(Object obj,Ray ray)
{
float in1,in2,f1,t;

f1=dot(obj.info.v3,ray.v);
if (abs(f1)<SMALL) return(false);
in1=obj.info.f3;
in2=dot(obj.info.v3,ray.p);
t=(in1-in2)/f1;
if (t<SMALL) return(false); /* ?? */
gpt=ray.p+t*ray.v;
return(true);
}

vec3 trinorm(Object obj,Ray ray)
{
vec3 n,pt;

return(obj.info.v3);
}

void planarobj(int t)
{
