info=infoblank;
info.v1=pol[1]-pol[0];
info.v2=pol[2]-pol[0];
info.v3=cross(info.v1,info.v2);
info.v3=normalize(info.v3);
info.v4=pol[0];
info.f1=dot(info.v1,info.v1);
info.f2=dot(info.v2,info.v2);
info.f3=dot(info.v3,info.v4);
pushobj(t,info);
}

