float shadow(Object obj,vec3 p,vec3 vv,float vd)
{
Ray v;
float t;
int j;
bool i;

v.p=p;
v.v=vv;
for (j=0;j<numobj;j++)
	{
	Object ptr=objs[j];
	if ((ptr.id!=obj.id)&&(ptr.type!=LIGHT)&&(ptr.type!=PLANE))
		{
		if (ptr.type==SPHERE) { i=sphereinter(ptr,v); }
		else { i=false; }
		if (i)
			{
			if (t<vd)
				{
				if (ptr.kt<min_weight) { return(0.0); }
				gat.r*=ptr.kt*ptr.tran.r/255.0;
				gat.g*=ptr.kt*ptr.tran.g/255.0;
				gat.b*=ptr.kt*ptr.tran.b/255.0;
				}
			}
		}
	}
return(1.0);
}

Rgb shade(Ray ray,Object obj,vec3 N,vec3 pt)
{
Rgb color,dcol;
float t2,f;
float t,t3;
int i;
vec3 L;
Ray R,T;
float light_dist=0.0; /* ?? */
Rgb at;

dcol=hit.col;
if (obj.type==LIGHT) { color.r=color.g=color.b=0.0; return(color); }
color.r=obj.ka*obj.amb.r*dcol.r;
color.g=obj.ka*obj.amb.g*dcol.g;
color.b=obj.ka*obj.amb.b*dcol.b;

for (i=0;i<numlights;i++)
	{
	L=light[i].p-pt;
	light_dist=length(L);
	L=normalize(L);
	f=dot(N,L);
	gat.r=gat.g=gat.b=1.0;
	if (shadow(obj,pt,L,light_dist)>0.0)
		{
		t3=light[i].dist/(light_dist+light[i].k);
		if ((f>0.0)||(obj.kt>min_weight))
			{
			t=t3*f*obj.kd;
			if ((obj.kt>min_weight)&&(f<0.0)) { t=-t; }
			color.r+=light[i].i.r*dcol.r*t*gat.r;
			color.g+=light[i].i.g*dcol.g*t*gat.g;
			color.b+=light[i].i.b*dcol.b*t*gat.b;
			t2=2.0*f;
			R.v=t2*N-L;
			t2= -dot(R.v,ray.v);
			if (t2>0.0)
				{
				t2=t3*pow(t2,obj.kn)*obj.ks;
				color.r+=light[i].i.r*obj.spec.r*t2*gat.r;
				color.g+=light[i].i.g*obj.spec.g*t2*gat.g;
				color.b+=light[i].i.b*obj.spec.b*t2*gat.b;
				}
			}
		}
	}
return(color);
}

bool intersect(Ray ray)
{
Object ptr,p;
int j;
bool inter=false,i=false;
float t;
vec3 pt,gpts;
float mint=MAXFLOAT;
Rgb col,colm;

for (j=0;j<numobj;j++)
	{
	ptr=objs[j];
	if (ptr.type==SPHERE) { i=sphereinter(ptr,ray); }
	else if (ptr.type==PLANE) { i=triinter(ptr,ray); }
	else { i=false; }
	if (i)
		{
		inter=true;
		if (gt<mint)
			{
			p=ptr; mint=gt; gpts=gpt;
			colm=ptr.diff;
			}
		}
	}
if (inter)
	{
	hit.object=p;
	hit.t=mint;
	gpt=gpts;
	if (p.type==SPHERE) { hit.normal=spherenorm(p,ray); }
	else if (p.type==PLANE) { hit.normal=trinorm(p,ray); }
	hit.pt=gpt;
	if (p.type==PLANE)
		{
		int ibx,ibz;

		ibx=int(gpt.x+textoff);
		ibz=int(gpt.z+textoff);
		if (((ibx+ibz)%2)==1) { colm.r=colm.g=colm.b=128.0; }
		else { colm.r=colm.g=colm.b=255.0; }
		}
	hit.col=colm;
	}
return(inter);
}

Rgb trace(Ray ray)
{
vec3 pt,N;
Object obj;
Rgb col,dcol,colt;
int level;
vec3 k;
float f,t,t2,t3;

ray.v=normalize(ray.v);
col=Rgb(0.0,0.0,0.0);
k=vec3(1.0,1.0,1.0);
for (level=0;level<=MAXLEVEL;level++)
	{
	if (intersect(ray))
		{
		obj=hit.object;
		dcol=hit.col;
		N=hit.normal;
		pt=hit.pt;
		colt=shade(ray,obj,N,pt);
		col.r+=colt.r*k.x;
		col.g+=colt.g*k.y;
		col.b+=colt.b*k.z;
		k=vec3(1.0,1.0,1.0);
		if (obj.kr>min_weight)
			{
			f= -2.0*dot(N,ray.v);
			ray.v=f*N+ray.v;
			ray.p=pt+SMALL3*ray.v;
			k=vec3(obj.kr,obj.kr,obj.kr);
			}
		else if (obj.kt>min_weight)
			{
			float n;

			f=dot(N,ray.v);
			if (f<0.0) { n=gn/obj.n; f= -f; }
			else { n=obj.n/gn; N=-N; }
			t=n*f;
			t3=1.0-n*n*(1.0-f*f);
			k=vec3(obj.kt*dcol.r/255.0,obj.kt*dcol.g/255.0,obj.kt*dcol.b/255.0);
			if (t3<0.0) // TIR
				{
				f= -2.0*dot(N,ray.v);
				ray.v=f*N+ray.v;
				ray.p=pt+SMALL3*ray.v;
				}
			else
				{
				t2=t-sqrt(t3);
				ray.v=t2*N+n*ray.v;
				ray.p=pt+SMALL3*ray.v;
				}
			}
		else { break; }
		}
	else
		{
		col.r+=bg.r*k.x;
		col.g+=bg.g*k.y;
		col.b+=bg.b*k.z;
		return(col);
		}
	}
return(col);
}

