const float PI = 3.14159265;

float remap(float x, float lowIn, float rangeIn, bool invert)
{
    float o = clamp(clamp(x - lowIn, 0., rangeIn) / rangeIn, 0., 1.);
    if (invert) {
        return 1. - o;
    }
    return o;
}

vec3 blend(vec3 x1, vec3 x2, float factor)
{
    return x2 * factor + x1 * (1. - factor);
}

float zigzag(float x)
{
    return abs(1. - mod(x, 2.0));
}

float eyeAccent(vec2 uv, float x) {
    float x2 = max(0., 0.8 - x);
    vec2 uv2 = uv / vec2(0.7, 0.55) + vec2(0., 0.4);
    float bottom = clamp(1.1 * (1.3 - 1.0 * (uv2.x * uv2.x + uv2.y * uv2.y)), 0., 1.);
    float center = clamp(2.5 * x2 * x2, 0., 1.0);
    return (cos(3.0 * (bottom - center)) - 1.) * -0.5;
}

float eyeHighlight(vec2 uv) {
    vec2 uv2 = (uv + vec2(0.3, -0.4)) * vec2(1.9, 2.6);
    float dist2 = (uv2.x * uv2.x + uv2.y * uv2.y);
    float top = smoothstep(0.8, 0.85, 1. - dist2);

    uv2 = (uv + vec2(0.15, -0.25)) * vec2(3., 4.5);
    dist2 = (uv2.x * uv2.x + uv2.y * uv2.y);
    float middle = smoothstep(0.8, 0.9, 1. - dist2);

    uv2 = (uv + vec2(-0.25, 0.4)) * vec2(1.3, 1.8);
    dist2 = (uv2.x * uv2.x + uv2.y * uv2.y);
    float bottom = smoothstep(0.8, 0.9, 1. - dist2);

    return max(top * 1.0, max(middle * 0.8, bottom * 0.3));
}

float timeBounce(float x)
{
    return -3. * sin(PI * x) + x;
}

float gear(float d, float theta)
{
    // constants
    // teeth
    const float teethCount = 8.0;
    const float teethWidth = 0.3; // from 0.0 to 1.0
    const float teethSlant = 0.2; // purely radial sides don't look right
    const float teethSmoothing = 0.06;
    // spokes
    const float spokeCount = 4.0;
    const float spokeWidth = 0.08; // from 0.0 to 1.0
    const float spokeSlant = 0.3; // purely radial sides don't look right
    const float spokeSmoothing = 0.04;
    // radii
    const float scale = 0.5; // total size of the gear
    const float smoothing = 0.02; // radial; teeth and spoke smoothing are defined separately
    const float center = 0.2; // spokes start here
    const float inner = 0.5; // spokes end here
    const float outer = 0.75; // teeth start here

    d /= scale;

    if (d < center) {
        return 1.;
    } else if (d < inner) {
        float slant = remap(
                    d, center, inner + smoothing - center, true
                );
        return clamp(
            remap(d, center, 0.25 * smoothing, true) // center smoothing (quarter)
            +
            remap( // spokes
                1. - zigzag(theta / PI * spokeCount) - slant * slant * spokeSlant,
                spokeWidth,
                spokeSmoothing,
                true
            )
            +
            remap(
                d,
                inner - smoothing,
                smoothing,
                false
            )
        , 0., 1.);
    } else if (d < outer) {
        return 1.;
    } else if (d < 1.) {
        return clamp(
            min(
                remap(d, outer, smoothing, true) // ring smoothing
                +
                remap( // teeth
                    1. - zigzag(theta / PI * teethCount) - remap(
                        d, outer, 1. + smoothing - outer, true) * teethSlant,
                    teethWidth,
                    teethSmoothing,
                    true
                )
            ,
                remap( // smooth outer rim of gear
             		d,
                    1. - 2. * smoothing, // smooth double for outside
                    smoothing,
                    true
                )
            )
        , 0., 1.);
    }
    return 0.;
}

float tick(float t){
    const float ticksPerRot = 24.0;
    const float tickLength = 0.125;
    const float tickPeriod = 1.;
    t += 0.5 * tickLength + 0.5 * tickPeriod;
    return (
        floor(t / tickPeriod) + smoothstep(0., 1., (mod(t / tickPeriod, 1.) - 0.5) / tickLength)
 		) / ticksPerRot
    ;
}

float spiral(float x) {
  return max(0.0, sin(x) * 0.75 + 0.25);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
  // colors
  const vec3 colorBase = vec3(0.2, 0.3, 0.5);
  const vec3 colorBaseG = vec3(0.1, 0.1, 0.3);
  const vec3 colorAccent = vec3(0., 0.4, 0.6);
  const vec3 colorGear = vec3(0.9, 0.95, 0.95);
  const vec3 colorSpiral = vec3(0.2);
  const vec3 colHighlight = vec3(0.7);

  // parameters to tweak
  // more params for the gear inside the gear() function
  const float eyesScale = 0.7;

  // more params for the ticking inside the tick() function
  float time = tick(iTime);

  // Normalized pixel coordinates (from 0 to 1)
  float scale = min(iResolution.x, iResolution.y);
  vec2 uv = (gl_FragCoord.xy - 0.50 * iResolution.xy) / scale * 1.41421356;

  float dist = sqrt(uv.x*uv.x+uv.y*uv.y) / eyesScale;

  float angle = atan(uv.y, uv.x);

  // Time varying pixel color
  float cAccent = eyeAccent(uv, dist);
  float cGear = gear(dist, angle + 2. * PI * time);
  float cHighlight = eyeHighlight(uv);

  float logDist = log(dist);
  float bounceTime = timeBounce(iTime * 2.0 + logDist * 0.4);
  float ring = spiral(logDist * 8.0 + bounceTime);

  vec3 colBase = colorBase + uv.y * colorBaseG + cAccent * colorAccent + ring * colorSpiral;
  vec3 colComp = blend(colBase, colorGear, cGear);

  // Output to screen
  fragColor =
    vec4(
        min(vec3(1.0), colComp + cHighlight * colHighlight)
        * remap(dist, 1.0, 0.02, true) // remove this line to remove circle crop
      , 1.
    );
}

